/// <summary>
/// Change the group-by layout.
/// </summary>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        switch (CashAdvanceGroupBy.selection())
        {
            case TrvCashAdvanceGroupBy::ApprovalStatus:
                EmployeeCashAdvanceList.groupBy(identifierStr(CashAdvanceCard_CashAdvanceStatus));
                TrvCashAdvance_DS.queryBuildDataSource().sortClear();
                TrvCashAdvance_DS.query().dataSourceTable(tablenum(TrvCashAdvance)).addSortField(fieldNum(TrvCashAdvance, CashAdvanceStatus), SortOrder::Ascending);
                TrvCashAdvance_DS.query().dataSourceTable(tablenum(TrvCashAdvance)).addSortField(fieldNum(TrvCashAdvance, RequestedDate), SortOrder::Descending);
                TrvCashAdvance_DS.executeQuery();
                break;

            case TrvCashAdvanceGroupBy::RequestedDate:
                EmployeeCashAdvanceList.groupBy(identifierStr(CashAdvanceCard_RequestedDate));
                TrvCashAdvance_DS.queryBuildDataSource().sortClear();
                TrvCashAdvance_DS.query().dataSourceTable(tablenum(TrvCashAdvance)).addSortField(fieldNum(TrvCashAdvance, RequestedDate), SortOrder::Descending);
                TrvCashAdvance_DS.query().dataSourceTable(tablenum(TrvCashAdvance)).addSortField(fieldNum(TrvCashAdvance, CashAdvanceStatus), SortOrder::Ascending);
                TrvCashAdvance_DS.executeQuery();
                break;

            case TrvCashAdvanceGroupBy::None:
                EmployeeCashAdvanceList.groupBy("");
                TrvCashAdvance_DS.queryBuildDataSource().sortClear();
                TrvCashAdvance_DS.query().dataSourceTable(tablenum(TrvCashAdvance)).addSortField(fieldNum(TrvCashAdvance, RequestedDate), SortOrder::Descending);
                TrvCashAdvance_DS.query().dataSourceTable(tablenum(TrvCashAdvance)).addSortField(fieldNum(TrvCashAdvance, CashAdvanceNumber), SortOrder::Descending);
                TrvCashAdvance_DS.executeQuery();
                break;
        }
    }

    return ret;
}

