public void init()
{
    recId   primaryLedgerRecId = Ledger::current();
    boolean isBudgetActivated = false;

    super();
    TrvParameters::find();

    if (BudgetControlConfiguration::existActiveForPrimaryLedger(primaryLedgerRecId))
    {
        if (BudgetControlConfiguration::isSourceIntegratorEnabled(primaryLedgerRecId, BudgetControlSourceIntegrator::TravelRequisition))
        {
            isBudgetActivated = true;
            budgetOptions_IsReleaseEncumbrancesOnCloseRequisition.visible(true);
            budgetOptions_IsAllowSubmitRequisitionOnBudgetFail.visible(true);
        }
        else
        {
            budgetOptions_IsReleaseEncumbrancesOnCloseRequisition.visible(false);
        }

        if (BudgetControlConfiguration::isSourceIntegratorEnabled(primaryLedgerRecId, BudgetControlSourceIntegrator::ExpenseReport))
        {
            isBudgetActivated = true;
            budgetOptions_IsAllowSubmitExpenseOnBudgetFail.visible(true);
            budgetOptions_IsAllowApproveExpenseOnBudgetFail.visible(true);
        }
        else
        {
            budgetOptions_IsAllowSubmitExpenseOnBudgetFail.visible(false);
            budgetOptions_IsAllowApproveExpenseOnBudgetFail.visible(false);
        }
    }

    // hide the entire Additional budget options group, if no budget
    budgetOptions.visible(isBudgetActivated);

    //hide receipt capture tabpage if the feature is disabled.
    AutomaticReceiptCapture.visible(FeatureStateProvider::isFeatureEnabled(TrvReceiptCaptureEnableFeature::instance()));
}

/// <summary>
/// Sets the anti-corruption message fields to either enabled and madatory or disabled and optional.
/// </summary>
/// <param name = "_enabled">If the fields should be enabled or disabled</param>
public void setAntiCorruptionMessageFieldStatus(boolean _enabled)
{
    TrvAntiCorruptionLanguageText_AttestationMessage.enabled(_enabled);
    TrvAntiCorruptionLanguageText_AttestationMessage.mandatory(_enabled);
    TrvAntiCorruptionLanguageText_ApproverMessage.enabled(_enabled);
    TrvAntiCorruptionLanguageText_ApproverMessage.mandatory(_enabled);
}

public int active()
{
    int ret;

    ret = super();
    element.initializeDependentBudgetFlags();

    return ret;
}

/// <summary>
/// Sets the anti-corruption fields' mandatory property when modified.
/// </summary>
public void modified()
{
    super();

    element.setAntiCorruptionMessageFieldStatus(TrvParameters.UseAntiCorruption);
}

/// <summary>
/// Populate with system language record.
/// </summary>
public void initValue()
{
    super();

    TrvAntiCorruptionLanguageText.LanguageId = SystemParameters::getSystemLanguageId();
}

/// <summary>
/// Adds range to only show the barcode instructions for the system language.
/// </summary>
public void init()
{
    super();

    this.queryBuildDataSource().addRange(fieldNum(TrvAntiCorruptionLanguageText, LanguageId)).value(SystemParameters::getSystemLanguageId());
}

