[Form]
public class UnitOfMeasureConversion extends FormRun implements UnitOfMeasureIConversion
    {
        EcoResProduct   product;
        UnitOfMeasure   fromUnit;
}

public void run()
{
    const int   ConversionIntraclassTabPageNumber = 2;
    Common      callerRecord;
    super();

    if (element.args() &&  element.args().record())
    {
        callerRecord = element.args().record();

        if (callerRecord is EcoResProduct)
        {
            tocTab.tab(ConversionIntraclassTabPageNumber);

            product = callerRecord;
        }

        if (callerRecord is UnitOfMeasure)
        {
            fromUnit = callerRecord;
        }

        if (callerRecord is WHSPhysDimUOM)
        {
            tocTab.tab(ConversionIntraclassTabPageNumber);
        }

    }
}

/// <summary>
///  Adds product Variant-level filtering by Product Master and Product Variant
///  If currentRecord is EcoResDistinctProduct we don't need to do anything
/// </summary>
/// <param name="_query">
///  Query to add the variant-level filtering.
/// </param>
public void queryFilterByProductMaster(Query _query)
{
    if (element.args())
    {
        Common currentRecord = element.args().record();

        if (currentRecord)
        {
            boolean isProductUnitConversionPerVariantEnabled = EcoResProductUnitConverter::isProductUnitConversionPerVariantEnabled();

            if (currentRecord is EcoResProductMaster)
            {
                EcoResProductMaster ecoResProductMaster = currentRecord as EcoResProductMaster;

                boolean hasProductVariants = ecoResProductMaster.hasProductVariants();

                if (isProductUnitConversionPerVariantEnabled && hasProductVariants)
                {
                    this.buildQueryFilterProductMasterAndProductVariant(_query, false, ecoResProductMaster.RecId);
                }
                else
                {
                    this.buildQueryFilterByProductMasterFlightDisabled(_query, ecoResProductMaster, hasProductVariants);
                }
            }
            else if (currentRecord is EcoResDistinctProductVariant && isProductUnitConversionPerVariantEnabled)
            {
                EcoResDistinctProductVariant ecoResProductVariant = currentRecord as EcoResDistinctProductVariant;
                this.buildQueryFilterProductMasterAndProductVariant(_query, true, ecoResProductVariant.RecId);
            }
        }
    }
}

/// <summary>
/// Adds product Variant-level filtering by Product Master if the Product Variant
/// unit conversion support is enabled.
/// </summary>
/// <param name = "_query">Query to add the variant-level filtering.</param>
/// <param name = "_ecoResProductMaster">Product Master</param>
/// <param name = "_hasProductVariants">A boolean value indicating if the product master has variants or not.</param>
private void buildQueryFilterByProductMasterFlightDisabled(Query _query,  EcoResProductMaster _ecoResProductMaster, boolean _hasProductVariants)
{
    QueryBuildDataSource    qbdsEcoResDistinctProductVariant;
    QueryBuildDataSource    qbdsUnitOfMeasureConversion;

    if (!_ecoResProductMaster.isFieldSet(fieldNum(EcoResProductMaster, IsProductVariantUnitConversionEnabled)))
    {
        _ecoResProductMaster.reread();
    }
    if (_ecoResProductMaster.IsProductVariantUnitConversionEnabled && _hasProductVariants)
    {
        _query.dataSourceTable(tableNum(UnitOfMeasureConversion)).clearDynalinks();
        qbdsUnitOfMeasureConversion = _query.dataSourceTable(tableNum(UnitOfMeasureConversion));
        qbdsEcoResDistinctProductVariant = qbdsUnitOfMeasureConversion.addDataSource(tableNum(EcoResDistinctProductVariant));
        qbdsEcoResDistinctProductVariant.relations(false);
        qbdsEcoResDistinctProductVariant.addLink(fieldNum(UnitOfMeasureConversion, Product), fieldNum(EcoResProduct, RecId));
        qbdsEcoResDistinctProductVariant.joinMode(JoinMode::ExistsJoin);
        qbdsEcoResDistinctProductVariant.addRange(fieldNum(EcoResDistinctProductVariant, ProductMaster)).value(queryValue(_ecoResProductMaster.RecId));
    }
}

/// <summary>
///  Adds product Variant-level filtering by Product Master and Product Variant
/// </summary>
/// <param name="_query">
/// Query to add the variant-level filtering.
/// </param>
/// <param name = "_isProductVariant">True if RecId belongs to a product variant; otherwise false</param>
/// <param name = "_productId">Product Master or Product Variant RecId</param>
private void buildQueryFilterProductMasterAndProductVariant(Query _query, boolean _isProductVariant, RecId _productId)
{
    _query.dataSourceTable(tableNum(UnitOfMeasureConversion)).clearDynalinks();

    QueryBuildDataSource qbdsUnitOfMeasureConversion = _query.dataSourceTable(tableNum(UnitOfMeasureConversion));
    QueryBuildDataSource qbdsRelatedProducts = qbdsUnitOfMeasureConversion.addDataSource(tableNum(EcoResProductVariantRelatedProducts));
    qbdsRelatedProducts.relations(false);
    qbdsRelatedProducts.joinMode(JoinMode::ExistsJoin);

    qbdsRelatedProducts.addLink(fieldNum(UnitOfMeasureConversion, Product), fieldNum(EcoResProductVariantRelatedProducts, ReferenceRecId));
    qbdsRelatedProducts.addRange(_isProductVariant ? fieldNum(EcoResProductVariantRelatedProducts, ProductVariant) : fieldNum(EcoResProductVariantRelatedProducts, ProductMaster)).value(queryValue(_productId));
}

/// <summary>
/// Added range on unit of measure conversion  to filter the records with same product when ever it is called from the physical dimension unit of measure.
/// </summary>
/// <param name="_productRange">
/// Applied range as product range for which data gets filtered when ever it is being called from the physical dimension unit of measure.
/// </param>
private void filterRecordsByProductIdForPhysicalDimensionUOM(QueryBuildRange _productRange)
{
    Common callerRecord = element.args().record();

    if (callerRecord is WHSPhysDimUOM)
    {
        WHSPhysDimUOM physicalDimensionUOM = callerRecord as WHSPhysDimUOM;
        product = EcoResProduct::find(InventTable::find(physicalDimensionUOM.ItemId).Product);
        _productRange.value(int642Str(product.RecId));
    }
}

/// <summary>
/// Checks if the conversion is in use for the selected product.
/// </summary>
/// <param name="_fromUOM">From Unit Id</param>
/// <param name="_toUOM">To Unit Id</param>
/// <param name="_itemId">Item id</param>
/// <return>"true" if the conversion is in use, otherwise "false".</return>
private boolean isConversionInUse(UnitOfMeasureSymbol _fromUOM, UnitOfMeasureSymbol _toUOM, ItemId _itemId)
{
    InventTableModule inventTableModule;
    Set unitIds = new Set(Types::String);

    while select UnitId from inventTableModule
        where inventTableModule.ItemId == _itemId
    {
        unitIds.add(inventTableModule.UnitId);
    }

    if (unitIds.in(_fromUOM) && unitIds.in(_toUOM))
    {
        return true;
    }

    return false;
}

public void init()
{
    QueryBuildRange productRange;
    super();

    if (element.args())
    {
        switch (element.args().dataset())
        {
            case tableNum(UnitOfMeasure):
                // Use standard dynalinking when opened from unit of measure form
                break;
            default:
                // No dynalinks and showing all when opened from products and other places
                this.query().dataSourceTable(tableNum(UnitOfMeasureConversion)).clearDynalinks();
                break;
        }
    }

    productRange = this.query().dataSourceTable(tableNum(UnitOfMeasureConversion)).addRange(fieldNum(UnitOfMeasureConversion, Product));
    productRange.value(queryValue(0));
    productRange.status(RangeStatus::Hidden);

    this.cacheAddMethod(tableMethodStr(UnitOfMeasureConversion, displayQuickFormula));
}

public void init()
{
    QueryBuildDataSource    qbdsUnitOfMeasureConversion;
    QueryBuildDataSource    qbdsFromUnitOfMeasure;
    QueryBuildDataSource    qbdsToUnitOfMeasure;
    QueryBuildRange         productRange;
    QueryBuildRange         unitOfMeasureClassRange;

    super();

    qbdsUnitOfMeasureConversion = this.query().dataSourceTable(tableNum(UnitOfMeasureConversion));
    productRange = qbdsUnitOfMeasureConversion.addRange(fieldNum(UnitOfMeasureConversion, Product));
    productRange.value(SysQuery::valueNot(0));
    productRange.status(RangeStatus::Hidden);

    qbdsFromUnitOfMeasure = qbdsUnitOfMeasureConversion.addDataSource(tableNum(UnitOfMeasure));
    qbdsFromUnitOfMeasure.relations(false);
    qbdsFromUnitOfMeasure.addLink(fieldNum(UnitOfMeasureConversion, FromUnitOfMeasure), fieldNum(UnitOfMeasure, RecId));
    qbdsFromUnitOfMeasure.joinMode(JoinMode::InnerJoin);

    qbdsToUnitOfMeasure = qbdsUnitOfMeasureConversion.addDataSource(tableNum(UnitOfMeasure));
    qbdsToUnitOfMeasure.relations(false);
    qbdsToUnitOfMeasure.addLink(fieldNum(UnitOfMeasureConversion, ToUnitOfMeasure), fieldNum(UnitOfMeasure, RecId));
    qbdsToUnitOfMeasure.joinMode(JoinMode::InnerJoin);

    unitOfMeasureClassRange = qbdsToUnitOfMeasure.addRange(fieldNum(UnitOfMeasure, UnitOfMeasureClass));
    unitOfMeasureClassRange.value(strFmt(
        '(%1.%2 == %3.%4)',
        qbdsToUnitOfMeasure.name(),
        fieldStr(UnitOfMeasure, UnitOfMeasureClass),
        qbdsFromUnitOfMeasure.name(),
        fieldStr(UnitOfMeasure, UnitOfMeasureClass)));
    unitOfMeasureClassRange.status(RangeStatus::Hidden);

    element.filterRecordsByProductIdForPhysicalDimensionUOM(productRange);
    element.queryFilterByProductMaster(this.query());

    this.cacheAddMethod(tableMethodStr(UnitOfMeasureConversion, displayQuickFormula));
}

public boolean validateDelete()
{
    boolean ret;
    UnitOfMeasureSymbol fromUOM = UnitOfMeasure::find(UnitOfMeasureConversionIntraclass.FromUnitOfMeasure).Symbol;
    UnitOfMeasureSymbol toUOM = UnitOfMeasure::find(UnitOfMeasureConversionIntraclass.ToUnitOfMeasure).Symbol;
    ItemId itemId = InventTable::findByProduct(UnitOfMeasureConversionIntraclass.Product).ItemId;

    if (element.isConversionInUse(fromUOM, toUOM, itemId))
    {
        return checkFailed(strFmt("@Retail:RetailUnitOfMeasureConversionChecker", fromUOM, toUOM, itemId));
    }

    ret = super();

    return ret;
}

/// <summary>
/// In case a product variant is clicked, opens up the EcoResProductVariants form.
/// </summary>
public void jumpRef()
{
    EcoResProduct   currentProduct  =   EcoResProduct::find(UnitOfMeasureConversionIntraclass.Product);

    if (currentProduct is EcoResDistinctProductVariant)
    {
        Args            args            =   new Args();
        MenuFunction    menuFunction    =   new MenuFunction(menuitemDisplayStr(EcoResProductVariantsAction), MenuItemType::Display);

        args.lookupRecord(currentProduct);
        args.formViewOption(FormViewOption::Details);
        menuFunction.run(args);
    }
    else
    {
        super();
    }
}

