Method.Source:
[Form]
public class VendAdvance extends FormRun
{
    VendAdvanceInvoice vendAdvanceInvoice; //class supporting business logic for advance application and its reversal
    VendAdvanceForm vendAdvanceForm; //helper class for user interface
    Common callerTable;
    boolean isAdvanceSelectionChanged;
    boolean isAdvanceApplication;
    boolean isInvoicePosted;
    Num previouslySelectedAdvanceInvoiceNum;
    Num previouslySelectedStandardInvoiceNum;

}

public void init()
{
    super();

    if (!(element.args().caller() || element.args().record()))
    {
        throw error("@SYS22539");
    }

    if (element.args().caller() is FormRun)
    {
        callerTable = element.args().record();
        isAdvanceApplication = element.args().parmEnum();
        vendAdvanceInvoice = VendAdvanceInvoice::construct(isAdvanceApplication);

        switch (callerTable.TableId)
        {
            case tableNum(VendTable):
                isInvoicePosted = true;
                break;

            case tableNum(VendInvoiceInfoTable):
                isInvoicePosted = false;
                break;
        }

        vendAdvanceInvoice.parmCallerTable(callerTable);
        vendAdvanceInvoice.parmIsInvoicePosted(isInvoicePosted);

        vendAdvanceInvoice.init();

        tmpVendStandardInvoice.setTmpData(vendAdvanceInvoice.populateInvoices());
        vendAdvanceForm = VendAdvanceForm::construct(isAdvanceApplication);

        if (!isAdvanceApplication)
        {
            Tab.caption('@AccountsPayable:VendorPrepaymentsReverse');
        }
    }
    else
    {
        throw error(strFmt("@SYS105865","@SYS183647","@SYS8008"));
    }
}

private VendAdvanceApplicationTransQueryParameters getVendAdvanceQueryParams(tmpVendStandardInvoice _tmpVendStandardInvoice, tmpVendAdvanceInvoice _tmpVendAdvanceInvoice)
{
    VendAdvanceApplicationTransQueryParameters queryParams = VendAdvanceApplicationTransQueryParameters::construct();
    queryParams.standardInvoiceRecId = _tmpVendStandardInvoice.InvoiceRecId;
    queryParams.advancedInvoiceRecId = _tmpVendAdvanceInvoice.InvoiceRecId;
    queryParams.isPosted = _tmpVendStandardInvoice.IsPosted;
    queryParams.isReversal = _tmpVendAdvanceInvoice.IsReversal;

    return queryParams;
}

public void run()
{
    super();

    element.design().caption (vendAdvanceForm.editingCaption());
    ok.text (vendAdvanceForm.editingCaption());

    availableAppRem.visible (isAdvanceApplication);
    applyAmount.visible (isAdvanceApplication);
    appliedAmount.visible (!isAdvanceApplication);
}

public void toggleAllowEditFields()
{
    boolean doesAllowEdit = VendAdvanceApplicationTrans::existForPostedOrReversal(element.getVendAdvanceQueryParams(tmpVendStandardInvoice, tmpVendAdvanceInvoice));

    applyAmount.allowEdit(doesAllowEdit);
}

private Set determinePurchaseOrdersInStandardInvoice()
{
    Set purchaseOrders = new Set(Types::String);
    VendInvoiceTrans vendInvoiceTrans;

    while select OrigPurchId from vendInvoiceTrans
        where vendInvoiceTrans.PurchID == tmpVendStandardInvoice.PurchId
    {
        if (!purchaseOrders.in(vendInvoiceTrans.OrigPurchId))
        {
            purchaseOrders.add(vendInvoiceTrans.OrigPurchId);
        }
    }

    return purchaseOrders;
}

public boolean validateTotalAppliedAmount(AmountCur _appliedAmount, AmountCur _previouslyAppliedAmount)
{
    boolean ret = true;

    if (_appliedAmount > (tmpVendAdvanceInvoice.AdvanceRemaining + _previouslyAppliedAmount))
    {
        return false;
    }

    return ret;
}

public edit AmountCur updateApplicationAmount(
    boolean _set,
    tmpVendAdvanceInvoice _tmpVendAdvanceInvoice,
    AmountCur _appliedAmount)
{
    AmountCur applicationAmount;
    AmountCur previouslyAppliedAmt;

    if (_set)
    {
        if (_appliedAmount > 0)
        {
            previouslyAppliedAmt = abs(VendAdvanceApplicationTrans::findInvoiceIdByPostedOrReversal(element.getVendAdvanceQueryParams(tmpVendStandardInvoice, _tmpVendAdvanceInvoice)).AppliedAmount);

            if (tmpVendStandardInvoice_ds.validateTotalAppliedAmount(_appliedAmount - previouslyAppliedAmt) //validates only difference since the applied amount for the same line already is summed internally
                && tmpVendAdvanceInvoice_ds.validateTotalAppliedAmount(_appliedAmount, previouslyAppliedAmt))
            {
                vendAdvanceInvoice.updateAdvanceApplicationAmount(tmpVendStandardInvoice.InvoiceRecId, _tmpVendAdvanceInvoice.InvoiceRecId, -(_appliedAmount));
            }
            else
            {
                throw warning("@SYS331457");
            }
        }
        else
        {
            throw warning("@SYS342088"); //throws an error message when prepayment amount being applied is zero
        }
    }

    applicationAmount = vendAdvanceInvoice.getAdvanceApplicationAmount(tmpVendStandardInvoice.InvoiceRecId, _tmpVendAdvanceInvoice.InvoiceRecId);

    return abs(applicationAmount);
}

private void checkExistingVendAdvanceTransInOpenPeriod(VendInvoiceAccount _invoiceAccount)
{
    VendInvoiceJour vendInvoiceJour;
    VendAdvanceApplicationTrans vendAdvanceApplicationTrans;
    RecId calendarRecId = CompanyInfoHelper::fiscalCalendarRecId();

    while select vendAdvanceApplicationTrans
    join vendInvoiceJour
        where vendAdvanceApplicationTrans.PostedInvoiceRecId == vendInvoiceJour.RecId
            && vendInvoiceJour.InvoiceAccount == _invoiceAccount
    {
        if (!LedgerFiscalCalendar::checkDateIsValid(calendarRecId, vendAdvanceApplicationTrans.SettlementPostingDate, FiscalPeriodType::Operating, false))
        {
            throw warning("@AccountsPayable:MarkedPrepaymentInClosedPeriod");
        }
    }
}

public edit NoYes markTrans(boolean _set, tmpVendAdvanceInvoice _tmpVendAdvanceInvoice, NoYes _markTrans)
{
    NoYes returnMarkTrans;
    VendSettlement vendSettlement;
    SpecTransManager specTransManager;

    if (_set)
    {
        if (isAdvanceApplication)
        {
            // Check if there is any marked reverse prepayment while marking in apply prepayment form
            if (_markTrans)
            {
                VendAdvanceApplicationTrans vendAdvanceApplicationTrans;
                select firstonly RecId from vendAdvanceApplicationTrans
                    where vendAdvanceApplicationTrans.InvoiceId == tmpVendStandardInvoice.InvoiceRecId
                        && vendAdvanceApplicationTrans.AppliedAmount > 0;
                if (vendAdvanceApplicationTrans.RecId)
                {
                    throw warning("@AccountsPayable:MarkApplyPrepaymentWithMarkedReversePrepayment");
                }
            }
            vendAdvanceInvoice.updateTransMarked(tmpVendStandardInvoice.InvoiceRecId, _tmpVendAdvanceInvoice.InvoiceRecId, _markTrans, tmpVendStandardInvoice.SettlementPostingDate);

            //When unmarking, the VendAdvanceApplicationTrans and VendInvoiceInfoLine records have been deleted, so we can skip the following code.
            if (_markTrans)
            {
                try
                {
                    // Check whether the existing marked prepayments are in open period
                    this.checkExistingVendAdvanceTransInOpenPeriod(tmpVendStandardInvoice.InvoiceAccount);

                    AmountCur invoiceAmountRemaining = tmpVendStandardInvoice.InvoiceAmount - tmpVendAdvanceInvoice_ds.applyAmountSum();

                    //sets advance apply amount to maximum possible value when selected a paid advance
                    if (invoiceAmountRemaining >= _tmpVendAdvanceInvoice.AdvanceRemaining)
                    {
                        this.updateApplicationAmount(true, _tmpVendAdvanceInvoice, _tmpVendAdvanceInvoice.AdvanceRemaining);
                    }
                    else
                    {
                        this.updateApplicationAmount(true, _tmpVendAdvanceInvoice, invoiceAmountRemaining);
                    }
                }
                catch
                {
                    // If an error occurred applying the prepayment, unmark it
                    vendAdvanceInvoice.updateTransMarked(tmpVendStandardInvoice.InvoiceRecId, _tmpVendAdvanceInvoice.InvoiceRecId, NoYes::No, tmpVendStandardInvoice.SettlementPostingDate);
                }
            }

            element.toggleAllowEditFields();
        }
        else
        {
            // Check if there is any marked apply prepayment while marking in reverse prepayment form
            if (_markTrans)
            {
                VendAdvanceApplicationTrans vendAdvanceApplicationTrans;
                select firstonly RecId from vendAdvanceApplicationTrans
                    where vendAdvanceApplicationTrans.InvoiceId == tmpVendStandardInvoice.InvoiceRecId
                        && vendAdvanceApplicationTrans.AppliedAmount < 0;
                if (vendAdvanceApplicationTrans.RecId)
                {
                    throw warning("@AccountsPayable:MarkReversePrepaymentWithMarkedApplyPrepayment");
                }
            }
            specTransManager = vendAdvanceInvoice.parmSpecTransManager();

            if (_markTrans == NoYes::Yes && _tmpVendAdvanceInvoice.InvoiceDate > tmpVendStandardInvoice.SettlementPostingDate)
            {
                //Warn the user, if the prepayment reversal date is earlier than the prepayment application date
                warning("@AccountsPayable:SettlementDateValidation");
            }

            vendAdvanceInvoice.updateTransMarked(tmpVendStandardInvoice.InvoiceRecId, _tmpVendAdvanceInvoice.InvoiceRecId, _markTrans, tmpVendStandardInvoice.SettlementPostingDate);

            vendSettlement = vendAdvanceInvoice.getVendSettlement(tmpVendStandardInvoice, _tmpVendAdvanceInvoice);

            if (_markTrans == NoYes::Yes)
            {
                vendAdvanceInvoice.updateAdvanceApplicationAmount(tmpVendStandardInvoice.InvoiceRecId, _tmpVendAdvanceInvoice.InvoiceRecId, -_tmpVendAdvanceInvoice.AdvanceAmount);

                vendSettlement.CustVendSettlement::markThisAndRelatedOffsets(specTransManager, vendTrans.CurrencyCode);
            }
            else
            {
                vendSettlement.CustVendSettlement::unmarkThisAndRelatedOffsets(specTransManager);
            }

            vendAdvanceInvoice.parmSpecTransManager(specTransManager);
        }

        previouslySelectedAdvanceInvoiceNum = _tmpVendAdvanceInvoice.InvoiceNum;
        previouslySelectedStandardInvoiceNum = tmpVendStandardInvoice.InvoiceNum;
        tmpVendStandardInvoice_ds.research(true);
    }

    returnMarkTrans = vendAdvanceInvoice.getTransMarked(tmpVendStandardInvoice.InvoiceRecId, _tmpVendAdvanceInvoice.InvoiceRecId);

    return returnMarkTrans;
}

public display AmountCur applyAmountSum()
{
    return abs(VendAdvanceApplicationTrans::totalAdvanceAppliedAmountForInvoice(tmpVendStandardInvoice.InvoiceRecId, tmpVendStandardInvoice.IsPosted));
}

public int active()
{
    int ret = super();

    if (!isAdvanceApplication)
    {
        element.toggleAllowEditFields();
    }

    if (isAdvanceApplication)
    {
        mark.enabled(tmpVendAdvanceInvoice.AdvanceRemaining > 0 || tmpVendAdvanceInvoice_ds.markTrans(false, tmpVendAdvanceInvoice, NoYes::No));
    }

    tmpVendAdvanceInvoice_ds.applyAmountSum();

    return ret;
}

public void executeQuery()
{
    super();

    if (previouslySelectedAdvanceInvoiceNum != '')
    {
        tmpVendAdvanceInvoice_ds.positionToRecordByValue(fieldNum(TmpVendAdvanceInvoice, InvoiceNum), previouslySelectedAdvanceInvoiceNum);
        previouslySelectedAdvanceInvoiceNum = '';
    }
}

public void modified()
{
    super();

    isAdvanceSelectionChanged = true;
}

public display AmountCur getAvailableApplicationAmount()
{
    Set purchaseOrders = element.determinePurchaseOrdersInStandardInvoice();
    SetEnumerator purchaseOrdersEnumerator = purchaseOrders.getEnumerator();

    PurchPrepayAvailable availablePrepaymentAmount;

    while (purchaseOrdersEnumerator.moveNext())
    {
        availablePrepaymentAmount += PurchPrepayTable::findPurchId(purchaseOrdersEnumerator.current()).PrepayApplicationRemaining;
    }

    return availablePrepaymentAmount;
}

public boolean validateTotalAppliedAmount(AmountCur _amountApplied)
{
    boolean ret = true;

    if (tmpVendAdvanceInvoice_ds.applyAmountSum() + _amountApplied > tmpVendStandardInvoice.InvoiceAmount)
    {
        ret = false;
    }

    return ret;
}

public void init()
{
    super();
    this.query().dataSourceTable(tableNum(tmpVendStandardInvoice)).clearDynalinks();
}

public int active()
{
    int ret = super();

    tmpVendAdvanceInvoice.setTmpData(vendAdvanceInvoice.populateAdvances(tmpVendStandardInvoice));
    settlementPostingDate.visible(isInvoicePosted);

    return ret;
}

public void executeQuery()
{
    super();

    if (previouslySelectedStandardInvoiceNum != '')
    {
        tmpVendStandardInvoice_ds.positionToRecordByValue(fieldNum(TmpVendStandardInvoice, InvoiceNum), previouslySelectedStandardInvoiceNum);
        previouslySelectedStandardInvoiceNum = '';
    }
}

void clicked()
{
    if (isInvoicePosted)
    {
        vendAdvanceInvoice.parmVendTrans(vendAdvanceInvoice.getVendTrans(tmpVendStandardInvoice, tmpVendAdvanceInvoice));
        vendAdvanceInvoice.processAdvances();
    }
    else
    {
        VendIInvoiceRefresh callerForm = element.args().caller() as VendIInvoiceRefresh;

        if (callerForm != null)
        {
            callerForm.refresh();
        }
    }

    super();
}

public void clicked()
{
    super();

    vendAdvanceInvoice.clearRecordsMarkedInSession();
}

public boolean modified()
{
    boolean isValid;

    isValid = super();

    tmpVendAdvanceInvoice_ds.active();

    return isValid;
}
