public boolean canClose()
{
    boolean ret;

    ret = super();

    if (ret && vendBankAccount.RecId)
    {
        //When the bank account address is created/changed, different business rules apply for that address.
        //We need to ensure the bank account is left in a good state before the form is closed.
        ret = vendBankAccount.validateWrite();
    }

    return ret;
}

public void init()
{
    // <GJP>
    #ISOCountryRegionCodes
    // </GJP>

    element.args().applyRecordAsDynalink(true);
    super();

    // <GJP>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoJP]))
    {
        element.initDesign_JP();
    }
    // </GJP>
    if (VendBankAccountChangeProposalFeature::instance().isEnabled())
    {
        element.markChangeProposalFields();
        DetailsHeader_ReviewStatus.visible(element.isReviewStatusVisible());
    }
}

private boolean isReviewStatusVisible()
{
    return VendBankAccountChangeProposalFeature::instance().isEnabled();
}

[Replaceable]
public boolean canSubmitToWorkflow()
{
    if (VendBankAccountChangeProposalFeature::instance().isEnabled())
    {
        return VendBankAccount.canSubmitToWorkflow();
    }
    else
    {
        return super();
    }
}

/// <summary>
