    this.applyChangeProposalLabel_DateControl(Status_ActiveDate,VendBankAccountChangeProposalFields::ActiveDate);
    this.applyChangeProposalLabel_DateControl(Status_ExpiryDate,VendBankAccountChangeProposalFields::ExpiryDate);
    this.applyChangeProposalLabel_StringControl(Identification_SWIFTNo,VendBankAccountChangeProposalFields::SWIFTNo);
    this.applyChangeProposalLabel_StringControl(Identification_RegistrationNum,VendBankAccountChangeProposalFields::RegistrationNum);
    this.applyChangeProposalLabel_StringControl(Currency_CurrencyCode,VendBankAccountChangeProposalFields::CurrencyCode);
    this.applyChangeProposalLabel_StringControl(DetailsHeader_Name,VendBankAccountChangeProposalFields::Name);
}

