/// <summary>
/// Handles a change in a change-controlled field.
/// </summary>
/// <param name = "_vendBankAccountField">The number of the changed field in <c>VendBankAccount</c>.</param>
/// <param name = "_changeProposalField">The number of the changed field in <c>VendBankAccountChangeProposal</c>.</param>
/// <param name = "_vendBankAccount">The pointer to a buffer to manipulate as a result of the change.</param>
/// <param name = "_controlledField">The enumeration representing the changed field. Used to check if change control is enabled on the field.</param>
/// <returns>True if changes have been reverted due to change control, otherwise false.</returns>
[Hookable(false)]
public boolean modifiedControlledField(FieldId _vendBankAccountField, FieldId _changeProposalField, VendBankAccount _vendBankAccount, VendBankAccountChangeProposalFields _controlledField)
{
    boolean ret;

    if (VendBankAccountChangeProposalHandlerFlight::instance().isEnabled())
    {
        ret = vendBankAccountChangeProposalHandler.modifiedControlledField(_vendBankAccountField, _changeProposalField, _vendBankAccount, _controlledField);
    }
    else
    {
        if (!VendBankAccountChangeProposalFeature::instance().isEnabled())
        {
            return ret;
        }

        VendBankAccount originalTable = _vendBankAccount.orig();

        if (originalTable.(_vendBankAccountField) != _vendBankAccount.(_vendBankAccountField)
                && VendBankAccountChangeProposalFieldEnablement::isEnabledFor(_controlledField))
        {
            if(!_vendBankAccount)
            {
                element.showWarningNoChangeControlOnCreation();
            }
            else
            {
                if (VendBankAccount_ds.validateWrite() &&
                    element.checkShowChangeProposal(_vendBankAccount))
                {
                    _vendBankAccount.modifyChangeProposalRecord(_changeProposalField, _vendBankAccountField, _controlledField);
                    VendBankAccount_ds.write();

                    element.showChangeProposalDialog();
                }

                ret = true;
            }
        }
    }

    return ret;
}

private boolean checkShowChangeProposal(VendBankAccount _bankAccount)
{
    return _bankAccount.WorkflowState == VendBankAccountChangeProposalWorkflowState::Submitted 
        || ! _bankAccount.isDraft();
}

private void showWarningNoChangeControlOnCreation()
{
    if (!showedWarningNoChangeControlOnCreation)
    {
        showedWarningNoChangeControlOnCreation = true;
        info("@AccountsPayable:VendorBankAccountApproveNotActive");
    }
}

private void handleChangeProposalVisibility()
{
    VendBankAccountChangeProposal _vendBankChangeProposal = VendBankAccountChangeProposal::findByVendBankAccountRecId(VendBankAccount.RecId, false);
    VendBankAccountChangeProposal.visible(VendBankAccount.RecId != 0 && _vendBankChangeProposal.RecId != 0);
}

private void initDesign_JP()
{
    if (Bank_JP::nameKanaValidateMasterDataNeeded())
    {
        AccountNameKanaValidate_JBA_JP.enabled(true);
    }
    else
    {
        AccountNameKanaValidate_JBA_JP.enabled(false);
    }
}

private void updateDesign()
{
    VendBankAccount_DS.object(fieldNum(VendBankAccount, BankCIN)).allowEdit(SysCountryRegionCode::isLocationInCountryRegion(VendBankAccount.Location, [#isoIT]));
}

/// <summary>
/// Shows the dialog listing changes to change-controlled fields.
/// </summary>
protected void showChangeProposalDialog()
{
    Args args = new Args();
    args.menuItemName(menuItemDisplayStr(VendBankAccountChangeProposal));
    args.menuItemType(MenuItemType::Display);
    args.record(VendBankAccount);
    args.parm("@AccountsPayable:NotificationChangesRequireApproval");

    new MenuFunction(menuItemDisplayStr(VendBankAccountChangeProposal), MenuItemType::Display).run(args);
}

public int active()
{
    int ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    if (VendBankAccountChangeProposalFeature::instance().isEnabled())
    {
        boolean allowEdit =  VendBankAccount.isVendBankAccountEditableOnWorkflow();
        VendBankAccount_DS.allowEdit(allowEdit);
        element.handleChangeProposalVisibility();
    }

    return ret;
}

public void init()
{
    QueryBuildRange qbrBank;
    QueryBuildRange qbrVendor;
    RecId           ledgerDimension;

    super();

    switch (element.args().dataset())
    {
        case tableNum(LedgerJournalTrans) :
            this.query().dataSourceNo(1).clearDynalinks();
            qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, AccountID));
            qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(LedgerJournalTrans,CustVendBankAccountId))));

            qbrVendor = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, VendAccount));

            ledgerDimension = element.args().record().(fieldName2id(element.args().dataset(),fieldStr(LedgerJournalTrans, LedgerDimension)));
            qbrVendor.value(LedgerDynamicAccountHelper::getAccountNumberFromDynamicAccount(ledgerDimension));

            break;

        case tableNum(PurchParmTable) :
            qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, AccountID));
            qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(PurchParmTable,VendBankAccountID))));

            qbrVendor = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, VendAccount));
            qbrVendor.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(PurchParmTable,InvoiceAccount))));

            break;

        case tableNum(PurchParmTable_PaymentDisbursement) :
            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, AccountID));
                qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(PurchParmTable_PaymentDisbursement,VendBankAccountID))));

                qbrVendor = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, VendAccount));
                qbrVendor.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(PurchParmTable_PaymentDisbursement,VendAccount))));

                break;
            }

        case tableNum(VendTransOpen) :
            if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
            {
                qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, AccountID));
                qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(VendTransOpen, ThirdPartyBankAccountId))));

                qbrVendor = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, VendAccount));
                qbrVendor.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(VendTransOpen,AccountNum))));

                break;
            }

        case tableNum(VendInvoiceInfoTable) :
            qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, AccountID));
            qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(VendInvoiceInfoTable,VendBankAccountID))));

            qbrVendor = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, VendAccount));
            qbrVendor.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(VendInvoiceInfoTable,InvoiceAccount))));

            break;

        case tableNum(VendTrans) :
            qbrBank = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, AccountID));
            qbrBank.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(VendTrans, ThirdPartyBankAccountId))));

            qbrVendor = this.query().dataSourceNo(1).addRange(fieldNum(VendBankAccount, VendAccount));
            qbrVendor.value(element.args().record().(fieldName2id(element.args().dataset(),fieldStr(VendTrans,AccountNum))));

            break;
    }

    if (VendBankAccountChangeProposalHandlerFlight::instance().isEnabled())
    {
        vendBankAccountChangeProposalHandler = VendBankAccountChangeProposalHandler::construct(element);
    }
}

