/// <summary>
/// Lookup for getting filtered Duns Numbers corresponding to current vendor account.
/// </summary>
/// <param name="_formReferenceControl">
/// The lookup control.
/// </param>
/// <returns>
/// The lookup values.
/// </returns>
public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup     sysReferenceTableLookup;
    QueryBuildDataSource        queryDSDirDunsNumber;
    QueryBuildDataSource        queryDSLogisticsLocation;
    QueryBuildDataSource        queryDSDirPartyLocation;
    QueryBuildRange             queryBuildRange;
    RecId                       partyRecId;
    Query                       query = new Query();

    queryDSDirDunsNumber = query.addDataSource(tableNum(DirDunsNumber));

    sysReferenceTableLookup = SysReferenceTableLookup::newParameters(tableNum(DirDunsNumber), _formReferenceControl);

    sysReferenceTableLookup.addLookupfield(fieldNum(DirDunsNumber, DunsNumber));

    queryDSLogisticsLocation = queryDSDirDunsNumber.addDataSource(tableNum(LogisticsLocation));
    queryDSLogisticsLocation.joinMode(JoinMode::InnerJoin);
    queryDSLogisticsLocation.relations(true);

    partyRecId = VendTable::find(vendBankAccount.VendAccount).Party;

    queryDSDirPartyLocation = queryDSLogisticsLocation.addDataSource(tableNum(DirPartyLocation));
    queryDSDirPartyLocation.joinMode(JoinMode::InnerJoin);
    queryDSDirPartyLocation.relations(true);

    queryBuildRange = queryDSDirPartyLocation.addRange(fieldNum(DirPartyLocation, Party));
    queryBuildRange.value(strFmt("%1", partyRecId));

    sysReferenceTableLookup.parmQuery(query);

    return sysReferenceTableLookup.performFormLookup();
}

