public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup = SysTableLookup::newParameters(tableNum(VendBankAccount),_formControl);
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource = query.addDataSource(tableNum(VendBankAccount));
    QueryBuildRange         queryBuildRangeBankAccount = queryBuildDataSource.addRange(fieldNum(VendBankAccount, AccountId));
    QueryBuildRange         queryBuildRangeVendAccount = queryBuildDataSource.addRange(fieldNum(VendBankAccount, VendAccount));

    sysTableLookup.addLookupfield(fieldnum(VendBankAccount, AccountId));

    queryBuildRangeVendAccount.value(vendBankAccount.VendAccount);
    queryBuildRangeBankAccount.value('!' + vendBankAccount.AccountID);

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

