public void clicked()
{
    BankGroup bankGroup = BankGroup::find(VendBankAccount.BankGroupID);

    super();

    Bank_JP::validateBankNameKana(
        VendBankAccount.BankAccountNameKana_JP,
        tableNum(VendBankAccount),
        fieldNum(VendBankAccount, BankAccountNameKana_JP));

    if (bankGroup)
    {
        Bank_JP::validateBankNameKana(
            bankGroup.NameKana_JP,
            tableNum(BankGroup),
            fieldnum(BankGroup, NameKana_JP));
        Bank_JP::validateBankNameKana(
            bankGroup.BranchNameKana_JP,
            tableNum(BankGroup),
            fieldnum(BankGroup, BranchNameKana_JP));
    }
    else
    {
        warning("@CashManagement:BankGroupsMustBeSetForVendBankAccounts_JP");
    }
}
