Method.Source:
[Form]
public class VendChangeProposal extends FormRun
{
    VendTable callerRecord;

}

/// <summary>
/// Initializes the form.
/// </summary>
public void init()
{
    super();

    if (!this.args() || !this.args().record() || this.args().record().TableId != tableNum(VendTable))
    {
        throw error(strFmt("@SYS22678", element.name()));
    }
    else
    {
        callerRecord = this.args().record() as VendTable;
    }

    if (!callerRecord.canModifyChangeProposal())
    {
        element.disableDiscardButtons();
        // called from an attempted change, and we'll inform the user of the change, 
        // but have to note it can't be done in the current workflow state
        if (this.args().parm())
        {
            warning("@AccountsPayable:VendTableChangeProposal_ChangesNotAllowed");
            this.args().parm(''); // suppress message since it is not valid if an error occurred
        }
    }

    if (this.args().parm())
    {
        info(this.args().parm());
    }

}

/// <summary>
/// Disables all the discard buttons on the form.
/// </summary>
protected void disableDiscardButtons()
{
    DiscardChanges.enabled(false);
    DiscardCreditMaxChange.enabled(false);
    DiscardName.enabled(false);
    DiscardVendGroup.enabled(false);
    DiscardPaymMode.enabled(false);
    DiscardPaymSpec.enabled(false);
    DiscardVATNum.enabled(false);
    DiscardBankAccount.enabled(false);
    DiscardCashDisc.enabled(false);
    DiscardCreditRating.enabled(false);
    DiscardPaymTermId.enabled(false);
    DiscardInvoiceAccount.enabled(false);
    DiscardVendPriceToleranceGroupId.enabled(false);
}

/// <summary>
/// Controls the visibility of groups representing individual grid rows
/// </summary>
public void showHideGroups()
{
    CreditMaxGroup.visible(VendTableChangeProposal.IsChangedCreditMax == NoYes::Yes);
    NameGroup.visible(VendTableChangeProposal.IsChangedName == NoYes::Yes);
    VendGroupGroup.visible(VendTableChangeProposal.IsChangedVendGroup);
    PaymModeGroup.visible(VendTableChangeProposal.IsChangedPaymMode);
    PaymSpecGroup.visible(VendTableChangeProposal.IsChangedPaymSpec);
    VATNumGroup.visible(VendTableChangeProposal.IsChangedVATNum);
    BankAccountGroup.visible(VendTableChangeProposal.IsChangedBankAccount);
    CashDiscGroup.visible(VendTableChangeProposal.IsChangedCashDisc);
    CreditRatingGroup.visible(VendTableChangeProposal.IsChangedCreditRating);
    PaymtermIdGroup.visible(VendTableChangeProposal.IsChangedPaymTermId);
    InvoiceAccountGroup.visible(VendTableChangeProposal.IsChangedInvoiceAccount);
    VendPriceToleranceGroupIdGroup.visible(VendTableChangeProposal.IsChangedVendPriceToleranceGroupId);
}

/// <summary>
/// Refreshes the calling form for the selected record, forcing it to show/hide "proposal" button based on newest data.
/// </summary>
private void refreshCaller()
{
    element.args().record().dataSource().reread(); // reads from DB
    element.args().record().dataSource().refresh(); // updates form controls
    element.args().record().dataSource().active(); // emulates selection of a record, so that button will be shown/hidden correctly
}

/// <summary>
/// Work that needs to be executed when the form closes, including refreshing the caller.
/// </summary>
public void close()
{
    super();
    element.refreshCaller();
}

/// <summary>
/// Filter to caller and execute query.
/// </summary>
public void executeQuery()
{
    SysQuery::findOrCreateRange(
        this.query().dataSourceTable(tableNum(VendTableChangeProposal)),
        fieldNum(VendTableChangeProposal, VendTable))
    .value(queryValue(callerRecord.RecId));

    super();

    element.showHideGroups();
}

/// <summary>
/// Initializes the datasource, including by adding the display method to cache.
/// </summary>
public void init()
{
    super();
    VendTableChangeProposal_ds.cacheAddMethod(tablemethodstr(VendTableChangeProposal, getCurrentName));
}

/// <summary>
/// Mark the change as discarded and refresh the form.
/// </summary>
public void clicked()
{
    ttsbegin;
    VendTableChangeProposal localRecord = VendTableChangeProposal::findByVendRecId(VendTableChangeProposal.VendTable, true);
    localRecord.IsChangedCreditMax = NoYes::No;
    localRecord.update();
    ttscommit;

    // Super will ensure the form reflects changes and controls are hidden
    super();
}

