/// <summary>
/// Remove changes and close the dialog.
/// </summary>
public void clicked()
{
    VendTableChangeProposal proposalTable;

    delete_from proposalTable where proposalTable.RecId == VendTableChangeProposal.RecId;

    super();
}

/// <summary>
/// Closes the form.
/// </summary>
public void clicked()
{
    VendTableChangeProposal.deleteIfRedundant();
    super();
}
