[Form]
public class VendConsInvoice_JP extends FormRun
{
    boolean researchData;

}

public void init()
{
    if (!VendConsInvoiceType_JP::isVendConsInvoiceEnabled())
    {
        // The 'Use consolidated invoice for vendor' parameter is not selected.
        throw error("@GLS62969");
    }

    super();
}

internal void refreshAfterPostAndReversal()
{
    vendConsInvoice_JP_ds.reread();
    ledgerJournalTable_ds.research();
    ledgerJournalTrans_ds.research();

    element.updateDesign();
}

/// <summary>
/// Adds vendor invoice journals to an existed consolidated invoice.
/// </summary>
public void updateConsInvoice()
{
    Query                   query;
    QueryRun                queryRun;
    VendConsInvoiceType_JP  vendConsInvoiceType;

    vendConsInvoiceType = VendConsInvoiceType_JP::construct(vendConsInvoice_JP);
    query = vendConsInvoiceType.buildUpdateQuery();

    queryRun = new QueryRun(query);
    if (queryRun.prompt())
    {
        VendConsInvoiceType_JP::update(vendConsInvoice_JP, queryRun.query());
        vendConsInvoice_JP_ds.reread();
        vendConsInvoice_JP_ds.refresh();
        vendInvoiceJour_ds.executeQuery();
    }
}

/// <summary>
/// Enables or disables buttons according to consolidated invoice status.
/// </summary>
public void updateDesign()
{
    boolean isConfirmed;

    if (vendConsInvoice_JP.isConsConfirmed())
    {
        isConfirmed = true;
    }

    if(TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled())
    {
        Posting_ButtonGroup.visible(true);
        ConsInvoice_ButtonGroup.visible(true);
        TaxTransSource.visible(true);
        VendConsInvoice_JP_ds.object(fieldNum(VendConsInvoice_JP, Posted)).visible(true);
        MenuItemButtonConfirm.visible(false);
        MenuItemButtonUnconfirm.visible(false);
        TaxTransactions.visible(false);

        menuItemButtonConfirmV2.enabled(!isConfirmed);
        menuItemButtonUnconfirmV2.enabled(isConfirmed && !vendConsInvoice_JP.JournalNum);

        MenuItemButtonPost.enabled(isConfirmed && !vendConsInvoice_JP.Posted);
        TransactionReversalDialogButton.enabled(vendConsInvoice_JP.Posted && vendConsInvoice_JP.JournalNum);
    }
    else
    {
        Posting_ButtonGroup.visible(false);
        ConsInvoice_ButtonGroup.visible(false);
        TaxTransSource.visible(false);
        VendConsInvoice_JP_ds.object(fieldNum(VendConsInvoice_JP, Posted)).visible(false);
        MenuItemButtonConfirm.visible(true);
        MenuItemButtonUnconfirm.visible(true);
        TaxTransactions.visible(true);

        menuItemButtonConfirm.enabled(!isConfirmed);
        menuItemButtonUnconfirm.enabled(isConfirmed);
    }

    AddInvoiceButton.enabled(!isConfirmed);
    DeleteInvoiceButton.enabled(!isConfirmed);

    vendConsInvoice_JP_ds.allowDelete(!isConfirmed);

    vendConsInvoice_JP_ds.object(fieldNum(VendConsInvoice_JP, ExecutionDate)).enabled(!isConfirmed);
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.updateDesign();
    }

    return ret;
}

public void delete()
{
    super();

    researchData = true;

    if (!VendConsInvoice_JP_ds.cursor().RecId)
    {
        VendConsInvoice_JP_ds.research(researchData);
    }
}

void clicked()
{
    Args argsLoc;
    argsLoc = new Args();
    argsLoc.record(vendConsInvoice_JP);

    VendConsolidatedInvoiceCreation_JP::main(argsLoc);

    vendConsInvoice_JP_ds.research();

    super();
}

/// <summary>
/// This button intiates the consolidated invoice posting flow
/// </summary>
public void clicked()
{
    super();

    element.refreshAfterPostAndReversal();
}

public void clicked()
{
    if (vendConsInvoice_JP.Posted)
    {
        super();
    }
    else
    {
        TaxConsInvoice_JP tax = TaxConsInvoice_JP::newForSourceTable(vendConsInvoice_JP);
        tax.validateSalesTaxTransactionProperties(false);
        tax.calculateTax();
        Tax::showTaxes(menuitemDisplayStr(TaxTmpWorkConsInvoice_JP), tax, vendConsInvoice_JP, element);
    }
}

void clicked()
{
    List invoiceJourToRemove = new List(Types::Int64);
    MultiSelectionHelper helper = MultiSelectionHelper::construct();
    helper.parmDatasource(VendInvoiceJour_ds);

    super();

    VendInvoiceJour vendInvoiceJourToRemove = helper.getFirst();

    while (vendInvoiceJourToRemove)
    {
        invoiceJourToRemove.addEnd(vendInvoiceJourToRemove.RecId);
        vendInvoiceJourToRemove = helper.getNext();
    }

    CustVendConsInvoiceType_JP::removeInvoiceJour(tablenum(vendInvoiceJour), invoiceJourToRemove);

    vendConsInvoice_JP_ds.reread();
    vendConsInvoice_JP_ds.refresh();
    vendInvoiceJour_ds.executeQuery();
    if(TaxConsInvoice_JP::isTaxDifferencePostingOnConsInvoiceEnabled())
    {
        ledgerJournalTable_ds.research();
        ledgerJournalTrans_ds.research();
        vendConsInvoiceHistory_JP_ds.research();
    }
}
