void updateFilters()
{
    QueryBuildDataSource    qbds = transportPointLine_DS.queryBuildDataSource();
    QueryBuildRange qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, ToCountryRegionId));
    if (qbr)
    {
        toCountrySelection = qbr.value();
        toCountryRegionIdFilter.text(toCountrySelection);
    }
    qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, ToState));
    if (qbr)
    {
        toStateSelection = qbr.value();
        toStateFilter.text(toStateSelection);
    }
    qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, ToCounty));
    if (qbr)
    {
        toCountySelection = qbr.value();
        toCountyFilter.text(toCountySelection);
    }
    qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, ToZipCode));
    if (qbr)
    {
        toZipCodeSelection = qbr.value();
        toZipCodeFilter.text(toZipCodeSelection);
    }
    qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, FromCountryRegionId));
    if (qbr)
    {
        fromCountrySelection = qbr.value();
        fromCountryRegionIdFilter.text(fromCountrySelection);
    }
    qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, FromState));
    if (qbr)
    {
        fromStateSelection = qbr.value();
        fromStateFilter.text(fromStateSelection);
    }
    qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, FromCounty));
    if (qbr)
    {
        fromCountySelection = qbr.value();
        fromCountyFilter.text(fromCountySelection);
    }
    qbr = qbds.findRange(fieldNum(VendCustTransportPointLine, FromZipCode));
    if (qbr)
    {
        fromZipCodeSelection = qbr.value();
        fromZipCodeFilter.text(fromZipCodeSelection);
    }

    this.validateFilters(); // Some of the entered values might not be valid, so validation is needed
}

void updateFilterSelection()
{
    element.setRange();
    transportPointLine_ds.executeQuery();
}

private void validateFilters()
{
    if (toCountryRegionIdFilter.text() && !LogisticsAddressCountryRegion::exist(toCountryRegionIdFilter.text()))
    {
        toCountryRegionIdFilter.text('');
    }

    if (toStateFilter.text() && !LogisticsAddressState::exist(toCountryRegionIdFilter.text(), toStateFilter.text()))
    {
        toStateFilter.text('');
    }

    if (toCountyFilter.text() && !LogisticsAddressCounty::exist(toCountryRegionIdFilter.text(), toStateFilter.text(), toCountyFilter.text()))
    {
        toCountyFilter.text('');
    }

    if (toZipCodeFilter.text())
    {
        LogisticsAddressZipCode zipCode;
        select firstonly RecId from zipCode
            where (zipCode.CountryRegionId   == toCountryRegionIdFilter.text()  || !toCountryRegionIdFilter.text())
                && (zipCode.ZipCode          == toZipCodeFilter.text()          || !toZipCodeFilter.text())
                && (zipCode.County           == toCountyFilter.text()           || !toCountyFilter.text())
                && (zipCode.State            == toStateFilter.text()            || !toStateFilter.text());

        if (!zipCode.RecId)
        {
            toZipCodeFilter.text('');
        }
    }

    if (fromCountryRegionIdFilter.text() && !LogisticsAddressCountryRegion::exist(fromCountryRegionIdFilter.text()))
    {
        fromCountryRegionIdFilter.text('');
    }

    if (fromStateFilter.text() && !LogisticsAddressState::exist(fromCountryRegionIdFilter.text(), fromStateFilter.text()))
    {
        fromStateFilter.text('');
    }

    if (fromCountyFilter.text() && !LogisticsAddressCounty::exist(fromCountryRegionIdFilter.text(), fromStateFilter.text(), fromCountyFilter.text()))
    {
        fromCountyFilter.text('');
    }

    if (fromZipCodeFilter.text())
    {
        LogisticsAddressZipCode zipCode;
        select firstonly RecId from zipCode
            where (zipCode.CountryRegionId   == fromCountryRegionIdFilter.text()  || !fromCountryRegionIdFilter.text())
                && (zipCode.ZipCode          == fromZipCodeFilter.text()          || !fromZipCodeFilter.text())
                && (zipCode.County           == fromCountyFilter.text()           || !fromCountyFilter.text())
                && (zipCode.State            == fromStateFilter.text()            || !fromStateFilter.text());

        if (!zipCode.RecId)
        {
            fromZipCodeFilter.text('');
        }
    }

}

public void executeQuery()
{
    super();

    element.updateFilters();
}

