public void linkActive()
{
    switch (element.args().dataset())
    {
        case tableNum(LogisticsAddressCountryRegion) :
            LogisticsAddressCountryRegion countryRegionCaller = element.args().record();
            toCountryRegionIdFilter.text(countryRegionCaller.CountryRegionId);
            fromCountryRegionIdFilter.text(countryRegionCaller.CountryRegionId);
            break;

        case tableNum(LogisticsAddressState)   :
            LogisticsAddressState stateCaller = element.args().record();
            toCountryRegionIdFilter.text(stateCaller.CountryRegionId);
            toStateFilter.text(stateCaller.StateId);
            fromCountryRegionIdFilter.text(stateCaller.CountryRegionId);
            fromStateFilter.text(stateCaller.StateId);
            break;

        case tableNum(LogisticsAddressCounty)  :
            LogisticsAddressCounty countyCaller = element.args().record();
            toCountryRegionIdFilter.text(countyCaller.CountryRegionId);
            toStateFilter.text(countyCaller.StateId);
            toCountyFilter.text(countyCaller.CountyId);
            fromCountryRegionIdFilter.text(countyCaller.CountryRegionId);
            fromStateFilter.text(countyCaller.StateId);
            fromCountyFilter.text(countyCaller.CountyId);
            break;

        case tableNum(LogisticsAddressZipCode) :
            LogisticsAddressZipCode zipCodeCaller = element.args().record();
            toCountryRegionIdFilter.text(zipCodeCaller.CountryRegionId);
            toStateFilter.text(zipCodeCaller.State);
            toCountyFilter.text(zipCodeCaller.County);
            toZipCodeFilter.text(zipCodeCaller.ZipCode);
            fromCountryRegionIdFilter.text(zipCodeCaller.CountryRegionId);
            fromStateFilter.text(zipCodeCaller.State);
            fromCountyFilter.text(zipCodeCaller.County);
            fromZipCodeFilter.text(zipCodeCaller.ZipCode);
            break;
    }

    element.setRange();

    super();
}

public void init()
{
    super();

    toCountryRange            = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,ToCountryRegionId));
    toStateRange              = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,ToState));
    toCountyRange             = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,ToCounty));
    toZipCodeRange            = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,ToZipCode));
    fromCountryRange          = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,FromCountryRegionId));
    fromStateRange            = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,FromState));
    fromCountyRange           = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,FromCounty));
    fromZipCodeRange          = transportPointLine_ds.query().dataSourceTable(tableNum(VendCustTransportPointLine)).addRange(fieldNum(VendCustTransportPointLine,FromZipCode));
}

public void updateTransportDaysForTheTransportRelation(boolean _isDefaultDlvModeEnabled, VendCustTransportDays _transportDays)
{
    if ( _isDefaultDlvModeEnabled)
    {
        transportPointLine.TransportDays = _transportDays;
    }
}

public boolean hasDefaulDeliveryMode()
{
    return VendCustTransportTime::defaultTransportTime(transportPointLine.RecId) != null ;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    LogisticsAddressCity::lookupCityId(_formControl, transportPointLine.ToCountryRegionId, transportPointLine.ToState, transportPointLine.ToCounty); 
}

public void lookup(FormControl _formControl, str _filterStr)
{
    FormStringControl stringformControl = _formControl as FormStringControl;
    if (stringformControl)
    {
        Args args = new Args();
        args.name(formStr(LogisticsAddressZipCodeLookup));
        args.caller(element);
        args.record(transportPointLine);
        args.parm(fieldId2Name(tableNum(VendCustTransportPointLine), fieldNum(VendCustTransportPointLine, ToCity)));

        FormRun formRun = ClassFactory::formRunClassOnClient(args);
        formRun.init();

        stringformControl.performFormLookup(formRun);
    }
}

