public int active()
{
    int ret = super();

    if (ret)
    {
        // Default delivery mode field should be editable if current record already contains the default delivery mode
        // or there is no default delivery mode specifield for the given main relation.
        VendCustTransportTime vendCustTransportTime = VendCustTransportTime::defaultTransportTime(TransportPointLine_DS.cursor().RecId);
        boolean isDefaultDlvModeEditable = (transportTime.Recid == vendCustTransportTime.RecId) || (vendCustTransportTime.RecId == 0);

        FormDataObject fdo = TransportTime_DS.object(fieldnum(VendCustTransportTime, DefaultDeliveryMode));
        fdo.allowEdit(isDefaultDlvModeEditable);
    }
    return ret;
}

