public boolean modified()
{
    boolean ret = super();

    boolean isDefaultDlvModeEnabled = TransportTime_DefaultDlvMode.value();
    transportPointLine_ds.updateTransportDaysForTheTransportRelation(isDefaultDlvModeEnabled, TransportTime_TransportDays.value());
    TransportDays_TransportDays.enabled(!isDefaultDlvModeEnabled);
    TransportPointLine_TransportDays.enabled(!isDefaultDlvModeEnabled);

    return ret;
}

