Method.Source:
    [Form]
    public class VendDirective_PSN extends FormRun
    {
        QueryFilter                         filter;
        QueryBuildDataSource                vendInvoiceHeaderqbds;
        QueryBuildDataSource                vendInvoiceLineqbds;
        QueryBuildDataSource                vendDirectiveTableqbds;
        QueryBuildDataSource                vendRoutinsSlipTableqbds;
        QueryBuildDataSource                dimensionAtributeCombqbds;
        QueryBuildDataSource                mainAccountqbds;
        QueryBuildDataSource                activeInvoiceFilterqbds;
        CustVendDirectiveInvoiceStatus_PSN  pendingOrPosted;
}

/// <summary>
/// Builds the filters that appear by default on the <c>SysQuery</c> form
/// when the user clicks the button to retrieve lines.
/// </summary>
/// <param name="_queryRun">
/// A <c>QueryRun</c> object that will be passed to the <c>SysQuery</c> form.
/// </param>
public void addQueryFilters(QueryRun _queryRun)
{
    Query                   query = _queryRun.query();
    QueryBuildRange         range;

    query.clearQueryFilters();

    if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
    {
        vendInvoiceHeaderqbds       = query.dataSourceTable(tableNum(VendInvoiceInfoTable));
        vendInvoiceLineqbds         = query.dataSourceTable(tableNum(VendInvoiceInfoLine));
        vendDirectiveTableqbds      = query.dataSourceName(VendDirective_PSN_Unposted_ds.name());
        vendRoutinsSlipTableqbds    = query.dataSourceName(VendRoutingSlip_PSN_Unposted_ds.name());
        dimensionAtributeCombqbds   = query.dataSourceName(DimensionAttributeValueCombination_Unposted_ds.name());
        mainAccountqbds             = query.dataSourceName(MainAccount_Unposted_ds.name());

        // Add a query filter for the InvoiceAccount field
        filter = _queryRun.query().addQueryFilter(vendInvoiceHeaderqbds, fieldStr(VendInvoiceInfoTable, InvoiceAccount));

        // Add a query filter for the Posting date field
        filter = _queryRun.query().addQueryFilter(vendInvoiceHeaderqbds, fieldStr(VendInvoiceInfoTable, TransDate));

        // Add a datasource and range that will filter out any invoices that are currently opened for edit
        if (!activeInvoiceFilterqbds)
        {
            ActiveInvoiceFilterqbds = vendInvoiceHeaderqbds.addDataSource(tableNum(VendInvoiceInfoTable));
            ActiveInvoiceFilterqbds.joinMode(JoinMode::NoExistsJoin);
            ActiveInvoiceFilterqbds.fetchMode(QueryFetchMode::One2Many);
            ActiveInvoiceFilterqbds.addLink(fieldNum(VendInvoiceInfoTable, TableRefId), fieldNum(VendInvoiceInfoTable, TableRefId));

            range = ActiveInvoiceFilterqbds.addRange(fieldNum(VendInvoiceInfoTable, ParmId));
            range.value(SysQuery::valueNotEmptyString());
            range.status(RangeStatus::Hidden);
        }
    }
    else
    {
        vendInvoiceHeaderqbds       = query.dataSourceTable(tableNum(VendInvoiceJour));
        vendInvoiceLineqbds         = query.dataSourceTable(tableNum(VendInvoiceTrans));
        vendDirectiveTableqbds      = query.dataSourceName(VendDirective_PSN_Posted_ds.name());
        vendRoutinsSlipTableqbds    = query.dataSourceName(VendRoutingSlip_PSN_Posted_ds.name());
        dimensionAtributeCombqbds   = query.dataSourceName(DimensionAttributeValueCombination_Posted_ds.name());
        mainAccountqbds             = query.dataSourceName(MainAccount_Posted_ds.name());

        // Add a query filter for the InvoiceAccount field
        filter = query.addQueryFilter(vendInvoiceHeaderqbds, fieldStr(VendInvoiceJour, InvoiceAccount));

        // Add a query filter for the Invoice date field
        filter = _queryRun.query().addQueryFilter(vendInvoiceHeaderqbds, fieldStr(VendInvoiceJour, InvoiceDate));
    }

    mainAccountqbds.clearLinks();
    mainAccountqbds.addLink(fieldNum(DimensionAttributeValueCombination, MainAccount), fieldNum(MainAccount, RecId));

    // Add a query filter for the MainAccount field
    filter = query.addQueryFilter(mainAccountqbds, fieldStr(MainAccount, MainAccountId));

    // Add a query filter for the VendDirectiveId field
    filter = query.addQueryFilter(vendDirectiveTableqbds, fieldStr(VendDirective_PSN, VendDirectiveId));

    // Add a query filter for the VendRoutingSlipId field
    filter = query.addQueryFilter(vendRoutinsSlipTableqbds, fieldStr(VendRoutingSlip_PSN, VendRoutingSlipId));

    // Add a query filter for the DirectorApprovalStatus field
    filter = query.addQueryFilter(vendDirectiveTableqbds, fieldStr(VendDirective_PSN, DirectorApprovalStatus));

    // Add a query filter for the AccountApprovalStatus field
    filter = query.addQueryFilter(vendDirectiveTableqbds, fieldStr(VendDirective_PSN, AccountantApprovalStatus));
}

/// <summary>
/// Iterates through the currently selected records and assigns them to a routing slip if they have a
/// directive assigned and are not already part of a routing slip.
/// </summary>
public void assignRoutingSlip()
{
    Array               recordsMarked;
    int                 iLoop;
    VendDirective_PSN   vendDirective;
    VendRoutingSlip_PSN vendRoutingSlip;

    if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
    {
        recordsMarked = VendInvoiceInfoTable_ds.recordsMarked();
    }
    else
    {
        recordsMarked = VendInvoiceJour_ds.recordsMarked();
    }

    for (iLoop = 1; iLoop <= recordsMarked.lastIndex(); iLoop++)
    {
        if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
        {
            vendDirective = VendDirective_PSN_Unposted_ds.cursor(recordsMarked.value(iLoop));
        }
        else
        {
            vendDirective = VendDirective_PSN_Posted_ds.cursor(recordsMarked.value(iLoop));
        }

        if ((vendDirective) && (!vendDirective.VendRoutingSlip_PSN) && (strLen(vendDirective.VendDirectiveId) > 0))
        {
            if (!vendRoutingSlip)
            {
                vendRoutingSlip.initValue();
                vendRoutingSlip.insert();
            }

            vendDirective.VendRoutingSlip_PSN = vendRoutingSlip.RecId;
            vendDirective.update();
        }
    }

    if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
    {
        VendInvoiceInfoTable_ds.research();
    }
    else
    {
        VendInvoiceJour_ds.research();
    }
}

/// <summary>
/// Launches the <c>SysQuery</c> form for the user to set filters and if the user clicks the <c>Ok</c> button
/// it updates the controls on the header and runs the query to fill the grid.
/// </summary>
public void buildSysQuery()
{
    Query query;
    SysQueryRun queryRun;

    if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
    {
        query = VendInvoiceInfoTable_ds.queryRun().query();
    }
    else
    {
        query = VendInvoiceJour_ds.queryRun().query();
    }

    queryRun = new SysQueryRun(query, true);
    queryRun.title("@GLS223265");
    queryRun.promptLoadLastUsedQuery(false);
    queryRun.promptShowSorting(false);

    this.addQueryFilters(queryRun);

    if (queryRun.prompt())
    {
        this.setFilterControlValues(queryRun);

        date fromDateValue = DateTimeUtil::date(FromDate.dateTimeValue());
        date toDateValue = DateTimeUtil::date(ToDate.dateTimeValue());

        if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
        {
            VendInvoiceInfoTable_ds.queryRun().query().dataSourceName(VendInvoiceInfoTable_ds.name()).clearRange(fieldNum(VendInvoiceInfoTable, TransDate));
            VendInvoiceInfoTable_ds.queryRun().query().dataSourceName(identifierStr(VendInvoiceInfoTable)).clearRange(fieldNum(VendInvoiceInfoTable, RecId));
            VendInvoiceInfoTable_ds.queryRun().query().dataSourceName(VendInvoiceInfoTable_ds.name()).addRange(fieldNum(VendInvoiceInfoTable, TransDate)).value(SysQueryRangeUtil::dateRange(fromDateValue, toDateValue));
            VendInvoiceInfoTable_ds.research();
        }
        else
        {
            VendInvoiceJour_ds.queryRun().query().dataSourceName(VendInvoiceJour_ds.name()).clearRange(fieldNum(VendInvoiceJour, InvoiceDate));
            VendInvoiceJour_ds.queryRun().query().dataSourceName(identifierStr(VendInvoiceJour)).clearRange(fieldNum(VendInvoiceJour, RecId));
            VendInvoiceJour_ds.queryRun().query().dataSourceName(VendInvoiceJour_ds.name()).addRange(fieldNum(VendInvoiceJour, InvoiceDate)).value(SysQueryRangeUtil::dateRange(fromDateValue, toDateValue));
            VendInvoiceJour_ds.research();
        }
    }
}

/// <summary>
/// Sets the <c>Visible</c> property of the posted and unposted grid groups based on the
/// users selection on the form.
/// </summary>
public void hideShowGrid()
{
    gridGroup_Unposted.visible(pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending);
    gridGroup_Posted.visible(pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Posted);
}

/// <summary>
/// Initializes the form and defaults the <c>FromDate</c> and <c>ToDate</c> field values.
/// </summary>
public void init()
{
    QueryBuildRange range;

    super();

    // Clear Dynalinks so that our form isn't tied to the active record of the calling form
    VendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).clearDynalinks();
    VendInvoiceJour_ds.query().dataSourceTable(tableNum(VendInvoiceJour)).clearDynalinks();

    // Add a sort field to each header dataset
    VendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).addSortField(fieldNum(VendInvoiceInfoTable, Num));
    VendInvoiceJour_ds.query().dataSourceTable(tableNum(VendInvoiceJour)).addSortField(fieldNum(VendInvoiceJour, InvoiceId));

    pendingOrPosted = str2enum(pendingOrPosted, pendingPostedRadio.valueStr());

    FromDate.dateTimeValue(DateTimeUtil::getSystemDateTime());
    ToDate.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(DateTimeUtil::maxValue(), DateTimeUtil::getUserPreferredTimeZone()));

    //Add a range to keep the grid from loading records on form launch
    range = VendInvoiceInfoTable_ds.query().dataSourceName(identifierStr(VendInvoiceInfoTable)).addRange(fieldNum(VendInvoiceInfoTable, RecId));
    range.value('0');
    range.status(RangeStatus::Hidden);

    range = VendInvoiceJour_ds.query().dataSourceName(identifierStr(VendInvoiceJour)).addRange(fieldNum(VendInvoiceJour, RecId));
    range.value('0');
    range.status(RangeStatus::Hidden);
}

/// <summary>
/// Inserts a new record into the <c>VendDirective_PSN</c> table.
/// </summary>
/// <returns>
/// The value of the <c>RecId</c> field of the new record.
/// </returns>
public CustVendDirectiveRecId_PSN insertVendDirectiveRecord()
{
    VendDirective_PSN vendDirective;

    vendDirective.initValue();
    vendDirective.insert();

    return vendDirective.RecId;
}

/// <summary>
/// Disables the Directive fields if the DirectiveId has not been assigned in a
/// public sector France context.
/// </summary>
public void setFieldsEditableForDirective()
{
    boolean isDirectiveSet;

    if (CustVendDirective_PSN::isContextPublicSectorFrance())
    {
        if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
        {
            isDirectiveSet = (VendDirective_PSN_Unposted.VendDirectiveId != '');

            Unposted_DirectorApprovalStatus_PSN.allowEdit(isDirectiveSet);
            Unposted_AccountantApprovalStatus_PSN.allowEdit(isDirectiveSet);
            VendDirective_PSN_Unposted_IssueDate.allowEdit(isDirectiveSet);
        }
        else
        {
            isDirectiveSet = (VendDirective_PSN_Posted.VendDirectiveId != '');

            Posted_DirectorApprovalStatus_PSN.allowEdit(isDirectiveSet);
            Posted_AccountantApprovalStatus_PSN.allowEdit(isDirectiveSet);
            VendDirective_PSN_Posted_IssueDate.allowEdit(isDirectiveSet);
        }
    }
}

/// <summary>
/// Updates the fields at the top of the form with the filter values that were added in the <c>SysQuery</c> form.
/// </summary>
/// <param name="_queryRun">
/// The <c>QueryRun</c> object that was used on the <c>SysQuery</c> form.
/// </param>
public void setFilterControlValues(QueryRun _queryRun)
{
    if (pendingOrPosted == CustVendDirectiveInvoiceStatus_PSN::Pending)
    {
        filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), vendInvoiceHeaderqbds, fieldId2name(tablenum(VendInvoiceInfoTable), fieldNum(VendInvoiceInfoTable, InvoiceAccount)));
    }
    else
    {
        filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), vendInvoiceHeaderqbds, fieldId2name(tablenum(VendInvoiceJour), fieldNum(VendInvoiceJour, InvoiceAccount)));
    }

    InvoiceAccounts.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), vendDirectiveTableqbds, fieldId2name(tablenum(VendDirective_PSN), fieldNum(VendDirective_PSN, VendDirectiveId)));
    VendDirectiveId.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), vendRoutinsSlipTableqbds, fieldId2name(tablenum(VendRoutingSlip_PSN), fieldNum(VendRoutingSlip_PSN, VendRoutingSlipId)));
    VendRoutingSlipId.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), vendDirectiveTableqbds, fieldId2name(tablenum(VendDirective_PSN), fieldNum(VendDirective_PSN, DirectorApprovalStatus)));
    DirectorStatus.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), vendDirectiveTableqbds, fieldId2name(tablenum(VendDirective_PSN), fieldNum(VendDirective_PSN, AccountantApprovalStatus)));
    AccountantStatus.text(filter.value());

    filter = SysQuery::findOrCreateQueryFilter(_queryRun.query(), mainAccountqbds, fieldId2name(tablenum(MainAccount), fieldNum(MainAccount, MainAccountId)));
    MainAccounts.text(filter.value());
}

/// <summary>
/// Sets the <c>VendDirectiveId</c> field of the <c>VendDirective_PSN</c> record referenced by
/// the passed in <c>CustVendDirectiveRecId_PSN</c> value.
/// </summary>
/// <param name="_recId">
/// The <c>CustVendDirectiveRecId_PSN</c> value of the <c>VendDirective_PSN</c> record to update.
/// </param>
/// <remarks>
/// The table will only be updated if the <c>VendDirectiveId</c> field does not already have a value.
/// </remarks>
public void updateVendDirectiveNumberSequence(CustVendDirectiveRecId_PSN _recId)
{
    VendDirective_PSN vendDirective;

    vendDirective = VendDirective_PSN::find(_recId, true);

    if ((vendDirective) && (strLen(vendDirective.VendDirectiveId) == 0))
    {
        vendDirective.setVendDirectiveId();
        vendDirective.update();
    }
}

