/// <summary>
/// Sets the <c>Visible</c> property of the posted and unposted grid groups based on the
/// users selection on the <c>PendingPostedRadio</c> control.
/// </summary>
/// <returns>
/// a non-zero <c>integer</c> value if the record changes successfully; otherwise, zero.
/// </returns>
public int selectionChange()
{
    int ret;

    ret = super();

    if (ret)
    {
        pendingOrPosted = str2enum(pendingOrPosted, pendingPostedRadio.valueStr());
        element.hideShowGrid();
    }

    return ret;
}

/// <summary>
/// Validates that the value in the <c>FromDate</c> field is not greater than the value in the <c>ToDate</c> field.
/// </summary>
/// <returns>
/// true if the value in the <c>FromDate</c> field is valie; otherwise, false.
/// </returns>
public boolean validate()
{
    boolean isValid;

    isValid = super();

    if (isValid)
    {
        if (this.dateTimeValue() > ToDate.dateTimeValue())
        {
            isValid = checkFailed("@GLS223139");
        }
    }

    return isValid;
}

/// <summary>
/// Validates that the value in the <c>ToDate</c> field is not less than the value in the <c>FromDate</c> field.
/// </summary>
/// <returns>
/// true if the value in the <c>ToDate</c> field is valie; otherwise, false.
/// </returns>
public boolean validate()
{
    boolean isValid;

    isValid = super();

    if (isValid)
    {
        if (this.dateTimeValue() < FromDate.dateTimeValue())
        {
            isValid = checkFailed("@GLS223140");
        }
    }

    return isValid;
}

/// <summary>
/// Displays the <c>SysQuery</c> form to the user and fills the grid with posted invoice line
/// records that match the criteria entered.
/// </summary>
void clicked()
{
    element.buildSysQuery();
}

/// <summary>
/// Iterates through the list of marked unposted invoice lines and either sets the <c>VendDirectiveId</c> value of the
/// <c>VendDirective_PSN</c> table or inserts a new record into the <c>VendDirective_PSN</c> table if one doesn't yet exist.
/// </summary>
void clicked()
{
    Array                   recordsMarked   = VendInvoiceInfoTable_ds.recordsMarked();
    int                     iLoop;
    VendInvoiceInfoLine     vendInvoiceInfoLineLocal;

    for (iLoop = 1; iLoop <= recordsMarked.lastIndex(); iLoop++)
    {
        vendInvoiceInfoLineLocal = VendInvoiceInfoLine_ds.cursor(recordsMarked.value(iLoop));

        if (vendInvoiceInfoLineLocal.VendDirective_PSN)
        {
            element.updateVendDirectiveNumberSequence(vendInvoiceInfoLineLocal.VendDirective_PSN);
        }
        else
        {
            vendInvoiceInfoLineLocal.VendDirective_PSN = element.insertVendDirectiveRecord();
            vendInvoiceInfoLineLocal.update();
        }
    }

    VendInvoiceInfoTable_ds.research();
}

/// <summary>
/// Adds the marked <c>VendDirective_PSN</c> records to a routing slip if they are not
/// already associated with one.
/// </summary>
void clicked()
{
    element.assignRoutingSlip();
}

