/// <summary>
/// Changes the editability of VendInvoiceInfoLine_W.freightNature_BR field.
/// </summary>
void allowEditFreightNature_BR()
{
    boolean allowEdit;

    switch (vendInvoiceInfoTable_W.FiscalDocumentModel_BR)
    {
        case #fiscalDocModel_07:
        case #fiscalDocModel_08:
        case #fiscalDocModel_8B:
        case #fiscalDocModel_09:
        case #fiscalDocModel_10:
        case #fiscalDocModel_11:
        case #fiscalDocModel_26:
        case #fiscalDocModel_27:
        case #fiscalDocModel_57:
            allowEdit = true;
            break;
        default:
            allowEdit = false;
            break;
    }

    vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, FreightNature_BR)).allowEdit(allowEdit);

    if (!allowEdit)
    {
        this.resetLinesFreightNature_BR();
    }
}

/// <summary>
/// This method is used to change the editability of VendInvoiceInfoTable_PackingSlipIdForUpdate_W control.
/// </summary>
void allowEditPackingSlips_W()
{
    vendInvoiceInfoTable_PackingSlipIdForUpdate_W.allowEdit(purchParmUpdate.SpecQty == PurchUpdate::PackingSlip);
    if (documentStatus == DocumentStatus::PackingSlip)
    {
        vendInvoiceInfoTable_PackingSlipIdForUpdate_W.allowEdit(vendInvoiceInfoTable_W.DocAutoNumbering_LT);
    }
}

void allowEditsAllDatasources(boolean _allowEdits)
{
    int i;
    FormDataSource dataSource;

    for (i = 1; i <= element.dataSourceCount(); i++)
    {
        dataSource = element.dataSource(i);

        // Only allow edit if initial property allowed it
        if (_allowEdits && !uneditableDataSourcesSet.in(i))
        {
            dataSource.allowEdit(true);
        }
        else
        {
            dataSource.allowEdit(false);
        }
    }
}

void allowEditsForHeader()
{
    workflowCompleted = false;
    isInAutomatedProcessing = VendInvoiceAutomationStateManagement::isInvoiceReadonly(vendInvoiceInfoTable);
    isVendInvoiceGeneratedFromCustProjInvoice = vendInvoiceInfoTable.checkIfVendInvoiceIsGeneratedFormCustProjInvoice();

    updateMatchStatus.enabled(!isInAutomatedProcessing);
    buttonParmTablePackingSlips.enabled(!isInAutomatedProcessing);

    if (isInAutomatedProcessing)
    {
        element.design().viewEditMode(ViewEditMode::View);
        vendInvoiceInfoTable_IncludeInAutomatedProcessing.viewEditMode(ViewEditMode::Edit);
        workflowFullEnabled = false;
        headerDeleteEnabled = false;
        workflowPartiallyEnabled = false;
    }
    else
    {
        element.design().viewEditMode(ViewEditMode::Auto);

        if (purchEditLinesForm_Invoice.invoiceHeaderEditFull(vendInvoiceInfoTable))
        {
            element.allowEditsAllDatasources(true);
            workflowFullEnabled = true;
            workflowPartiallyEnabled = true;
            headerDeleteEnabled = true;
        }
        else if (purchEditLinesForm_Invoice.invoiceHeaderEditPartial(vendInvoiceInfoTable))
        {
            element.allowEditsAllDatasources(true);
            workflowFullEnabled = false;
            headerDeleteEnabled = false;
            workflowPartiallyEnabled = true;
        }
        else
        {
            element.allowEditsAllDatasources(false);
            workflowFullEnabled = false;
            workflowPartiallyEnabled = VendInvoiceWorkflow::isUserTaskOrApprovalOwner(vendInvoiceInfoTable);
            headerDeleteEnabled = (vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Approved);
            workflowCompleted = (vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Approved);
        }
    }

    element.enableHeaderActions();
}

void allowEditsForLine()
{
    if (isInAutomatedProcessing)
    {
        workflowLinesPartiallyEnabled = false;
        element.allowLineEdits(false);
        element.enableLineActions(false);
    }
    else
    {
        if (purchEditLinesForm_Invoice.invoiceLineEditFull(vendInvoiceInfoTable) &&
            !isLinePrepaymentApplication)
        {
            workflowLinesPartiallyEnabled = true;
            element.allowLineEdits(true);
            element.enableLineActions(true);
        }
        else if (purchEditLinesForm_Invoice.invoiceLineEditPartial(vendInvoiceInfoTable) &&
            !isLinePrepaymentApplication)
        {
            workflowLinesPartiallyEnabled = true;
            element.allowLineEdits(true);
            element.enableLineActions(false);
        }
        else
        {
            workflowLinesPartiallyEnabled = false;
            element.allowLineEdits(false);
            element.enableLineActions(false);
        }
    }
}

public void allowEditsRAsset()
{
    boolean isNonPO = vendInvoiceInfoLine.isNonPO();
    boolean isAssetGroup = vendInvoiceInfoLine.inventTable().AssetGroupId_RU != '' || vendInvoiceInfoLine.ProcurementCategory;

    rassetpurchTrans_ds.allowCreate(isNonPO && isAssetGroup);
    rassetpurchTrans_ds.allowDelete(rAssetPurchTrans.Posted ? false : vendInvoiceInfoLine.isNonPO());

    rassetpurchTrans_ds.allowEdit(isAssetGroup);
    rAssetPurchTrans_ds.object(fieldNum(RAssetPurchTrans, AssetId)).allowEdit(isNonPO);
    rAssetPurchTrans_ds.object(fieldNum(RAssetPurchTrans, AssetId)).skip(isNonPO);
    rAssetPurchTrans_ds.object(fieldNum(RAssetPurchTrans, ReceiveNow)).visible(! isNonPO);
}

/// <summary>
/// Changes the editability of VendInvoiceInfoTable_W.typeOfCTe_BR field.
/// </summary>
void allowEditTypeOfCTe_BR()
{
    const container allowedModels = [FiscalBooksConstants_BR::FiscalDocModel_57,
                                     FiscalBooksConstants_BR::FiscalDocModel_67];

    boolean allowEdit = conFind(allowedModels, vendInvoiceInfoTable_W.FiscalDocumentModel_BR);

    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, TypeOfCTe_BR)).allowEdit(allowEdit);

    if (!allowEdit)
    {
        vendInvoiceInfoTable_W.TypeOfCTe_BR = TypeOfCTe_BR::Blank;
    }
}

void allowHeaderCreate()
{
    boolean allowHeaderCreate = true;

    if (isLastInvoiceAdvance)
    {
        // Can't create header when prepayment invoice
        allowHeaderCreate = false;
    }

    // <GEEPL>
    allowHeaderCreate = allowHeaderCreate && purchEditLinesForm_Invoice.allowHeaderCreate();
    // </GEEPL>

    vendInvoiceInfoTable_ds.allowCreate(allowHeaderCreate);
}

void allowLineCreate()
{
    boolean allowLineCreate = true;

    // --- Determine if lines can be created ---
    if (!workflowFullEnabled || vendInvoiceInfoTable.InvoiceAccount == '')
    {
        // Can't create lines until vendor is entered
        allowLineCreate = false;
    }
    else if (!vendInvoiceInfoTable.isNonPO() && vendInvoiceInfoTable.purchTable().isInterCompanyOrder())
    {
        // Can't create lines when header has an inter-company PO assigned
        allowLineCreate = false;
    }
    else if (vendInvoiceInfoTable.existIntercompanyVendorPurchaseOrderLine())
    {
        // Can't create lines when the invoice account is intercompany and a purchase order is referenced on the invoice
        allowLineCreate = false;
    }
    else if (isLastInvoiceAdvance)
    {
        // Can't create lines when header is prepayment invoice
        allowLineCreate = false;
    }

    allowLineCreate = allowLineCreate && purchEditLinesForm_Invoice.allowLineCreate();
    vendInvoiceInfoLine_ds.allowCreate(allowLineCreate);
}

public void allowLineEdits(boolean _canEdit)
{
    vendInvoiceInfoLine_ds.allowEdit(_canEdit);
    vendInvoiceInfoSubTable_ds.allowEdit(_canEdit);
    vendInvoiceInfoLine_Asset_ds.allowEdit(_canEdit);
    vendInvoiceInfoLine_Project_ds.allowEdit(_canEdit);
    vendInvoiceLineForeignTradeCategory_ds.allowEdit(_canEdit);
}

/// <summary>
/// Enables automatic submit to workflow details group and fields.
/// </summary>
internal void enableGroupAutomaticSubmitToWorkflowDetails()
{
    if (VendInvoiceAutomationStateManagement::isAutomationInvoice(vendInvoiceInfoTable))
    {
        groupAutomaticSubmitToWorkflowDetails.visible(true);
        if (VendInvoiceIncludeManualCreatedInvoiceIntoAutomationFeature::isEnabled())
        {
            VendInvoiceAutomationHistoryTimelineDialog.visible(vendInvoiceInfoTable.AutomationStatus != VendInvoiceAutomationStatus::NotApplicable);
            VendInvoiceAutomationHistoryTimelineDialog.text("@AccountsPayable:VendInvoiceAutomationHistoryTimelineDialogLabelV2");
            groupAutomaticSubmitToWorkflowDetails.caption("@AccountsPayable:VendInvoiceAutomaticProcessingGroupV2");
            VendInvoiceInfoTable_AutomationStatus.label("@AccountsPayable:VendInvoiceAutomationStatusV2");
        }

        this.enableIncludeInAutomatedProcessing();

        automationFailureControlManager.initializeFailureControlsForInvoice(VendInvoiceInfoTable);
    }
}

private void enableGroupAutomaticMatchingFailedDetails()
{
    boolean shouldAutomaticMatchingFailuresBeShown = false;

    if (VendAutomateVendorInvoicesFeature::isEnabled() &&
        vendParameters.editUseInvoiceMatching() == NoYes::Yes &&
        vendParameters.AutomationProductReceiptMatching == NoYes::Yes)
    {
        // Check if the most recent matching event log entry indicates a failure
        DocumentEventLog mostRecentProductReceiptMatchingEventLog = DocumentEventLog::findMostRecentLogEntryOfType(
            tableNum(VendInvoiceInfoTable),
            VendInvoiceInfoTable.RecId,
            enumNum(VendInvoiceMatchProductReceiptToInvoiceLineEvent));

        if (mostRecentProductReceiptMatchingEventLog.RecId != 0 &&
            mostRecentProductReceiptMatchingEventLog.EventEnumValue == VendInvoiceMatchProductReceiptToInvoiceLineEvent::Failed)
        {
            if (VendInvoiceInfoTable.DocumentOrigin == DocumentOrigin::Service)
            {
                // If the invoice is of service origin, the matching failure details should only
                // appear if automatic workflow submission is not enabled
                shouldAutomaticMatchingFailuresBeShown = vendParameters.AutomationWorkflowSubmission == NoYes::No;
            }
            else
            {
                // If the invoice is not of service origin, matching failure details should always appear
                shouldAutomaticMatchingFailuresBeShown = true;
            }
        }
    }

    groupAutomaticMatchingFailedDetails.visible(shouldAutomaticMatchingFailuresBeShown);
}

