    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, IncludeInAutomatedProcessing)).enabled(VendInvoiceAutomationStateManagement::canUpdateAutomationStatusManually(vendInvoiceInfoTable));
}

