void calcTotals(VendInvoiceInfoTable calcVendInvoiceInfoTable = vendInvoiceInfoTable)
{
    calcVendInvoiceInfoTable.reread();
    calcVendInvoiceInfoTable.initInvoiceTotals(true);

    if (calcVendInvoiceInfoTable.RecId == vendInvoiceInfoTable.RecId)
    {
        // <GEEU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
        {
            purchFormLetter.initOfficials_RU(calcVendInvoiceInfoTable);
        }
        // </GEEU>
        vendInvoiceInfoTable.write();

        // Daatsource VendInvoiceInfoLine must be set to Linktype = active
        searchParmLine = false;
        vendInvoiceInfoTable_ds.active();
        searchParmLine = true;
    }
    else
    {
        calcVendInvoiceInfoTable.write();
    }
}

public void calculateTaxes()
{
    PurchTotals purchTotals;

    purchTotals = PurchTotals::newParmTable(vendInvoiceInfoTable,
                                            element.specQty(),
                                            vendInvoiceInfoTable.ParmId,
                                            '',
                                            vendInvoiceInfoTable.Ordering);
    purchTotals.calc(false, false, true);

    if (isTaxServiceForPurchaseEnabled)
    {
        element.rereadAfterCalculatingTaxUsingTaxIntegration();
    }
}

public void updateProjectControls(boolean _projOpsIntegEnabled)
{
    if (_projOpsIntegEnabled)
    {
        projInterCompanyVendorInvoiceFeatureEnabled =  FeatureStateProvider::isFeatureEnabled(ProjInterCompanyVendorInvoiceFeature::instance());
        if (!this.canProjectBeAssigned())
        {
            vendInvoiceInfoLine_Project_ds.allowEdit(false);
        }

        if (projInterCompanyVendorInvoiceFeatureEnabled)
        {
            this.updateAccessibility(VendInvoiceInfoLine_Project_ProjDataAreaId, true, true, true);
        }
        else
        {
            this.updateAccessibility(VendInvoiceInfoLine_Project_ProjDataAreaId, true, true, false);
        }
        // Set ProjTransId to read-only
        this.updateAccessibility(Transaction_ProjTransId, true, true, false);

        boolean projVendorInvoiceEnabledForProjOps = FeatureStateProvider::isFeatureEnabled(ProjProjectOperationsProcurementIntegrationFeature::instance());
        this.updateAccessibility(PurchParmLine_Project_ProjId, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(PurchParmLine_Project_ProjActivityNumber, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(PurchParmLine_Project_ProjCategoryId, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(PurchParmLine_ItemId2, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(PurchParmLine_Project_ProjLinePropertyId, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(Transaction_TransDate, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(PurchParmLine_ReceiveNow2, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(PurchParmLine_PurchPrice1, true, true, projVendorInvoiceEnabledForProjOps);
        this.updateAccessibility(PurchParmLine_LineAmount2, true, true, projVendorInvoiceEnabledForProjOps);

        projSubcontractActualsPostingFeatureEnabled= FeatureStateProvider::isFeatureEnabled(ProjEnableSubcontractActualsPostingFeature::instance());
        this.updateAccessibility(SubconVendorInvoice_SubcontractInvoice, projSubcontractActualsPostingFeatureEnabled, true, projSubcontractActualsPostingFeatureEnabled);
        boolean procurementCategoryEnabled = (projVendorInvoiceEnabledForProjOps && vendInvoiceInfoLine_Project.ProjId && vendInvoiceInfoLine.ProcurementCategory);
        boolean hourCategory = ProjCategory::isProjCategoryTypeHour(vendInvoiceInfoLine_Project.ProjCategoryId);
        this.updateAccessibility(Role, projSubcontractActualsPostingFeatureEnabled, true, (projSubcontractActualsPostingFeatureEnabled && procurementCategoryEnabled && hourCategory));

        // Hide below fields
        this.updateAccessibility(SalesPrice_ProjSalesUnitId, false, false, false);
        this.updateAccessibility(SalesPrice_ProjSalesCurrencyId, false, false, false);
        this.updateAccessibility(SalesPrice_ProjSalesPrice, false, false, false);
        this.updateAccessibility(ProjTransferPrice_TransferCurrency, false, false, false);
        this.updateAccessibility(ProjTransferPrice_TransferPrice, false, false, false);
        this.updateAccessibility(VendInvoiceInfoLine_Project_Resource, false, false, false);
        this.updateAccessibility(ProjectSalesTax_ProjTaxItemGroupId, false, false, false);
        this.updateAccessibility(ProjectSalesTax_ProjTaxGroupId, false, false, false);
        vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjTaxGroupId)).allowEdit(false);
        vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjTaxItemGroupId)).allowEdit(false);
    }
}

protected void updateAccessibility(FormControl _control, boolean _visible, boolean _enabled, boolean _editable)
{
    _control.visible(_visible);
    _control.enabled(_enabled);
    _control.allowEdit(_editable);
}

boolean canClose()
{
    VendInvoiceInfoTable vendInvoiceInfoTableLocal;
    boolean ret = true;
    boolean showDialog;
    // <GIN>
    VendInvoiceInfoLine vendInvoiceInfoLineLocal;
    VendInvoiceInfoLine vendInvoiceInfoLineFirst;
    // </GIN>
    // <GTE>
    TransitDocumentManager_IN transitDocManager;
    // </GTE>

    if (!isInvoicePosting && boxFormOnceIsActive)
    {
        return false;
    }

    if (this.closedOk())
    {
        // <GEEU>
        if (countryRegion_LTLV && docNumberingCode_LT.isVisible() && ! docNumberingCode_LT.text())
        {
            return checkFailed(strFmt("@SYS26332", fieldPName(LtInvoiceAutoNumberingTable, NumberingCode)));
        }
        // </GEEU>

        // <GTE>
        if (TaxIntegrationUtils::isTransitDocumentEnabled())
        {
            transitDocManager = TransitDocumentManager_IN::newManager();
            if (transitDocManager.existsAnyTransitDocType() &&
                transitDocManager.existTransitRuleForTransType(tableNum(VendInvoiceInfoLine)))
            {
                // If invoice lines have different form information, apply the first line
                vendInvoiceInfoLineFirst = VendInvoiceInfoLine_ds.getFirst();

                while (vendInvoiceInfoLineLocal)
                {
                    vendInvoiceInfoLineLocal = VendInvoiceInfoLine_ds.getNext();
                    transitDocManager.transit(vendInvoiceInfoLineFirst, vendInvoiceInfoLineLocal);
                }
            }
        }
        // </GTE>

        showDialog = purchFormLetter.parmShowDialog();
        purchFormLetter.parmShowDialog(false); // Suppress reopening this form which will be called from purchFormLetter.checkCloseDialog() below.

        if (!purchFormLetter.checkCloseDialog())
        {
            validationErrorsExist = true;
            purchFormLetter.parmShowDialog(showDialog);
            purchParmUpdate_ds.executeQuery();
            vendInvoiceInfoTable_ds.research();
            return false;
        }

        purchFormLetter.parmShowDialog(showDialog);

        if (purchParmUpdate.SumBy == AccountOrder::Order)
        {
            vendInvoiceInfoTableLocal = VendInvoiceInfoTable::findPurchId(purchParmUpdate.SumPurchId, purchParmUpdate.ParmId);

            if (!vendInvoiceInfoTable_ds.checkPaymentId(vendInvoiceInfoTableLocal))
            {
                return false;
            }
        }
    }

    if (ret)
    {
        ret = super();
    }

    return ret;
}

