void checkParmTable()
{
    [numOfAccounts, numOfCurrency] = PurchFormLetter_Invoice::checkParmTable(purchParmUpdate.ParmId, vendAccountInQuery, invoiceAccountInQuery);
}

private void chooseLines_RU()
{
    purchFormLetter.purchParmUpdate(purchParmUpdate.data());
    purchFormLetter.chooseLines(false, vendInvoiceInfoTable.data(), vendInvoiceInfoTable.isNonPO());

    // have to reread data to avoid unsaved data writing at the executeQuery call bellow
    vendInvoiceInfoTable_ds.executeQuery();
    if (vendInvoiceInfoTable_ds.numberOfRowsLoaded() > 1)
    {
        isSingleInvoiceHeader = false;
        element.setFormDesign(VendInvoiceDesignMode::MultipleInvoice);
    }
}

void chooseTable(boolean _reArrange = false)
{
    editLinesChanged = true;

    purchParmUpdate_ds.executeQuery();
    vendInvoiceInfoTable_ds.research(true);

    element.checkParmTable();

    if (_reArrange)
    {
        element.reArrangeNow();
    }
}

void chooseTrans()
{
    vendInvoiceInfoLine_ds.executeQuery();
}

/**
    clear parmTables totals and synchronizes form cache
**/
void clearParmTableTotals()
{
    purchParmUpdate.clearParmTableTotals();
    element.updateFormCacheVendInvoiceInfoTable();
}

void close(boolean _saveAndClose = true)
{
    super();

    if (_saveAndClose && !isInvoiceSaved)
    {
        element.saveInvoicesAndClose();
    }
}

void closeOk()
{
    element.saveInvoicesAndClose();

    super();
}

/// <summary>
/// Configs the Letter of Credit related controls.
/// </summary>
public void configBankLCControls()
{
    boolean isAllowEdit;
    boolean isVisible = purchEditLinesForm_Invoice.bankLCImportLine();

    if (isVisible)
    {
        isAllowEdit = purchEditLinesForm_Invoice.editBankLCImportLine();
    }

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BankLCImportLine)).visible(isVisible);
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BankLCImportLine)).allowEdit(isAllowEdit);

    if (isAllowEdit)
    {
        purchEditLinesForm_Invoice.setupBankLCImportLineController();
        summaryPurchSetup.enabled(false);
        buttonRetrievePO.enabled(false);
    }
}

/// <summary>
/// Creates the agreement link in the current <c>VendInvoiceInfoTable</c> table record based on the values in
/// the current <c>VendInvoiceInfoTable</c> table record.
/// </summary>
public void createAgreementLink()
{
    AgreementLine localAgreementLine;
    Args args;
    FormRun formRun;
    VendInvoiceInfoLine origVendInvoiceInfoLine;

    args = new Args(formStr(AgreementLinePrompt));
    args.caller(element);
    args.record(vendInvoiceInfoLine);
    args.parm(int642str(vendInvoiceInfoTable.PurchAgreementHeader_PSN));
    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait(true);
    localAgreementLine = args.record() as AgreementLine;

    if (localAgreementLine)
    {
        // Make a pre-associated copy of the current purchase line.
        origVendInvoiceInfoLine = vendInvoiceInfoLine.data().orig();

        ttsbegin;

        vendInvoiceInfoLine.selectForUpdate(true);
        vendInvoiceInfoLine.AgreementLine_PSN = localAgreementLine.RecId;
        vendInvoiceInfoLine.update();

        ttscommit;

        vendInvoiceInfoLine_ds.executeQuery();
        element.args().lookupRecord(origVendInvoiceInfoLine);
        vendInvoiceInfoLine_ds.research(false);

        this.enableAgreementButtons(true, true, true);
        info("@SYS4002119");
    }
}

void createDocFromPO()
{
    purchFormLetter.initParameters(purchParmUpdate.data());
    purchFormLetter.editLinesChanged(editLinesChanged);
    purchFormLetter.reArrangeNow(reArrangeNow);
    purchFormLetter.wmsJournalTable_ds(null);
    purchFormLetter.showQueryForm(true);

    purchFormLetter.initLinesQuery();
    purchFormLetter.prePromptModifyData();

    element.chooseTable();

    element.checkForPrepaymentApplicationFlag();
    element.checkForVendActiveInvoiceFlag(VendActiveInvoiceFlags::SalesTaxAdjustmentsProrated, "@AccountsPayable:SalesTaxAdjustmentsProrated");
}

/// <summary>
/// Destroys this current session and sets up the <c>PurchFormLetter_Invoice</c> instance up to create a new session as this session completes.
/// </summary>
/// <param name = "_newCompany">The new DataAreaId value for the company in which the new session will be created</param>
public void createNewSessionForNewCompany(DataAreaId _newCompany)
{
    vendInvoiceInfoTable.delete();
    purchParmUpdate.delete();

    PurchFormLetterNewSession newSession = PurchFormLetterNewSession::construct(
        new MenuFunction(menuItemDisplayStr(PurchFormLetter_NewInvoiceDisplay), MenuItemType::Display), _newCompany);

    purchFormLetter.parmPurchFormLetterNewSession(newSession);

    this.close(false);
}

/// <summary>
/// This method is called post super() of modified() of the form controls that are bound to the ProcurementCategory field
/// on VendInvoiceInfoLine. It needs to be specifically called in that location due to issues with resolving the
/// ProcurementCategory reference and caling create() on an inner/outer joined FormDataSource.
/// </summary>
private void defaultDataSourceSettingsBasedOnProcurementCategory()
{
    if (vendInvoiceInfoLine.isCategoryBased())
    {
        if (vendInvoiceLineForeignTradeCategory.VendInvoiceInfoLine == 0)
        {
            //Only create a new record if none exist
            vendInvoiceLineForeignTradeCategory_ds.allowCreate(true);
            vendInvoiceLineForeignTradeCategory_ds.create();
            vendInvoiceLineForeignTradeCategory_ds.allowEdit(true);
            vendInvoiceLineForeignTradeCategory_ds.allowCreate(false);
        }
    }
    else
    {
        vendInvoiceLineForeignTradeCategory.clearFormFields();
        vendInvoiceLineForeignTradeCategory_ds.allowEdit(false);
        vendInvoiceLineForeignTradeCategory_ds.refresh();
    }

    vendInvoiceInfoLine_Project_ds.setAllowEdit();
}

private void disableFieldsForReversingFiscalDoc_BR()
{
    if (vendInvoiceInfoTable.purchTable().isReversingFiscalDocument_BR())
    {
        vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentModel_BR)).allowEdit(false);
        vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, AccessKey_BR)).allowEdit(false);
        fiscalDocumentSeries_BR.allowEdit(false);
        CopyOfFiscalDocumentSeries_BR.allowEdit(false);
        vendInvoiceInfoTable_DS.object(fieldNum(VendInvoiceInfoTable, Num)).allowEdit(false);
        vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentSpecie_BR)).allowEdit(false);
        vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentType_BR)).allowEdit(false);
    }
}

