public void enableHeaderActions()
{
    vendInvoiceInfoTable_ds.allowDelete(headerDeleteEnabled);

    // Disable the Invoice line's Add, Delete functionality through keyboard
    vendInvoiceInfoLine_ds.allowCreate(workflowFullEnabled);
    vendInvoiceInfoLine_ds.allowDelete(workflowFullEnabled);

    // Disable delete the Invoice
    if (projInterCompanyVendorInvoiceFeatureEnabled)
    {
        vendInvoiceInfoTable_ds.allowDelete(!isVendInvoiceGeneratedFromCustProjInvoice);
        vendInvoiceInfoLine_ds.allowDelete(!isVendInvoiceGeneratedFromCustProjInvoice);
        removeButton.enabled(!isVendInvoiceGeneratedFromCustProjInvoice);
    }

    buttonDistributeLedgerAccounts.enabled(workflowPartiallyEnabled);

    buttonPageParmTableMiscCharges.enabled(workflowPartiallyEnabled && !isLastInvoiceAdvance);
    buttonPageParmTableAllocate.enabled(workflowPartiallyEnabled && !isLastInvoiceAdvance);

    // Disable the controls
    element.enableRetrievePOButton();
    buttonApplyPrepayments.enabled(workflowFullEnabled && !isLastInvoiceAdvance);

    menuItemButtonAdd.enabled(workflowFullEnabled && !isLastInvoiceAdvance);
    removeButton.enabled(workflowFullEnabled && !isLastInvoiceAdvance);
    PurchParmTable_PurchId.enabled(workflowFullEnabled);
    PurchParmTable_PurchAggHeader.allowEdit(workflowFullEnabled);

    parmTableTotals.enabled((!workflowCompleted && workflowPartiallyEnabled) || isInAutomatedProcessing);
    //<GTE>
    taxTransSource.visible(!TaxSolutionScopeIntegrationUtil::isCompanyEnabled());
    //</GTE>
    taxTransSource.enabled(!workflowCompleted && workflowPartiallyEnabled);

    specQtyDropDialog.enabled(workflowFullEnabled
        && vendInvoiceDesignMode != VendInvoiceDesignMode::MultipleInvoiceHeader
        && purchEditLinesForm_Invoice.specQtyAllowEdit());

    subLedger.enabled(!isInAutomatedProcessing);
    menuItemButton_DistributionsView.enabled(!isInAutomatedProcessing);
    overview_Process.enabled(!isInAutomatedProcessing);
    overview_Setup.enabled(!isInAutomatedProcessing);
    buttonHeader_BudgetCheckResults.enabled(!isInAutomatedProcessing);
    projBudgetDisplay.enabled(!isInAutomatedProcessing);
    buttonOpenExcelTemplate.enabled(!isInAutomatedProcessing);
    if (projInterCompanyVendorInvoiceFeatureEnabled)
    {
        removeButton.enabled(!isVendInvoiceGeneratedFromCustProjInvoice);
    }

    this.enableHeaderDeliveryAddressFields();
}

/// <summary>
/// Enables or disables invoice related documnet fields based on PO and PA values.
/// </summary>
/// <param name="_purchID">
/// Purchase Order no.
/// </param>
/// <param name="_purchAgreement">
/// Purchase Agreement number.
/// </param>
private void enableInvoiceRelatedDocFields(str _purchID, str _purchAgreement)
{
    if (_purchID != '' && _purchAgreement == '0')
    {
        if (!VendInvoiceEditPurchaseAgreementFromPendingVendorInvoiceFlight::instance().isEnabled())
        {
            purchParmTable_PurchAggHeader.enabled(false);
        }
    }
    else if (_purchID == '' && _purchAgreement != '0')
    {
        purchParmTable_PurchId.enabled(false);
    }
    else
    {
        purchParmTable_PurchId.enabled(workflowFullEnabled);
        purchParmTable_PurchAggHeader.allowEdit(workflowFullEnabled);
    }
}

void enableLineActions(boolean _enable)
{
    vendInvoiceInfoLine_ds.allowDelete(_enable);
    vendInvoiceInfoSubTable_ds.allowDelete(_enable);
    vendInvoiceInfoSubLine_ds.allowDelete(_enable);
    vendInvoiceInfoLine_Asset_ds.allowDelete(_enable);
    vendInvoiceInfoLine_Project_ds.allowDelete(_enable);
    vendInvoiceLineForeignTradeCategory_ds.allowDelete(_enable);
    purchTable_ds.allowDelete(_enable);
    purchLine_ds.allowDelete(_enable);

    vendInvoiceInfoLine_ds.allowCreate(_enable);

    menuItemButtonAdd.enabled(_enable && !isLastInvoiceAdvance);
    removeButton.enabled(_enable && !isLastInvoiceAdvance);
    if (projInterCompanyVendorInvoiceFeatureEnabled)
    {
        removeButton.enabled(!isVendInvoiceGeneratedFromCustProjInvoice);
    }

    distLedgerAccounts.enabled(!workflowCompleted && workflowLinesPartiallyEnabled);
    maintainMiscLineCharges.enabled(!workflowCompleted && !isLastInvoiceAdvance && workflowLinesPartiallyEnabled);
    tabButtonLineTaxTransSource.enabled(!workflowCompleted && workflowLinesPartiallyEnabled);
    this.enableMatchingDetailsButtons();

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        if (vendInvoiceInfoLine.purchLine().ProjId)
        {
            psaVendPaymentRetentionDetails.visible(true);
        }
        else
        {
            psaVendPaymentRetentionDetails.visible(false);
        }
    }
}

