/// <summary>
/// Enables the matching details buttons on the Overview and Lines tab if the button is already visible,
/// "Use invoice matching" is enabled in setup and there is at least one invoice line (and invoice) available; otherwise, disable the buttons.
/// </summary>
public void enableMatchingDetailsButtons()
{
    boolean isWorkflowInReview = vendInvoiceInfoTable.isDocumentBeingProcessedInWorklow();

    // If there are any lines, that implies there is at least one invoice
    boolean enable = (purchEditLinesForm_Invoice.matchingDetailsButton() &&
        useInvoiceMatching &&
        !isLastInvoiceAdvance &&
        (vendInvoiceInfoLine_ds.numberOfRowsLoaded() != 0) &&
        !isInAutomatedProcessing);

    //Enables the Matching details button based on enable value, workflow state and user ownership.
    buttonParmTableMatchingDetails.enabled(enable && (!workflowCompleted) && (workflowPartiallyEnabled || isWorkflowInReview));
    parmLineMatchingDetails.enabled(enable && (!workflowCompleted) && (workflowLinesPartiallyEnabled || isWorkflowInReview));
    vendInvoicePolicyViolationTable.enabled(enable);
}

/// <summary>
/// Enables the payment format button.
/// </summary>
/// <remarks>
/// Enables the button if the <c>VendPaymModeTable</c> record has <c>ExportOnInvoice</c> set to true.
/// </remarks>
public void enablePaymentFormatButton()
{
    boolean doEnable = false;
    VendPaymModeTable vendPaymModeTable;

    if (vendInvoiceInfoTable.PaymMode != '')
    {
        vendPaymModeTable = VendPaymModeTable::find(vendInvoiceInfoTable.PaymMode);

        doEnable = vendPaymModeTable.ExportOnInvoice;
    }

    buttonPaymentFormat.enabled(doEnable);
}

/// <summary>
/// Configures the state of the post button on the form
/// </summary>
public void configurePostButtonState()
{
    if (VendInvoicePostButtonDisableV2Flight::instance().isEnabled())
    {
        InterCompanyEndpointActionPolicy interCompanyEndpointActionPolicy  = vendInvoiceInfoTable.purchTable().interCompanyEndPointActionPolicy();

        ok.enabled(!purchFormLetter.proforma() && vendInvoiceInfoTable_ds.numberOfRowsLoaded() > 0 && !isInAutomatedProcessing &&
            ((interCompanyEndpointActionPolicy && interCompanyEndpointActionPolicy.BypassVendorInvoiceWorkflowForIntercompanyVendorInvoices)
            || !Workflow::findWorkflowConfigurationToActivate(vendInvoiceInfoTable, workflowTypeStr(VendProcessInvoice))
            || (vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Approved)));
    }
    else
    {
        ok.enabled(!purchFormLetter.proforma() && vendInvoiceInfoTable_ds.numberOfRowsLoaded() > 0 && !isInAutomatedProcessing);
    }
}

public void enablePerformBudgetCheckButtons(boolean _includeHeaderButtons)
{
    boolean enableButtons = !isLastInvoiceAdvance && purchEditLinesForm_Invoice.enablePerformBudgetCheckButtons(vendInvoiceInfoTable) && !isInAutomatedProcessing;

    if (_includeHeaderButtons)
    {
        buttonHeader_PerformBudgetCheck.enabled(enableButtons);
    }

    stripButtonLine_PerformBudgetCheck.enabled(enableButtons);

    // Moved code from the VendInvoiceInfoLine.active() method -- to avoid duplicating the data sources node in the higher layer.
    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        if (vendInvoiceInfoLine.psaIsFinal)
        {
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleaseAmount)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleasePercent)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainageAmount)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainagePercent)).allowEdit(false);
        }
        else
        {
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleaseAmount)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleasePercent)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainageAmount)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainagePercent)).allowEdit(true);
        }
    }
}

void enablePurchasesButtons()
{
    buttonSubTableMiscCharges.enabled(!isLastInvoiceAdvance && VendInvoiceInfoSubTable::exist(
                                        vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId, vendInvoiceInfoTable.PurchId) && (vendInvoiceInfoSubTable_ds.numberOfRowsLoaded() > 1) );

    buttonSubTableAllocate.enabled(!isLastInvoiceAdvance && purchEditLinesForm_Invoice.checkInvoiceMiscCharges(vendInvoiceInfoSubTable) &&
                                    (vendInvoiceInfoSubTable_ds.numberOfRowsLoaded() > 1) );
}

/// <summary>
/// Enables the Purchase Order dropdown as long as the form has no <c>VendInvoiceInfoLine</c> lines
/// </summary>
public void enablePurchaseOrderLookups()
{
    if (vendInvoiceInfoTable.existLine())
    {
        purchParmTable_PurchId.allowEdit(false);
        purchParmTable_PurchId2.allowEdit(false);
        invoiceDetails_PurchId.allowEdit(false);
    }
    else
    {
        purchParmTable_PurchId.allowEdit(vendInvoiceInfoTable.PurchId == '');
        purchParmTable_PurchId2.allowEdit(vendInvoiceInfoTable.PurchId == '');
        invoiceDetails_PurchId.allowEdit(vendInvoiceInfoTable.PurchId == '');
    }
}

/// <summary>
/// Enables the InvoiceAccount control
/// </summary>
/// <remarks>
/// Disables the control if there are <c>VendInvoiceInfoLine</c> records present
/// </remarks>
public void enableInvoiceAccountLookup()
{
    boolean hasLines = vendInvoiceInfoTable.existLine();
    boolean isNonPO = vendInvoiceInfoTable.isNonPO();
    boolean isDirectInvoice = vendInvoiceInfoTable.isDirectInvoice();

    boolean doEnable = isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) ? !hasLines && isNonPO && !isDirectInvoice
                                                                                    : !hasLines && isNonPO;

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, InvoiceAccount)).allowEdit(doEnable);
}

/// <summary>
/// Enables the PurchAgreementHeader_PSN control
/// </summary>
/// <remarks>
/// Disables the control if there are <c>VendInvoiceInfoLine</c> records present
/// </remarks>
internal void enablePurchAgreementHeader_PSNLookup()
{
    if (VendInvoiceEditPurchaseAgreementFromPendingVendorInvoiceFlight::instance().isEnabled())
    {
        boolean hasLines = vendInvoiceInfoTable.existLine();
        boolean isNonPO = vendInvoiceInfoTable.isNonPO();
        boolean isDirectInvoice = vendInvoiceInfoTable.isDirectInvoice();

        boolean doEnable = !hasLines && isNonPO && !isDirectInvoice;

        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PurchAgreementHeader_PSN)).allowEdit(doEnable);
    }
    else if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PurchAgreementHeader_PSN)).allowEdit(!vendInvoiceInfoTable.PurchAgreementHeader_PSN);
    }
}

/// <summary>
/// Enables the retrievePOButton.
/// </summary>
/// <remarks>
/// Disables the button if an InvoiceAccount is not defined; otherwise enables the RetrievePO button.
/// Disables the button if Letter of Credit is marked for the <c>PurchTable</c>.
/// Disables the button if workflow is already approved; otherwise enables the RetrievePO button.
/// </remarks>
public void enableRetrievePOButton()
{
    PurchTable purchTableLocal;
    boolean doEnable = !isLastInvoiceAdvance && vendInvoiceInfoTable.InvoiceAccount != '' && vendInvoiceInfoTable.CurrencyCode != '' && vendInvoiceInfoTable.RequestStatus != VendInvoiceRequestStatus::Approved && !isInAutomatedProcessing;

    if (doEnable && BankLCImportFeatureChecker::checkBankLCImportEnabled())
    {
        purchTableLocal = vendInvoiceInfoTable.purchTable();
        if (purchTableLocal.PurchId)
        {
            if (purchTableLocal.isMarkedForBankLC())
            {
                doEnable = false;
            }
        }
    }

    // <GEEPL>
    doEnable = doEnable && purchEditLinesForm_Invoice.editButtonRetrievePO();
    // </GEEPL>

    buttonRetrievePO.enabled(doEnable);
}

public void enableServiceFields_BR()
{
    InventTable inventTable;

    select firstonly ItemType from inventTable where inventTable.ItemId == vendInvoiceInfoLine.ItemId;

    boolean enableServiceCodeField = inventTable.ItemType == ItemType::Service;

    vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, TaxServiceCode_BR)).allowEdit(enableServiceCodeField);
}

/// <summary>
///     Sets the enabled status of the catch-weight fields in the <c>VendInvoiceInfoLine</c> datasource.
/// </summary>
/// <remarks>
///     Customization to enabled fields is located on outside the datasources to preserve compatibility with other industry solutions.
/// </remarks>
public void enableVendInvoiceInfoLineCWFields()
{
    boolean isCWItem = PdsGlobal::pdsIsCWItem(vendInvoiceInfoLine.ItemId);

    if (#PdsCatchWeightEnabled)
    {
        // remain before
        element.setCWQuantityFieldsEnabled(
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainBeforeInvent)),
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainBefore)),
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, PdsCWRemainBefore)),
                isCWItem);

        // remain after
        element.setCWQuantityFieldsEnabled(
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfterInvent)),
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfter)),
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, PdsCWRemainAfter)),
                isCWItem);

        // receive now
        element.setCWQuantityFieldsEnabled(
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, InventNow)),
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ReceiveNow)),
                vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, PdsCWReceiveNow)),
                isCWItem);
    }
}

public void focusBackFromMatchPackingSlips()
{
    int pos = vendInvoiceInfoLine_ds.getPosition();

    vendInvoiceInfoLine_ds.executeQuery();
    vendInvoiceInfoLine_ds.setPosition(pos);

    if (!vendParameters.editAutomaticHeaderMatching())
    {
        vendInvoiceInfoTable.resetWorkflowMatchStatusAfterMatchingPackingSlips();
        this.checkIfMatchedAndRefresh();
    }
    else
    {
        vendInvoiceInfoTable_ds.reread();
        vendInvoiceInfoTable_ds.refresh();
    }

    VendInvoiceAutomationMatchingLogger matchingLogger =  new VendInvoiceAutomationMatchingLogger();
    matchingLogger.writeInvoiceAutomationMatchStatusManually(vendInvoiceInfoTable.RecId);

    vendInvoiceInfoLine_ds.reread();
    vendInvoiceInfoLine_ds.refresh();
    vendInvoiceInfoLine_ds.active();
}

private boolean formControlIsDataBound(Object _formControl)
{
    FormControlType formControlType = SysFormRun::controlType(classIdGet(_formControl));
    boolean ret = false;

    if (formControlType == FormControlType::CheckBox
    || formControlType == FormControlType::ComboBox
    || formControlType == FormControlType::ListBox
    || formControlType == FormControlType::RadioButton
    || formControlType == FormControlType::String
    || formControlType == FormControlType::StaticText
    || formControlType == FormControlType::Date
    || formControlType == FormControlType::Integer
    || formControlType == FormControlType::Real
    || formControlType == FormControlType::Time
    || formControlType == FormControlType::Image
    || formControlType == FormControlType::Grid
    || formControlType == FormControlType::TabPage)
    {
        ret = true;
    }

    return ret;
}

public FormRun formRun()
{
    //Required by the SysOperation framework.
    return this;
}

