    // </GIN>
    // </GEERU>
}

/// <summary>
/// Gets an instance of the <c>PurchEditLinesForm_Invoice</c> class.
/// </summary>
/// <returns>
/// An instance of the <c>PurchEditLinesForm_Invoice</c> class.
/// </returns>
/// <remarks>
/// This method allows the called objects to read the existing workflow state from the <c>VendEditInvoice</c> since initializing the
/// <c>PurchEditLinesForm_Invoice</c> form and read the workflow state is expensive.
/// </remarks>
public PurchEditLinesForm_Invoice getPurchEditLinesForm_Invoice()
{
    return purchEditLinesForm_Invoice;
}

public VendInvoiceInfoTable getVendInvoiceInfoTable_RU()
{
    return vendInvoiceInfoTable;
}

void hideQualityManagement()
{
    purchParmLine_qualityOrderStatusDisplay.visible(false);
}

/// <summary>
/// Hides the system defined attachments button.
/// </summary>
protected void hideSystemDefinedAttachmentsButton()
{
    #SysSystemDefinedButtons

    FormCommandButtonControl attachButton = this.control(this.controlId(#SystemDefinedAttachButton)) as FormCommandButtonControl;
    if (attachButton)
    {
        attachButton.visible(false);
    }
}

/// <summary>
/// Sets the label on the header attachment button.
/// </summary>
/// <param name = "_fromCache">true if the attachment count should come from the cache; otherwise, false.</param>
protected void setHeaderAttachmentButtonLabel(boolean _fromCache = true)
{
    HeaderAttachments.text(custVendInvoiceAttachments.getAttachmentCountButtonLabel(vendInvoiceInfoTable, "@SYS114630", _fromCache));
}

/// <summary>
/// Sets the label on the line attachment button.
/// </summary>
/// <param name = "_fromCache">true if the attachment count should come from the cache; otherwise, false.</param>
protected void setLineAttachmentButtonLabel(boolean _fromCache = true)
{
    TabButtonAttachments.text(custVendInvoiceAttachments.getAttachmentCountButtonLabel(vendInvoiceInfoLine, "@SYS114630", _fromCache));
}

void init()
{
    ParmId parmId;
    SecurityRights sr = SecurityRights::construct();
    boolean hasMarkupTransLinePurchParmAccess;
    boolean hasAccountingDistributionAccess;
    int i;
    FormDataSource dataSource;
    boolean isFrenchRegulatoryEnabled = PublicSectorUtils::isFrenchRegulatoryEnabled();

    isBR = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]);
    isIN = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    isRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);
    isW = SysCountryRegionCode::isLegalEntityInCountryRegion(#VendInv_W);
    isGB = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoGB]);
    #EECountryRegionCodes
    isEU = SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU);
    isHU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoHU]);
    isMX = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoMX]);
    isUS = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoUS]);
    isBE = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBE]);
    QueryFilter queryFilter;
    QueryBuildDataSource qbds;

    currentCompany = curExt();

    // <GTH>
    isVendInvoiceInfoTableExtTHEnabled = VendInvoiceInfoTableExtensionTH::isExtensionEnabled();
    // </GTH>

    super();

    if (!element.args().caller())
    {
        throw error("@SYS22539");
    }

    purchFormLetter = element.args().caller();
    documentStatus = purchFormLetter.documentStatus();
    parmId = purchFormLetter.parmId();

    //check if one or more invoices were marked as already opened by another user
    if (purchFormLetter.isSetActiveInvoiceFlag(VendActiveInvoiceFlags::InvoiceInUse))
    {
        List invoicesInUseList = purchFormletter.getInvoicesInUseList();

        if (!invoicesInUseList || invoicesInUseList.empty())
        {
            warning("@AccountsPayable:VendInvoiceInUseByAnotherUser");
        }
        else
        {
            VendInvoiceInfoTable::showWarningInvoiceListInUse(invoicesInUseList);
        }
    }

    this.checkForPrepaymentApplicationFlag();
    this.checkForVendActiveInvoiceFlag(VendActiveInvoiceFlags::SalesTaxAdjustmentsProrated, "@AccountsPayable:SalesTaxAdjustmentsProrated");

    uneditableDataSourcesSet = new Set(Types::Integer);

    // Cache each of the form datasources' initial allowEdit property
    for (i = 1; i <= element.dataSourceCount(); i++)
    {
        dataSource = element.dataSource(i);

        if (!dataSource.allowEdit())
        {
            uneditableDataSourcesSet.add(i);
        }
    }

    if (purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(VendEditInvoice)
    // <GEERU>
    || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(VendEditInvoice_Facture_RU)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_Facture_RU)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_Proforma_Facture_RU)
    // </GEERU>
    || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_InvoiceDisplay)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_NewInvoiceDisplay)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_Invoice_Display)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_PrepayInvoice_Display)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_Invoice)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_Proforma_Invoice)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_Proforma_Invoice_Display)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_OneTimeVendInvoice)
        || purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(VendWorkspacePurchFormLetter_Invoice))
    {
        isSingleInvoiceHeader = true;
    }

    element.configurePostButtonState();

    tabPage1099.visible(isUS);

    companyLookup.visible(element.supportsGlobalFunctionality());

    previousNonPO = true;
    previousNonPOLine = true;

    purchEditLinesForm_Invoice = PurchEditLinesForm::construct(documentStatus);

    workflowIconObservableLink = new FormObservableLink();

    vendParameters = VendParameters::find();
    useInvoiceMatching = (vendParameters.editUseInvoiceMatching() == NoYes::Yes);

    ledgerParameters = LedgerParameters::find();

    tabButtonVendInvoicePolicyViolationLine.enabled(useInvoiceMatching);

    purchEditLinesForm_Invoice.purchFormLetter(purchFormLetter);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    searchParmLine = true;

    purchParmUpdate_ds.query().dataSourceTable(tableNum(PurchParmUpdate)).addRange(fieldNum(PurchParmUpdate, ParmId)).value(queryValue(parmId));
    vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).addRange(fieldNum(VendInvoiceInfoTable, ParmId)).value(queryValue(parmId));

    this.batch(purchFormLetter.canGoBatch());

    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    //Initialize the invoice group variable
    vendInvoiceGroupDefaultGroup = VendParameters::getDefaultInternalInvoiceGroup();

    purchFormLetter.parmSpecQtyIsSet(false);

    //initialize invoice group on the basis of its parameter settings.
    invoiceGrouping_VendInvoiceGroup2.fastTabSummary(vendParameters.VendInvoiceGroupingEnable);
    invoiceDetails_VendInvoiceGroup.fastTabSummary(invoiceGrouping_VendInvoiceGroup2.fastTabSummary());

    if (BankLCImportFeatureChecker::checkBankLCImportEnabled())
    {
        bankLCImportLineNumController = BankLCImportLineNumController::construct();
        purchEditLinesForm_Invoice.parmBankLCImportLineNumController(bankLCImportLineNumController);
    }
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BankLCImportLine)).visible(purchEditLinesForm_Invoice.bankLCImportLine());

    hasMarkupTransLinePurchParmAccess = sr.hasMenuItemAccess(SecurableType::MenuItemDisplay, menuitemDisplayStr(MarkupTrans_LinePurchParm));
    if (hasMarkupTransLinePurchParmAccess)
    {
        maintainMiscLineCharges.visible(true);
    }
    else
    {
        maintainMiscLineCharges.visible(false);
    }

    hasAccountingDistributionAccess = sr.hasMenuItemAccess(SecurableType::MenuItemDisplay, menuitemDisplayStr(AccountingDistVendEditInvHdr));
    if (hasAccountingDistributionAccess)
    {
        buttonDistributeLedgerAccounts.visible(true);
    }
    else
    {
        buttonDistributeLedgerAccounts.visible(false);
    }

    // <GTH>
    if (TaxThaiGovCertificationFeatureChecker::isAverageVATEnabled())
    {
        this.updateDesignForAverageVAT();
    }
    // </GTH>

    // Make the commitments fields mandatory and visible if the feature is enabled, otherwise hide them and make them non-mandatory.
    purchCommitmentLineLookup_PSN.visible(isFrenchRegulatoryEnabled);
    purchCommitmentLineLookup_PSN.mandatory(isFrenchRegulatoryEnabled);

    purchCommitmentLineLookup2_PSN.visible(isFrenchRegulatoryEnabled);
    purchCommitmentLineLookup2_PSN.mandatory(isFrenchRegulatoryEnabled);

    BudgetReservationLine_PSN.visible(PublicSectorUtils::isBudgetReservationEnabled());

    directiveTabPage_PSN.visible(isFrenchRegulatoryEnabled);

    purchCommitmentLineGroup_PSN.visible(isFrenchRegulatoryEnabled);

    VendInvoiceHoldHistory_PSN.visible(isFrenchRegulatoryEnabled);
    PaymentDelay_PSN.visible(isFrenchRegulatoryEnabled);

    if (isFrenchRegulatoryEnabled)
    {
        paymentInvoice_FixedDueDate.allowEdit(false);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        element.enableAgreementButtons(false, false, false);
    }

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        //For Direct taxes
        taxWithholdParameters = TaxWithholdParameters_IN::find();
        element.setTaxWithholdVisible_IN();

        //For Indirect taxes
        taxParameters = TaxParameters::find();

        element.setVisible_IN();
        element.updateDesign_IN();
    }
    // </GIN>
    // <GEEU>
    if (VendAdvanceInvoice::isDeactivated())
    {
        buttonApplyPrepayments.visible(false);
    }

    countryRegion_LTLV = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoLT, #isoLV]);
    if (countryRegion_LTLV)
    {
        docNumberingCode_LT.text(purchFormLetter.getPurchFormLetterContract().parmLtDocNumberingCode());
    }
    // </GEEU>
    // <GEEPL>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoPL ]))
    {
        overview_New.visible(purchEditLinesForm_Invoice.showOverviewNew_PL());
    }
    // </GEEPL>
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        vendInvoiceInfoTable_W_DS.object(fieldNum(VendInvoiceInfoTable_W, cfpsId_BR)).visible(BrazilParameters::isCFPSEnabled_BR());
        this.updateDesign_BR();
    }
    // </GBR>

    // Explicitly add and hide the VendInvoiceInfoTable.ParmId filter in order to keep it from defaulting on the form filter
    qbds = vendInvoiceInfoTable_ds.query().dataSourceTable(tablenum(VendInvoiceInfoTable));
    queryFilter = vendInvoiceInfoTable_ds.query().addQueryFilter(qbds, fieldStr(VendInvoiceInfoTable, ParmId));
    queryFilter.status(RangeStatus::Hidden);

    reverseCharge = ReverseCharge_W::newParameters(vendInvoiceInfoTable, vendInvoiceInfoLine);

    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        TaxDocumentLauncher.visible(true);
        SalesTax_TaxItemGroup.visible(false);
        SalesTax_TaxGroup.visible(false);
        GroupSalesTax.visible(false);
        PurchTable_TaxGroup.visible(false);
    }

    this.updateDesign_VendReccuringInvoice();

    // Needed to check validity of address
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressHeader_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddressLine_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    checkIfMatchedStatusObservableLink = new FormObservableLink();

    custVendInvoiceAttachments = CustVendInvoiceAttachments::construct();

    element.hideSystemDefinedAttachmentsButton();

    vendInvoiceInfoTable_W_DS.object(fieldNum(VendInvoiceInfoTable_W, VatDueDate_W)).visible(TaxParameters::isEasternEuropeOrVatDueDateFeatureEnabled());

    if (isTaxServiceForPurchaseEnabled)
    {
        VendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, OverrideSalesTax)).visible(true);
        VendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, NumberSequenceGroup)).enabled(false);
        VendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, OverrideSalesTax)).visible(true);
    }

    this.initializeVendInvoiceAutomationControls();
    projOpsIntegEnabled = ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled();
    element.updateProjectControls(projOpsIntegEnabled);

    projSubcontractActualsPostingFeatureEnabled = FeatureStateProvider::isFeatureEnabled(ProjEnableSubcontractActualsPostingFeature::instance());
    if (projSubcontractActualsPostingFeatureEnabled)
    {
        ProjParameters projParameters = ProjParameters::find();
        SubconVendorInvoice_SubcontractInvoice.checked(projParameters.SubcontractInvoice);
        this.refresh();
    }

    if (TaxWithholdingGlobalFeature::isCalcWHTInInvoiceEnabled())
    {
        TaxWithhold.visible(true);
    }

    boolean isTaxIntegrationAccrueUseTaxEnabled = TaxIntegrationUtils::isTaxIntegrationUseTaxAssessmentEnabled(TaxIntegrationBusinessProcess::Purchase);
    VendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, AccrueSalesTaxType)).visible(isTaxIntegrationAccrueUseTaxEnabled);
    VendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, VendorChargedSalesTax)).visible(isTaxIntegrationAccrueUseTaxEnabled);

    TabFinTagHeader.visible(isFinTagConfigurationSetForCurrent && PurchaseOrderFinTagFeature::isEnabled());
    TabFinTagLine.visible(isFinTagConfigurationSetForCurrent && PurchaseOrderFinTagFeature::isEnabled());
}

private void initializeVendInvoiceAutomationControls()
{
    boolean isVendAutomateVendorInvoicesFeatureEnabled = VendAutomateVendorInvoicesFeature::isEnabled();

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, ReceivedDate)).visible(isVendAutomateVendorInvoicesFeatureEnabled);
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, ReceivedDate)).allowEdit(isVendAutomateVendorInvoicesFeatureEnabled);

    VendInvoiceAutomationFailureControlContract automationFailureControlContract =
        VendInvoiceAutomationFailureControlContract::newFromAutomationFailureControls(
            AutomationFailuresDetailGroup,
            AutomationFailedStep,
            AutomationFailedStepResults);

    automationFailureControlManager =
        VendInvoiceAutomationFailureControlManager::newFormAutomationFailureControlContract(automationFailureControlContract);
}

public void initDesign_BE()
{
    InvoiceChange.visible(vendInvoiceInfoTable.isNonPO());
}

void initDesign_EEU()
{
    boolean isNonPO = vendInvoiceInfoTable.isNonPO();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        element.initDesign_RU();
    }

    officials_RU.visible(purchEditLinesForm_Invoice.showOfficials_RU());

    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, EmplAccount_RU)).visible(isNonPO);
    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, EmplAccount_RU)).enabled(PaymTerm::isEmplAccountUsed_RU(vendInvoiceInfoTable.Payment));
}

/// <summary>
/// Checks to see if the current document has a default attachment and adjusts the form controls appropriately.
/// </summary>
public void checkForDefaultAttachment()
{
    boolean showViewHideAttachment = (DocuRefImageAnnotatorController::findDefaultAttachmentEnabledDocuRef(vendInvoiceInfoTable.DataAreaId, vendInvoiceInfoTable.TableId, vendInvoiceInfoTable.RecId).RecId != 0);

    ViewHideAttachment.visible(showViewHideAttachment);
    ImageGroupControl.visible(showViewHideAttachment && !hideDefaultAttachment);
    DocuViewerContainer1.visible(showViewHideAttachment);

    element.adjustViewHideAttachmentText(showViewHideAttachment && !hideDefaultAttachment);
}

/// <summary>
/// Adjusts the button text of the ViewHideAttachments button.
/// </summary>
/// <param name = "_isAttachmentVisible">true if the attachment control is visible; otherwise, false.</param>
public void adjustViewHideAttachmentText(boolean _isAttachmentVisible)
{
    if (_isAttachmentVisible)
    {
        ViewHideAttachment.text("@AccountsPayable:HideAttachment");
    }
    else
    {
        ViewHideAttachment.text("@AccountsPayable:ViewAttachment");
    }
}

/// <summary>
/// Checks if the prepayment application pending <c>VendActiveInvoiceFlags</c> flag is set, and if so
/// the corresponding <c>SysInfoLogStr</c> message is added to the <c>Infolog</c> and the flag is reset.
/// </summary>
public void checkForPrepaymentApplicationFlag()
{
    this.checkForVendActiveInvoiceFlag(VendActiveInvoiceFlags::PrepaymentApplicationPending, "@AccountsPayable:PrepaymentApplicationPending");
    this.checkForVendActiveInvoiceFlag(VendActiveInvoiceFlags::PrepaymentApplicationMarked, "@AccountsPayable:MarkedVendorPrepayment");
}

/// <summary>
/// Checks if the specified <c>VendActiveInvoiceFlags</c> flag is set, and if so the specified
/// <c>SysInfoLogStr</c> message is added to the <c>Infolog</c> and the flag is reset.
/// </summary>
/// <param name = "_vendActiveInvoiceFlags">The <c>VendActiveInvoiceFlags</c> flag to check.</param>
/// <param name = "sysInfoLogStr">The <c>SysInfoLogStr</c> message to be written to the <c>Infolog</c>.</param>
private void checkForVendActiveInvoiceFlag(VendActiveInvoiceFlags _vendActiveInvoiceFlags, SysInfoLogStr _sysInfoLogStr)
{
    if (purchFormLetter.isSetActiveInvoiceFlag(_vendActiveInvoiceFlags))
    {
        checkFailed(_sysInfoLogStr);

        // Reset the flag
        purchFormLetter.setActiveInvoiceFlag(_vendActiveInvoiceFlags, false);
    }
}

void initDesign_HU()
{
    boolean isNonPO = vendInvoiceInfoTable.isNonPO();

    foreignTrade_HU_IntrastatFulfillmentDate_HU.visible(isNonPO);
}

/// <summary>
/// This method is used to change the visibility/editability of LT,LV specific fields.
/// </summary>
void initDesign_LT()
{
    docNumberingCode_LT.visible(purchEditLinesForm_Invoice.showDocNumberingCode_LT(purchFormLetter.parmId()));
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, Num)).allowEdit(!vendInvoiceInfoTable_W.DocAutoNumbering_LT);
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, Description)).allowEdit(!vendInvoiceInfoTable_W.DocAutoNumbering_LT);
    this.allowEditPackingSlips_W();
}

#define.twoColumns(2)
void initDesign_RU()
{
    boolean isNonPO = vendInvoiceInfoTable.isNonPO();

    purchParmTable_TransDate_W.label(purchEditLinesForm_Invoice.dateCaption());

    if (documentStatus == DocumentStatus::Facture_RU)
    {
        ok.text("@GLS104543");
        element.design().caption(purchEditLinesForm_Invoice.editingCaption());
    }

    officials_RU.visible(purchEditLinesForm_Invoice.showOfficials_RU());
    extra_RU.visible(purchEditLinesForm_Invoice.showExtra_RU());

    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, FactureExternalId_RU)).visible(purchEditLinesForm_Invoice.showFactureId_RU());
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, ProcessVAT)).visible(purchEditLinesForm_Invoice.showProcessVAT());
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, PurchBookVATProcessParametersRecId)).visible(purchEditLinesForm_Invoice.showProcessVAT());

    vatOnPayment_RU.visible(purchEditLinesForm_Invoice.showVATOnPayment_RU());
    correctedLine_RU.visible(vendInvoiceInfoTable_RU.Correct_RU);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, Correct_RU)).visible(purchEditLinesForm_Invoice.showCorrect_RU());
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedInvoiceId_RU)).visible(purchEditLinesForm_Invoice.showCorrectedInvoiceId_RU());
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedInvoiceDate_RU)).visible(purchEditLinesForm_Invoice.showCorrectedInvoiceId_RU());

    vendInvoiceInfoTableCorrectionType_RU_ds.object(fieldNum(VendInvoiceInfoTableCorrectionType_RU, CorrectionType)).visible(purchEditLinesForm_Invoice.showCorrectedInvoiceId_RU());

    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedFactureExternalId_RU)).visible(purchEditLinesForm_Invoice.showCorrectedFactureExternalId_RU());
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedFactureDate_RU)).visible(purchEditLinesForm_Invoice.showCorrectedFactureExternalId_RU());
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedPeriod_RU)).visible(false);

    lastRevisionNum_RU.visible(purchEditLinesForm_Invoice.showCorrectedFactureExternalId_RU());
    lastRevisionDate_RU.visible(purchEditLinesForm_Invoice.showCorrectedFactureExternalId_RU());

    purchParmTable_PostingProfile_RU.visible(purchParmUpdate.SplitPostingProfile_RU);
    purchParmTable_InventProfileType_RU.visible(purchParmUpdate.SplitInventProfileType_RU);

    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, ConsignorAccount_RU)).visible(isNonPO);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, ConsigneeAccount_RU)).visible(isNonPO);
    agreement_RU.visible(isNonPO);
    lineAgreement_RU.visible(isNonPO);

    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, vatOperationCode_RU)).visible(isNonPO);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, vatChargeSource_RU)).visible(isNonPO);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, InventProfileId_RU)).visible(isNonPO);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, InventProfileType_RU)).visible(purchEditLinesForm_Invoice.showInventProfileType_RU());
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, InventProfileType_RU)).enabled(isNonPO);
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PostingProfile)).visible(purchEditLinesForm_Invoice.showPostingProfile_RU());
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PostingProfile)).enabled(isNonPO && ! vendInvoiceInfoTable_W.EmplAccount_RU);
    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, EmplAccount_RU)).visible(isNonPO);
    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, EmplAccount_RU)).enabled(PaymTerm::isEmplAccountUsed_RU(vendInvoiceInfoTable.Payment));
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, ProcessVAT)).enabled(vendInvoiceInfoTable_RU.vatOnPayment_RU == NoYes::No);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, PurchBookVATProcessParametersRecId)).enabled(vendInvoiceInfoTable_RU.vatOnPayment_RU == NoYes::No);
    vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, AlcoholLicenseSeriesNum_RU)).visible(purchEditLinesForm_Invoice.showAlcoholLicenseSeriesNum_RU());

    tabPageFixedAsset.columns(#twoColumns);
}

