/// <summary>
/// Initializes values on the datasource for the <c>VendInvoiceInfoTable</c> table.
/// </summary>
protected void invoiceAccountModifiedInitVendInvoiceInfoTable()
{
    vendInvoiceInfoTable.Ordering = documentStatus;
    vendInvoiceInfoTable.resetFieldStateForAllFields();
    vendInvoiceInfoTable.ParmId = purchParmUpdate.ParmId;
    vendInvoiceInfoTable.defaultRow(purchTable, element.purchFormLetter().parmLedgerJournalTrans(), true);
}

public void invoiceAccountModified()
{
    #ISOCountryRegionCodes;

    // If the user is modifying the InvoiceAccount, this is a non-PO invoice
    PurchTable emptyPurchTable = null;
    purchTable.data(emptyPurchTable);

    element.invoiceAccountModifiedInitVendInvoiceInfoTable();

    element.updateVendorChargedSalesTaxControl();

    //This is needed to correctly refresh ReferenceGroupFields
    vendInvoiceInfoTable_ds.rereadReferenceDataSources();

    vendInvoiceInfoTable_ds.paymentIdValidated(true, vendInvoiceInfoTable, false);
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, SettleVoucher)).allowEdit(false);

    buttonPaymentSched.enabled(vendInvoiceInfoTable.PaymentSched != '');
    element.enablePaymentInvoiceFixedDueDate();

    element.setHeaderFields();

    DimensionEntryControlHeader.reactivate();

    element.enableHeaderDeliveryAddressFields();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        if (!vendInvoiceInfoTable.hasToIssueFiscalDocument_BR())
        {
            vendInvoiceInfoTable.Num = '';
            vendInvoiceInfoTable_W.AccessKey_BR = '';
        }

        element.prepareIssuingScenario_BR();
    }
    // </GBR>

    // Default the LE currency and disable the Currency field in France.
    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        if (!vendInvoiceInfoTable.Payment)
        {
            PaymTerm paymTerm = PaymTerm::getDefaultPaymTerm();

            if (paymTerm.RecId)
            {
                vendInvoiceInfoTable.Payment = paymTerm.PaymTermId;
            }
        }

        vendInvoiceInfoTable.CurrencyCode = CompanyInfoHelper::standardCurrency();
        groupCurrency_CurrencyCode.enabled(false);
        vendInvoiceInfoTable_ds.refresh();
    }
}

protected void updateVendorChargedSalesTaxControl()
{
    boolean shouldAccruedSalesTaxAmountAllowEdit = TaxIntegrationUtils::isTaxIntegrationUseTaxAssessmentEnabled(TaxIntegrationBusinessProcess::Purchase)
                                                    && (vendInvoiceInfoTable.AccrueSalesTaxType != TaxAccrueSalesTaxType::Default)
                                                    && (vendInvoiceInfoTable.ParmJobStatus != ParmJobStatus::Executed)
                                                    && !(vendInvoiceInfoTable.ImportedAmount || vendInvoiceInfoTable.ImportedSalesTax);

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, VendorChargedSalesTax)).allowEdit(shouldAccruedSalesTaxAmountAllowEdit);
    VendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, AccrueSalesTaxType)).allowEdit(vendInvoiceInfoTable.ParmJobStatus != ParmJobStatus::Executed);
}

public boolean isNeeded()
{
    return TaxParameters::checkTaxParameters_IN() || TaxWithholdParameters_IN::checkTaxParameters();
}

