public void markupTransFormClosed()
{
    // Update budget check result for changes from edit
    vendInvoiceInfoTable_ds.updateBudgetCheckResult(true);
    element.checkIfMatchedAndRefresh();

    if (VendInvoiceInfoSubTable::count(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId) > 1)
    {
        buttonPageParmTableAllocate.enabled(purchEditLinesForm_Invoice.checkInvoiceMiscCharges(vendInvoiceInfoTable) && workflowPartiallyEnabled);
    }
    else
    {
        buttonPageParmTableAllocate.enabled(purchEditLinesForm_Invoice.checkInvoiceMiscCharges(VendInvoiceInfoSubTable::find(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId, vendInvoiceInfoTable.PurchId)) && workflowPartiallyEnabled);
    }

    element.enablePurchasesButtons();
}

