private void openExcelTemplate()
{
    // Variables used for opening Excel template
    DocuTemplateName templateName = resourceStr(VendInvoiceLineEntityTemplate);
    DocuTemplate template = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, templateName);

    // Ensure the template was present
    if (template && template.TemplateID == templateName)
    {
        Map filtersToApply = this.createFiltersForExcelVendorInvoiceEntity(vendInvoiceInfoTable);

        // Generate the workbook using the template and filters
        DocuTemplateRender renderer = new DocuTemplateRender();
        str documentUrl = renderer.renderTemplateToStorage(template, filtersToApply, OfficeTrimmable::construct());

        // Pass the workbook to the user
        if (documentUrl)
        {
            Browser b = new Browser();
            b.navigate(documentUrl, false, false);
        }
        else
        {
            error(strFmt("@ApplicationFoundation:DocuTemplateGenerationFailed", templateName));
        }
    }
    else
    {
        warning(strFmt("@ApplicationFoundation:DocuTemplateNotFound", templateName));
    }
}

private Map createFiltersForExcelVendorInvoiceEntity(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    str lineEntityName = tableStr(VendorInvoiceLineEntity);
    str lineEntityTableRefId = fieldStr(VendorInvoiceLineEntity, HeaderReference);
    str lineEntityDataAreaId = fieldStr(VendorInvoiceLineEntity, dataAreaId);
    str headerEntityName = tableStr(VendorInvoiceHeaderEntity);
    str headerEntityTableRefId = fieldStr(VendorInvoiceHeaderEntity, HeaderReference);
    str headerEntityDataAreaId = fieldStr(VendorInvoiceHeaderEntity, dataAreaId);

    Map filtersToApply = new Map(Types::String, Types::Class);

    // Create lines filter
    ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(lineEntityName);

    // Create header filter
    ExportToExcelFilterTreeBuilder headerFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);

    var filter = filterBuilder.and(
        headerFilterBuilder.areEqual(headerEntityTableRefId, _vendInvoiceInfoTable.TableRefId),
        headerFilterBuilder.areEqual(headerEntityDataAreaId, _vendInvoiceInfoTable.DataAreaId));

    filtersToApply.insert(headerEntityName, filter);

    return filtersToApply;
}

public LogisticsLocationRecId parmPostalAddressLocation(LogisticsLocationRecId _postalAddressLocation = 0)
{
    if (!_postalAddressLocation)
    {
        // Set location by active datasource
        if (element.dataSource().name() == vendInvoiceInfoLine_DS.name())
        {
            _postalAddressLocation = vendInvoiceInfoLine.DeliveryPostalAddress;
        }
        else
        {
            _postalAddressLocation = vendInvoiceInfoTable.DeliveryPostalAddress;
        }
    }

    postalAddressLocation = _postalAddressLocation;
    return postalAddressLocation;
}

void postInvoice()
{
    if (element.canProceedWithInvoicePosting())
    {
        const int Post = 263;

        if (purchFormLetter.validateMatchStatus())
        {
            MenuItemNameDisplay callerMenuItem = purchFormLetter.parmCallerMenuItem();
            isInvoiceSaved = true;
            isInvoicePosting = true;

            element.updateDocFromProforma(callerMenuItem);
            element.task(Post);

            vendInvoiceInfoTable.reread();
            if (vendInvoiceInfoTable.ParmId != '')
            {
                element.createBREForPostedPOInvoice(vendInvoiceInfoTable);
            }

            if (FeatureStateProvider::isFeatureEnabled(VendInvoicePostingDateAutoAdjustmentFeature::instance())
                && isInvoicePosting
                && !validationErrorsExist)
            {
                vendInvoiceInfoTable.reread();
                vendInvoiceInfoTable.updatePostingDateV2();
                purchFormLetter.parmCallerTable(vendInvoiceInfoTable);
            }

            isInvoicePosting = false;

            // Reset the invoice saved flag just in case if the posting fails.
            isInvoiceSaved = false;
        }
    }
}

internal void updateApprovedButtonState()
{
    if(ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled() &&
            Dynamics.AX.Application.FeatureStateProvider::IsFeatureEnabled(ProjEnableSubcontractActualsPostingFeature::instance()) &&
            Dynamics.AX.Application.FeatureStateProvider::IsFeatureEnabled(ProjHoldVendorPaymentFeature::instance()) &&
            vendInvoiceInfoTable.checkIfProjExistsInLines() &&
            vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).getValue() == NoYes::Yes)
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, Approved)).setValue(NoYes::No);
    }
}

