public void prepareIssuingScenario_BR()
{
    boolean isChronologicalControlEnabled = BrazilParameters::find().ChronologicalInvoiceNumbering == NoYes::Yes;
    boolean isIssuingScenario = vendInvoiceInfoTable.IsTheFiscalDocumentIssuer_BR();

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, Num)).allowEdit(!isIssuingScenario);
    fiscalDocumentSeries_BR.allowEdit(!isIssuingScenario);
    CopyOfFiscalDocumentSeries_BR.allowEdit(!isIssuingScenario);
    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentSeries_BR)).allowEdit(!isIssuingScenario);
    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentModel_BR)).allowEdit(!isIssuingScenario);
    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, FiscalDocumentSpecie_BR)).allowEdit(!isIssuingScenario);
    vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, AccessKey_BR)).allowEdit(!isIssuingScenario);

    if (isIssuingScenario)
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, TransDate)).allowEdit(!isChronologicalControlEnabled);
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, DocumentDate)).allowEdit(!isChronologicalControlEnabled);
    }
    else
    {
        element.enableEFDocument_BR(FiscalDocModel_BR::find(vendInvoiceInfoTable_W.FiscalDocumentModel_BR).IsForNFeFederal);
    }

    element.allowEditFreightNature_BR();
    element.allowEditTypeOfCTe_BR();

    element.disableFieldsForReversingFiscalDoc_BR();

    element.enableXmlMatchbuttons_BR();

    vendInvoiceInfoTable_ds.refresh();
}

void printInvoice()
{
    purchFormLetter.proforma(true);
    element.postInvoice();
}

PurchFormLetter purchFormLetter()
{
    return purchFormLetter;
}

boolean reArrange(boolean _clearTotals)
{
    boolean ret = true;

    purchParmUpdate_ds.write();

    purchFormLetter.initParameters(purchParmUpdate.data());

    purchFormLetter.editLinesChanged(editLinesChanged);
    purchFormLetter.reArrangeNow(reArrangeNow);

    purchFormLetter.validateAll(false);

    ret = purchFormLetter.reArrange(_clearTotals);
    reArrangeNow = !ret;

    purchFormLetter.validateAll(true);

    if (ret)
    {
        vendInvoiceInfoTable_ds.executeQuery();
    }
    else
    {
        vendInvoiceInfoTable_ds.research();
    }

    purchFormLetter.reArrangeNow(reArrangeNow);

    return ret;
}

void reArrangeNow()
{
    reArrangeNow = true;
    purchFormLetter.reArrangeNow(reArrangeNow);
}

/// <summary>
/// Refreshes the VendInvoiceInfoTable and VendInvoiceInfoLine table data sources.
/// </summary>
/// <remarks>
/// Called by child forms when closing due to possibly changed data contained in these two tables.
/// </remarks>
public void refresh()
{
    int pos;

    // Reread the header which will reread the lines. Retain the current row selected on both the header and lines
    pos = vendInvoiceInfoLine_ds.getPosition();
    vendInvoiceInfoTable_ds.research(true);
    vendInvoiceInfoLine_ds.setPosition(pos);
}

public void refreshAfterWorkflowSubmit()
{
    vendInvoiceInfoTable_ds.research(true);
    vendInvoiceInfoTable_ds.active();

    if (!isSingleInvoiceHeader)
    {
        element.setFormDesign(VendInvoiceDesignMode::MultipleInvoice);
    }
}

/// <summary>
/// Removes the Purchase Agreement link from Vendor Invoice
/// </summary>
public void removeAgreementLink()
{
    VendInvoiceInfoLine origVendInvoiceInfoLine;

    if (Box::okCancel("@SYS306910", DialogButton::Cancel, "@SYS67038", "@SYS4002120"))
    {
        origVendInvoiceInfoLine = vendInvoiceInfoLine.data().orig();

        PriceDiscPolicyRule::createInstance(purchLine).launchPolicyDialogForAllPolicyFields(false, true);

        ttsbegin;

        vendInvoiceInfoLine.selectForUpdate(true);
        vendInvoiceInfoLine.AgreementLine_PSN = 0;
        vendInvoiceInfoLine.update();

        ttscommit;

        vendInvoiceInfoLine_ds.executeQuery();
        element.args().lookupRecord(origVendInvoiceInfoLine);
        vendInvoiceInfoLine_ds.research(false);
        element.enableAgreementButtons(false, true, false);
        info("@SYS4002121");
    }
    else
    {
        throw warning("@SYS4002122");
    }
}

void reSelect()
{
    purchFormLetter.reSelect(purchParmUpdate.data());
    element.chooseTable();
}

/// <summary>
/// Resets the vendor invoice lines' freight nature to the default 'Blank' value.
/// </summary>
private void resetLinesFreightNature_BR()
{
    VendInvoiceInfoLine_W line_w;
    VendInvoiceInfoLine line;

    ttsbegin;

    while select RecId from line
        where line.ParmId == vendInvoiceInfoTable.ParmId &&
              line.TableRefId == vendInvoiceInfoTable.TableRefId
    join forupdate line_w
        where line_w.VendInvoiceInfoLine == line.RecId
    {
        line_w.FreightNature_BR = FreightNature_BR::Blank;
        line_w.update();
    }

    ttscommit;
}

