void run()
{
    PurchUpdate recommendedQty;
    SysDictEnum salesUpdateEnum = new SysDictEnum(enumNum(PurchUpdate));
    BoxFormOnce box;
    boolean isInvoiceGroupEnabled = this.isInvoiceGroupEnabled();
    boolean isMatchControlVisible = this.isMatchControlVisible();
    boolean isPriceMatchVisible = this.isPriceMatchVisible();
    boolean isExtendedPriceMatchVisible = this.isExtendedPriceMatchVisible();

    super();

    purchParmUpdate.SumBy = purchEditLinesForm_Invoice.sumBy();
    purchParmUpdate.SumIncludePending = purchEditLinesForm_Invoice.sumIncludePending();
    element.sumByChanged(true);

    if (VendInvoiceInfoTable.InvoiceType == PurchInvoiceType::VendorAdvance)
    {
        DimensionEntryControlLine.allowEdit(false);
    }

    if (purchParmUpdate.ParmId)
    {
        purchFormLetter.setPurchEditLinesDataSource(vendInvoiceInfoTable_ds);

        vendAccountInQuery = (purchFormLetter.vendAccountInQuery());
        invoiceAccountInQuery = (purchFormLetter.invoiceAccountInQuery());

        if (purchParmUpdate.SumBy == AccountOrder::Order && purchFormLetter.sumNum())
        {
            initSumNum = purchFormLetter.sumNum();
        }

        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumNum)).allowEdit(purchEditLinesForm_Invoice.sumNumAllowEdit());
        purchParmUpdate_ds.object(fieldNum(PurchParmUpdate, SumBy)).allowEdit(purchEditLinesForm_Invoice.sumByAllowEdit());

        buttonpaymentSched.enabled(false);
        buttonPaymentFormat.enabled(false);

        if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
        {
            // we also want to disable the button if the commitments feature is enabled.
            buttonSelectJournal.enabled(purchParmUpdate.SpecQty == PurchUpdate::PackingSlip && !PublicSectorUtils::isFrenchRegulatoryEnabled());
        }
        else
        {
            // <GEEU>
            if (countryRegion_LTLV)
            {
                buttonSelectJournal.enabled(purchParmUpdate.SpecQty == PurchUpdate::PackingSlip && !vendInvoiceInfoTable_W.PackingSlipIdForUpdate_W);
            }
            else
            {
                // </GEEU>
                buttonSelectJournal.enabled (purchParmUpdate.SpecQty == PurchUpdate::PackingSlip);
                // <GEEU>
            }
            // </GEEU>
        }
        buttonParmTableMatchingDetails.visible(purchEditLinesForm_Invoice.matchingDetailsButton());

        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            this.allowEditTypeOfCTe_BR();
            this.allowEditFreightNature_BR();
        }
        // </GBR>

        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, Closed)).visible(purchEditLinesForm_Invoice.close());

        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfter)).allowEdit(purchEditLinesForm_Invoice.editRemainAfter());
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, LineAmount)).allowEdit(purchEditLinesForm_Invoice.editPrices());
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ReceiveNow)).allowEdit(purchEditLinesForm_Invoice.editReceiveNow());
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, InventNow)).allowEdit(purchEditLinesForm_Invoice.editReceiveNow());
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfterInvent)).allowEdit(purchEditLinesForm_Invoice.editReceiveNow());
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, PurchPrice)).allowEdit(purchEditLinesForm_Invoice.editPrices());
        vendInvoiceInfoLine_ds.allowDelete(purchEditLinesForm_Invoice.editReceiveNow());

        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, Num)).allowEdit(purchEditLinesForm_Invoice.numAllowEdit());
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, TransDate)).allowEdit(purchEditLinesForm_Invoice.transDateAllowEdit());
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, Approved)).allowEdit(purchEditLinesForm_Invoice.approvedAllowEdit());
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, Approver)).allowEdit(purchEditLinesForm_Invoice.approvedByAllowEdit());

        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PaymMode)).allowEdit(purchEditLinesForm_Invoice.paymModeAllowEdit());
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PaymSpec)).allowEdit(purchEditLinesForm_Invoice.paymSpecAllowEdit());
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, PaymId)).allowEdit(purchEditLinesForm_Invoice.paymentIdAllowEdit());
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, VendBankAccountID)).allowEdit(purchEditLinesForm_Invoice.vendBankAccountIdAllowEdit());

        buttonPrintFormletter.enabled(purchFormLetter.printFormLetter());
        buttonPrintFormletterCopy.enabled(purchFormLetter.printFormLetter());
        printEUVatButton.visible(purchEditLinesForm_Invoice.showPrintEUVatInvoice());

        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, FixedExchRate)).allowEdit(purchEditLinesForm_Invoice.fixedExchRate());

        quantityMatch.visible(isMatchControlVisible);
        priceMatch.visible(isPriceMatchVisible);
        extendedPriceMatch.visible(isExtendedPriceMatchVisible);
        extPriceMatch1.visible(isExtendedPriceMatchVisible);
        matchVariance.visible(isMatchControlVisible);
        invoiceDetails_MatchVariance.visible(isMatchControlVisible);
        gridParmTable_MatchVariance.visible(isMatchControlVisible);
        varianceApproved.visible(purchEditLinesForm_Invoice.varianceApproved());
        invoiceDetails_VarianceApproved.visible(purchEditLinesForm_Invoice.varianceApproved());
        gridParmTable_varianceApproved.visible(purchEditLinesForm_Invoice.varianceApproved());
        creditCorrection.visible(!isRU && ledgerParameters.TransactionReversalCorrection);
        creditCorrectionHeaderView.visible(!isRU && ledgerParameters.TransactionReversalCorrection);

        quantityMatch1.visible(isMatchControlVisible);
        priceMatch1.visible(isPriceMatchVisible);

        updateMatchStatus.visible(vendParameters.editUseInvoiceMatching() && !vendParameters.editAutomaticHeaderMatching());

        invoiceGrouping.visible(isInvoiceGroupEnabled);
        invoiceGrouping_VendInvoiceGroup2.visible(isInvoiceGroupEnabled);
        invoiceDetails_VendInvoiceGroup.visible(isInvoiceGroupEnabled);
        vendPaymentGroup.visible(vendParameters.VendPaymentGroupingEnable);

        groupCurrency_M_editExchRate.helpText(strFmt("@SYS78107", vendInvoiceInfoTable.ExchRate));

        // <GEEU>
        if (countryRegion_LTLV)
        {
            element.initDesign_LT();
        }
        // </GEEU>
        // <GEERU><GEEU>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
        {
            element.initDesign_EEU();
        }
        // </GEERU></GEEU>

        if (isHU)
        {
            element.initDesign_HU();
        }

        if (isBE)
        {
            element.initDesign_BE();
        }

        if (vendInvoiceInfoTable.InvoiceType != PurchInvoiceType::VendorAdvance && PurchParameters::find().PromptQty)
        {
            recommendedQty = purchFormLetter.recommendedSpecQty();
            if (recommendedQty != purchParmUpdate.SpecQty)
            {
                boxFormOnceIsActive = true;

                box = BoxFormOnce::construct();
                box.parmDialogBoxType(DialogBoxType::YesNoBox);
                box.parmDialogButton(DialogButton::Yes);
                box.parmTitle("@SYS59372");
                box.parmText(strFmt("@SYS102340", salesUpdateEnum.value2Name(enum2int(purchParmUpdate.SpecQty)), salesUpdateEnum.value2Name(enum2int(recommendedQty))));
                box.parmOwner(new SysDictClass(classIdGet(purchEditLinesForm_Invoice)).name());
                if (box.prompt() == DialogButton::Yes)
                {
                    purchParmUpdate.SpecQty = recommendedQty;
                    purchFormLetter.parmSpecQtyIsSet(true);
                    element.updateDocWithSpecQty();
                }

                boxFormOnceIsActive = false;
            }
        }

        buttonPaymentSched.enabled(vendInvoiceInfoTable.isNonPO() ? vendInvoiceInfoTable.PaymentSched != '' : vendInvoiceInfoTable.purchTable().PaymentSched != '');
    }

    // Do not allow lines to be manually created until PO or Vendor is set
    element.allowLineCreate();

    element.specQtyChanged();
    editLinesChanged = false;

    buttonPageParmTableAllocate.enabled(purchEditLinesForm_Invoice.checkInvoiceMiscCharges(vendInvoiceInfoSubTable) && workflowPartiallyEnabled);

    //
    // If there are multiple records selected from VendInvoiceInfoTable then the invoice form will have multiple invoices
    // or if the invoice is created from invoice pool then launch in grid view
    //
    if (vendInvoiceInfoTable_ds.numberOfRowsLoaded() > 1 || vendInvoiceInfoTable.Ordering == DocumentStatus::ApproveJournal)
    {
        isSingleInvoiceHeader = false;
    }

    // set form design view
    if (purchFormLetter.parmCallerTable().TableId == tableNum(VendTable))
    {
        purchEditLinesForm_Invoice.parmIsNewInvoiceFromVendor(true);
        isSingleInvoiceHeader = true;
    }

    if (isSingleInvoiceHeader)
    {
        element.setFormDesign(VendInvoiceDesignMode::SingleInvoiceHeader);
    }
    else
    {
        element.setFormDesign(VendInvoiceDesignMode::MultipleInvoice);
    }

    switch (purchFormLetter.parmCallerMenuItem())
    {
        case menuitemDisplayStr(VendEditInvoiceFromPurchaseOrder):
            chooseInvoice.clicked();
            break;

        case menuitemDisplayStr(VendEditInvoiceFromPackingSlip):
            buttonSelectJournal.clicked();
            break;

        case menuitemDisplayStr(PurchFormLetter_NewInvoiceDisplay):
        case menuitemDisplayStr(VendEditInvoice):
        case menuitemDisplayStr(PurchFormLetter_OneTimeVendInvoice):
        // <GEERU>
        case menuitemDisplayStr(VendEditInvoice_Facture_RU):
            // </GEERU>
            element.task(#taskNew);
            break;
    }

    //If this invoice has been approved by workflow, and the invoice is currently in edit mode, put the form in View mode
    if (vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Approved && !element.inViewMode())
    {
        element.task(#taskDoneEditRecord);
    }

    SpecQtyDropDialog.text("@AccountsPayable:DefaultFrom" + enum2Str(PurchParmUpdate.SpecQty));

    if (purchFormLetter.parmWasMatchStatusReset() && VendInvoiceInfoSubTable::existAnyPurchId(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId))
    {
        info(strFmt("@AccountsPayable:InvoiceMatchingNotPerformed", "@AccountsPayable:MatchStatus", "@AccountsPayable:VendInvoiceMatchingEmpty"));
    }

    if (VendInvoiceAutomationStateManagement::isInvoiceInUseByAutomationMatching(vendInvoiceInfoTable))
    {
        info(strFmt("@AccountsPayable:InvoiceIsInUseByAutomationMatchingMessage", vendInvoiceInfoTable.Num));
    }

    element.enableGroupAutomaticSubmitToWorkflowDetails();
    element.enableGroupAutomaticMatchingFailedDetails();
}

PurchFormLetter runBase()
{
    return purchFormLetter;
}

PurchFormLetter runBasePostPrint()
{
    return purchFormLetter;
}

/// <summary>
/// Saves the vendor invoices that are made or updated in the <c> VendEditInvoice </c> before closing.
/// </summary>
void saveInvoicesAndClose()
{
    #OCCRetryCount
    VendInvoiceInfoTable vendInvoiceInfoTableLocal;

    if (purchParmUpdate)
    {
        purchParmUpdate_ds.write();
        vendInvoiceInfoTable_ds.query().dataSourceTable(tableNum(VendInvoiceInfoTable)).addRange(fieldNum(VendInvoiceInfoTable, ParmId)).value(queryValue(purchParmUpdate.ParmId));
    }

    if (!isInvoiceSaved && !purchFormLetter.isEGAISPosting_RU())
    {
        if (vendInvoiceInfoTable)
        {
            for (vendInvoiceInfoTableLocal = vendInvoiceInfoTable_ds.getFirst(); vendInvoiceInfoTableLocal; vendInvoiceInfoTableLocal = vendInvoiceInfoTable_ds.getNext() )
            {
                try
                {
                    // Update the invoice using a separate table buffer in order to not force pending changes to be saved
                    VendInvoiceInfoTable vendInvoiceInfoTablePrevious = VendInvoiceInfoTable::findRecId(vendInvoiceInfoTableLocal.RecId);
                    isWorkflowActive = vendInvoiceInfoTablePrevious.RequestStatus != VendInvoiceRequestStatus::Draft;

                    if (!isWorkflowActive || vendInvoiceInfoTablePrevious.ParmJobStatus != ParmJobStatus::Executed)
                    {
                        VendInvoiceInfoTable::moveFromActiveToSaved(vendInvoiceInfoTablePrevious);
                        element.submitToSourceDocument(vendInvoiceInfoTablePrevious);
                    }
                }
                catch (Exception::UpdateConflict)
                {
                    if (xSession::currentRetryCount() >= #RetryNum)
                    {
                        throw Exception::UpdateConflict;
                    }
                    else
                    {
                        //
                        // Retry saving the invoice by re-reading the table buffer.
                        // We might get update conflicts while saving the invoice, if another process
                        // such as workflow modifies the state of the invoice after we read it.
                        //
                        retry;
                    }
                }
            }

            vendInvoiceInfoLine.reread(); // moveFromActiveToSaved has updated this record, reread to get current content.
        }

        isInvoiceSaved = true;
    }

    if (!isSingleInvoiceHeader && purchTable.isInterCompanyOrder() && isInvoicePosting)
    {
        for (vendInvoiceInfoTableLocal = vendInvoiceInfoTable_ds.getFirst(); vendInvoiceInfoTableLocal; vendInvoiceInfoTableLocal = vendInvoiceInfoTable_ds.getNext())
        {
            vendInvoiceInfoTableLocal.checkIfMatched(true);
        }
    }

    purchFormLetter.initParameters(purchParmUpdate.data());

    purchFormLetter.editLinesChanged(editLinesChanged);
    purchFormLetter.reArrangeNow(reArrangeNow);
    // <GEEU>
    if (countryRegion_LTLV)
    {
        purchFormLetter.getPurchFormLetterContract().parmLtDocNumberingCode(docNumberingCode_LT.text());
    }
    // </GEEU>

    purchFormLetter.parmSaveChanges(true);
}

/// <summary>
/// Saves and reloads the data for a new invoice or an invoice with no lines
/// </summary>
private void saveAndReloadNewOrNoLineInvoice()
{
    if (vendInvoiceInfoLine_ds.numberOfRowsLoaded() == 0)
    {
        vendInvoiceInfoTable_ds.write();
        vendInvoiceInfoTable_ds.research(true);
    }
}

/// <summary>
/// Saves and reloads the data for a invoice for purchase agreement.
/// </summary>
private void saveAndReloadForPurchaseAgreement()
{
    vendInvoiceInfoTable_ds.write();

    vendInvoiceInfoLine_ds.executeQuery();

    element.task(#taskSave);

    vendInvoiceInfoTable_ds.research(true);
}

/// <summary>
/// Sets the 1099G fields to be editable if G-2 is selected
/// </summary>
/// <remarks>
/// When G-2 is selected, the fields are set as editable, when it is not selected they are uneditable
/// </remarks>
void setAllowEdit1099GFields()
{
    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        boolean canEdit = (vendInvoiceInfoLine.Tax1099Fields == Tax1099Base::findTax1099FieldsRecId(Tax1099G::incomeTaxRefundsCreditsOffsets()));
        tax1099GFields.allowEdit(canEdit);

        canEdit = (vendInvoiceInfoLine.Tax1099Fields == Tax1099Base::findTax1099FieldsRecId(Tax1099G::unemploymentCompensation()));
        tax1099G1Fields.allowEdit(canEdit);
    }
}

/// <summary>
/// Sets the 1099G fields to be editable if G-2 is selected
/// </summary>
/// <remarks>
/// When S-2 is selected, the fields are set as editable, when it is not selected they are uneditable
/// </remarks>
void setAllowEdit1099SFields()
{
    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        boolean canEdit = (vendInvoiceInfoLine.Tax1099Fields == Tax1099Base::findTax1099FieldsRecId(Tax1099S::grossProceeds()));
        tax1099SFields.allowEdit(canEdit);
    }
}

/// <summary>
///     Sets the enabled status of a quantity combination of datasource field objects by catch-weight.
/// </summary>
/// <param name="_invDataObjectField">
///     The inventory quantity data object.
/// </param>
/// <param name="_untDataObjectField">
///     The quantity data object in transaction units.
/// </param>
/// <param name="_cwDataObjectField">
///     The catch-weight quantity data object.
/// </param>
/// <param name="_isCWItem">
///     A boolean value stating whether the item on the line is a catch-weight item.
/// </param>
public void setCWQuantityFieldsEnabled(
    FormDataObject _invDataObjectField,
    FormDataObject _untDataObjectField,
    FormDataObject _cwDataObjectField,
    boolean _isCWItem)
{
    boolean originallyEnabledInvField = _invDataObjectField.allowEdit();
    boolean originallyEnabledUntField = _untDataObjectField.allowEdit();

    // if the status originally was disabled, don't change it
    if (originallyEnabledInvField)
    {
        enableDatasourceFieldObject(_invDataObjectField, !_isCWItem);
    }
    if (originallyEnabledUntField)
    {
        enableDatasourceFieldObject(_untDataObjectField, !_isCWItem);
    }
    enableDatasourceFieldObject(_cwDataObjectField, ((originallyEnabledUntField || originallyEnabledInvField) && _isCWItem));
}

private void setIsICMSTTaxUpdatedFromXml_BR(boolean _updated)
{
    isICMSSTTaxUpdatedFromXml_BR = _updated;
}

/// <summary>
/// Disables the Directive tab if the DirectiveId has not been assigned in a public sector France context.
/// </summary>
void setFieldsEditableForDirective()
{
    if (CustVendDirective_PSN::isContextPublicSectorFrance())
    {
        directiveTabPage_PSN.allowEdit(vendDirective_PSN.VendDirectiveId != '');
    }
}

/// <summary>
/// Sets the design view for <c> VendEditInvoice </c> form to either view in Multiple invoice experience,
/// multiple invoice experience header view, single invoice experience or invoice line details view.
/// </summary>
/// <param name="_vendInvoiceDesignMode">
/// Defines the view type with which the form should present. Default view for vendor invoice form is multiple invoice experience.
/// </param>
void setFormDesign(VendInvoiceDesignMode _vendInvoiceDesignMode = VendInvoiceDesignMode::MultipleInvoice)
{
    vendInvoiceDesignMode = _vendInvoiceDesignMode;

    // Set Groups
    switch (_vendInvoiceDesignMode)
    {
        case VendInvoiceDesignMode::MultipleInvoice:
            isSingleInvoiceHeader = false;

            ok.visible(true);
            specQtyDropDialog.enabled(purchEditLinesForm_Invoice.specQtyAllowEdit());
            general.visible(true);

            // <GEEPL>
            summaryPurchSetup.visible(purchEditLinesForm_Invoice.showSummaryPurchSetupDefault_PL());
            chooseInvoice.visible(purchEditLinesForm_Invoice.showOverviewNew_PL());
            buttonSelectJournal.visible(purchEditLinesForm_Invoice.showOverviewNew_PL());
            // </GEEPL>);

            companyLookup.allowEdit(false);

            element.task(#taskSwitchToGridView);
            break;

        case VendInvoiceDesignMode::MultipleInvoiceHeader:
            isSingleInvoiceHeader = false;

            ok.visible(false);

            specQtyDropDialog.enabled(false);
            general.visible(false);

            summaryPurchSetup.visible(false);
            chooseInvoice.visible(false);
            buttonSelectJournal.visible(false);

            companyLookup.allowEdit(false);

            element.setLineDetailControls();

            break;

        case VendInvoiceDesignMode::SingleInvoiceHeader:
            isSingleInvoiceHeader = true;

            ok.visible(true);
            specQtyDropDialog.enabled(purchEditLinesForm_Invoice.specQtyAllowEdit());
            general.visible(true);

            summaryPurchSetup.visible(false);
            chooseInvoice.visible(false);
            buttonSelectJournal.visible(false);

            element.setLineDetailControls();

            break;
    }
}

/// <summary>
/// Provide access to invoice header fields that are in common with the PO header.
/// </summary>
public void setHeaderFields()
{
    boolean isNonPO = vendInvoiceInfoTable.isNonPO();
    boolean isAdvance = (vendInvoiceInfoTable.InvoiceType == PurchInvoiceType::VendorAdvance);
    boolean isDirectInvoice = vendInvoiceInfoTable.isDirectInvoice();
    boolean enableFields;
    boolean isHeaderInitialized = (vendInvoiceInfoTable.TableRefId != '');

    element.enablePurchAgreementHeader_PSNLookup();

    groupForeignTrade_ListCode.visible(IntrastatParameters::find().euSalesListTransferPurchases);

    this.enableInvoiceAccountLookup();

    buttonApplyPrepayments.enabled(!isAdvance && !ledgerParameters.JournalizingDefinitionEnableValue && !isInAutomatedProcessing);

    if (isLastInvoiceAdvance != isAdvance)
    {
        isLastInvoiceAdvance = isAdvance;

        if (isInAutomatedProcessing)
        {
            enableFields = false;
        }
        else
        {
            enableFields = isHeaderInitialized && !isAdvance;
        }

        useInvoiceMatching = enableFields;

        if (isAdvance && purchParmUpdate.SpecQty != PurchUpdate::All)
        {
            purchParmUpdate.SpecQty = PurchUpdate::All;
            purchParmUpdate.update();
            purchParmUpdate_ds.reread();
            purchParmUpdate_ds.refresh();
        }

        specQtyDropDialog.enabled(!isAdvance);
        chooseInvoice.enabled(enableFields);
        buttonSelectJournal.enabled(enableFields);

        if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
        {
            // we also want to disable the button if the commitments feature is enabled.
            buttonSelectJournal.enabled(enableFields && !PublicSectorUtils::isFrenchRegulatoryEnabled());
        }
        else
        {
            buttonSelectJournal.enabled(enableFields);
        }

        buttonParmTablePackingSlips.enabled(workflowFullEnabled && enableFields);
        buttonRetrievePO.enabled(enableFields && purchEditLinesForm_Invoice.editButtonRetrievePO());
        summaryPurchSetup.enabled(enableFields);

        paymentInvoice_PaymentSched.enabled(enableFields);
        paymentInvoice_Payment.enabled(enableFields);
        paymentInvoice_FixedDueDate.allowEdit(enableFields);
        groupSalesTax_InclTax.allowEdit(enableFields);
        inventoryGroup.enabled(enableFields);

        buttonSubTableMiscCharges.enabled(enableFields);
        buttonPageParmTableMiscCharges.enabled(enableFields);
        buttonPageParmTableAllocate.enabled(enableFields && workflowPartiallyEnabled);
        maintainMiscLineCharges.enabled(workflowFullEnabled && enableFields);
        vendInvoiceInfoTable_MarkupGroup.enabled(enableFields);

        buttonParmTableMatchingDetails.enabled(enableFields);
        vendInvoicePolicyViolationTable.enabled(enableFields);

        buttonPaymentFormat.enabled(enableFields);
        buttonPaymentSched.enabled(enableFields && !isAdvance);
        groupMarkup.enabled(enableFields);
        groupDiscount.enabled(enableFields);
        groupCashDiscount.enabled(enableFields);
        packingslip.enabled(enableFields);
        tabButtonParmLinePackingSlips.enabled(enableFields);
        reasons.enabled(enableFields);
        tabPageFixedAsset.enabled(enableFields);
        tabPageProject.enabled(enableFields);
        tabPageInventoryDimensions.enabled(enableFields);
        menuItemButtonAdd.enabled(workflowFullEnabled && enableFields);
    }

    if (previousNonPO != isNonPO || isLastInvoiceAdvance != isAdvance
        || (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) && isLastInvoiceDirect != isDirectInvoice))
    {
        previousNonPO = isNonPO;
        isLastInvoiceAdvance = isAdvance;

        if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
        {
            isLastInvoiceDirect = isDirectInvoice;
        }

        // Hide/show the non-PO fields
        paymentInvoice_PaymentSched.visible(isNonPO || isAdvance);
        paymentInvoice_Payment.visible(isNonPO || isAdvance);

        groupCashDiscount_CashDiscCode.visible(isNonPO || isAdvance);
        groupCashDiscount_CashDiscPercent.visible(isNonPO || isAdvance);

        groupSalesTax_TaxGroup.visible(isNonPO || isAdvance);
        groupSalesTax_OverrideSalesTax.visible(isTaxServiceForPurchaseEnabled && (isNonPO || isAdvance));
        groupSalesTax_VATNum.visible(isNonPO || isAdvance);
        groupSalesTax_InclTax.visible(isNonPO || isAdvance);
        groupSalesTax_EnterpriseNumber.visible(isNonPO);
        //<GEEPL>
        groupSalesTax_TaxPeriodPaymentCode_PL.visible(isNonPO || isAdvance);
        //</GEEPL>

        groupPosting_PostingProfile.visible(isNonPO || isAdvance);
        groupPosting_SettleVoucher.visible(isNonPO || isAdvance);
        groupPosting_NumberSequenceGroup.visible(isNonPO || isAdvance);

        groupForeignTrade_TransactionCode.visible(isNonPO);
        groupForeignTrade_Transport.visible(isNonPO);
        groupForeignTrade_Port.visible(isNonPO);
        groupForeignTrade_StatProcId.visible(isNonPO);
        groupForeignTrade_CountyOrigDest.visible(isNonPO);
        groupForeignTrade_ListCode.visible(isNonPO && IntrastatParameters::find().euSalesListTransferPurchases);

        vendInvoiceInfoTable_markupGroup.visible(isNonPO || isAdvance);
        inventoryGroup_InventLocationId.visible(isNonPO || isAdvance);
        inventoryGroup_InventSiteId.visible(isNonPO || isAdvance);

        purchTable_PaymentSched.visible(!(isNonPO || isAdvance));
        purchTable_Payment.visible(!(isNonPO || isAdvance));

        purchTable_CashDisc.visible(!(isNonPO || isAdvance));
        purchTable_CashDiscPercent.visible(!(isNonPO || isAdvance));

        purchTable_TaxGroup.visible(!(isNonPO || isAdvance));
        purchTable_OverrideSalesTax.visible(isTaxServiceForPurchaseEnabled && !(isNonPO || isAdvance));
        purchTable_VATNum.visible(!(isNonPO || isAdvance));
        purchTable_InclTax.visible(!(isNonPO || isAdvance));
        purchTable_EnterpriseNumber.visible(!(isNonPO || isAdvance));
        // <GEEPL>
        purchTable_TaxPeriodPaymentCode_PL.visible(!(isNonPO || isAdvance));
        // </GEEPL>

        purchTable_PostingProfile.visible(!(isNonPO || isAdvance));
        purchTable_SettleVoucher.visible(!(isNonPO || isAdvance));
        purchTable_NumberSequenceGroup.visible(!(isNonPO || isAdvance));

        purchTable_TransactionCode.visible(!isNonPO);
        purchTable_Transport.visible(!isNonPO);
        purchTable_Port.visible(!isNonPO);
        purchTable_StatProcId.visible(!isNonPO);
        purchTable_CountyOrigDest.visible(!isNonPO);

        purchTable_markupGroup.visible(!(isNonPO || isAdvance));
        purchTable_InventLocationId.visible(!(isNonPO || isAdvance));
        purchTable_InventSiteId.visible(!(isNonPO || isAdvance));

        //Set visibility for financial dimension controls
        GroupFinancialDimensionHeader.visible(isNonPO);
        GroupFinancialDimensionHeaderPurch.visible(!isNonPO);
        GroupPurchDistTemplateHeader.visible(!isNonPO);
        GroupDistributionTemplateHeader.visible(isNonPO);
    }
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        vendInvoiceInfoTable_W_ds.object(fieldNum(VendInvoiceInfoTable_W, SalesPurchOperationType_BR)).allowEdit(isNonPO);
    }
    // </GBR>

    if (isHU)
    {
        foreignTrade_HU.visible(isNonPO);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, CurrencyCode)).allowEdit(isNonPO && !isDirectInvoice);
        this.enableInvoiceRelatedDocFields(purchParmTable_PurchId.valueStr(), purchParmTable_PurchAggHeader.valueStr());
    }
    else
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, CurrencyCode)).allowEdit(isNonPO);
    }

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, IgnoreCalculatedSalesTax)).visible(vendParameters.KeepSalesTaxAdjustmentsForPO);

    element.allowHeaderCreate();
    element.allowLineCreate();

    if (purchFormLetter.isEGAISPosting_RU())
    {
        vendInvoiceInfoTable_ds.allowDelete(purchEditLinesForm_Invoice.allowHeaderDelete());
        vendInvoiceInfoLine_ds.allowDelete(purchEditLinesForm_Invoice.allowLineDelete());
    }
}

