{
    boolean isInventoried = vendInvoiceInfoLine.inventoried();

    tabButtonInventReserve.enabled(isInventoried);
    tabButtonInventTransRegister.enabled(isInventoried);
    tabButtonInventTransPick.enabled(isInventoried);
    tabButtonInventOnhand.enabled(isInventoried);
    tabButtonInventLot.enabled(isInventoried);
    tabButtonInventTrans.enabled(isInventoried);
}

void setInvoiceAndExit() /* Benchmark -start */
{
    //    BMRandom BmRandom = new BMRandom();

    purchParmTable_Num.setFocus();
    purchParmTable_Num.pasteText(strFmt('%1', vendInvoiceInfoTable.RecId)); //BMRandom.num(10000,9999999)));

    purchParmTable_gridParmTableNum.setFocus();
    purchParmTable_gridParmTableNum.pasteText(strFmt('%1', vendInvoiceInfoTable.RecId)); //BMRandom.num(10000,9999999)));

    purchParmTable_PurchId.setFocus();

    element.closeOk();
