}

private void buildMainInstructionText()
{
    if (vendInvoiceInfoTable.Num != "" && vendInvoiceInfoTable.PurchName != "")
    {
        mainInstructionStaticText.text(strFmt("%1 : %2", vendInvoiceInfoTable.Num, vendInvoiceInfoTable.PurchName));
    }
    else
    {
        mainInstructionStaticText.text(vendInvoiceInfoTable.Num != "" ? vendInvoiceInfoTable.Num : vendInvoiceInfoTable.PurchName);
    }
}

void setInvoiceDetailsHeader()
{
    // Hide title when there are no records
    mainInstructionStaticText.visible(vendInvoiceInfoTable_ds.numberOfRowsLoaded() > 0);

    // Set static texts
    // <Invoice Number> : <Invoice Account Name>
    this.buildMainInstructionText();

    // Moved code from the VendInvoiceInfoTable.Active() method in order to avoid duplicating the Data Sources node in the higher layer.
    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        if ((vendInvoiceInfoTable.Ordering == DocumentStatus::Invoice))
        {
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainagePercent)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainageAmount)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleasePercent)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleaseAmount)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainageBalance)).allowEdit(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaIsFinal)).allowEdit(true);
        }
        else
        {
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainagePercent)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainageAmount)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleasePercent)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaReleaseAmount)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaRetainageBalance)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine,psaIsFinal)).allowEdit(false);
        }
    }
}

/// <summary>
/// Sets the line details controls to disable if there is no header record exists.
/// </summary>
void setLineDetailControls()
{
    if (!vendInvoiceInfoTable.RecId)
    {
        buttonGroupLines.enabled(false);
    }
    else
    {
        buttonGroupLines.enabled(true);
    }
}

/// <summary>
/// Sets the print management variables.
/// </summary>
/// <param name="_printInvoice">
/// A boolean value, to indicate if an invoice report will be printed.
/// </param>
/// <param name="_printout">
/// An enum value, to indicate if information before the system is updated should be printed,
/// or else if information after the system was updated should be printed.
/// </param>
/// <param name="_usePrintManagement">
/// A boolean value, to indicate if print management will be used.
/// </param>
/// <param name="_printSalesFormLetter">
/// A boolean value, to indicate if sales form letter will be printed.
/// </param>
/// <param name="_printEUVatInvoice">
/// A boolean value, to indicate if intra-community invoice will be printed.
/// </param>
/// <param name="_printFacture">
/// A boolean value, to indicate if a facture will be printed.
/// </param>
/// <param name="_printAcceptance">
/// A boolean value, to indicate if an acceptance report will be printed.
/// </param>
/// <param name="_printInventBailee">
/// A boolean value, to indicate if an acceptance report in store will be printed.
/// </param>
/// <param name="_printExciseInvoice">
/// A boolean value, to indicate if an excise invoice report will be printed.
/// </param>
/// <param name="_exciseInvoiceAddress">
/// An enum value, to indicate whether a company address or warehouse address
/// will be printed.
/// </param>
public void setPrintVariables( boolean _printInvoice,
                                Printout _printout,
                                boolean _usePrintManagement,
                                boolean _printSalesFormLetter,
                                boolean _printEUVatInvoice,
                                boolean _printFacture = false,
                                boolean _printAcceptance = false,
                                boolean _printInventBailee = false,
                                boolean _printExciseInvoice = false,
                                ExciseInvoiceAddressType_IN _exciseInvoiceAddress = ExciseInvoiceAddressType_IN::WarehouseAddress)
{
    purchFormLetter.printFormLetter(_printInvoice);
    purchFormLetter.printout(_printout);
    purchFormLetter.usePrintManagement(_usePrintManagement);
    purchFormLetter.printSalesFormLetter(_printSalesFormLetter);
    purchFormLetter.printEUVatInvoice(_printEUVatInvoice);
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]))
    {
        purchFormLetter.printFacture_RU(_printFacture);
        purchFormLetter.printAcceptanceReport_RU(_printAcceptance);
        purchFormLetter.printInventBaileeReport_RU(_printInventBailee);
    }
    // </GEERU>
    // <GIN>
    if (isIN)
    {
        purchFormLetter.printExciseFormLetter_IN(_printExciseInvoice);
        purchFormLetter.printExciseInvoiceAddress_IN(_exciseInvoiceAddress);
    }
    // </GIN>
}

/// <summary>
/// Sets the release date and comment fields.
/// </summary>
void setStoppedReleaseFields()
{
    if (vendInvoiceInfoTable.Approved == NoYes::No)
    {
        vendInvoiceInfoTable.InvoiceReleaseDate = utcDateTimeNull();
        groupApprove_releaseDate.enabled(false);
        vendInvoiceInfoTable.ReleaseDateComment = "";
        groupApprove_ReleaseDateComment.enabled(false);
    }
    else
    {
        groupApprove_releaseDate.enabled(true);
        groupApprove_ReleaseDateComment.enabled(true);
    }
}

void setTaxWithholdEnabled_IN()
{
    vendInvoiceInfoTableTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoTableTaxExtension_IN, tcsGroup)).allowEdit(!vendInvoiceInfoTableTaxExtension_IN.tdsGroup &&
                                                                    taxWithholdParameters.tcsActivate);
    vendInvoiceInfoTableTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoTableTaxExtension_IN, tdsGroup)).allowEdit(!vendInvoiceInfoTableTaxExtension_IN.tcsGroup &&
                                                                    taxWithholdParameters.tdsActivate);
    vendInvoiceInfoLineTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoLineTaxExtension_IN, tcsGroup)).allowEdit(!vendInvoiceInfoLineTaxExtension_IN.tdsGroup &&
                                                                   taxWithholdParameters.tcsActivate);
    vendInvoiceInfoLineTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoLineTaxExtension_IN, tdsGroup)).allowEdit(!vendInvoiceInfoLineTaxExtension_IN.tcsGroup &&
                                                                   taxWithholdParameters.tdsActivate);
    buttonWithholdTax_IN.enabled((vendInvoiceInfoLineTaxExtension_IN.tdsGroup || vendInvoiceInfoLineTaxExtension_IN.tcsGroup) &&
                                 (taxWithholdParameters.tdsActivate || taxWithholdParameters.tcsActivate));
}

void setTaxWithholdVisible_IN()
{
    vendInvoiceInfoTableTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoTableTaxExtension_IN, tdsGroup)).visible(taxWithholdParameters.tdsInvoice);
    vendInvoiceInfoLineTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoLineTaxExtension_IN, tdsGroup)).visible(taxWithholdParameters.tdsInvoice);
    vendInvoiceInfoTableTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoTableTaxExtension_IN, tcsGroup)).visible(taxWithholdParameters.tcsInvoice);
    vendInvoiceInfoLineTaxExtension_IN_ds.object(fieldNum(VendInvoiceInfoLineTaxExtension_IN, tcsGroup)).visible(taxWithholdParameters.tcsInvoice);
    withholdingTax_header_IN.visible(taxWithholdParameters.tdsInvoice || taxWithholdParameters.tcsInvoice);
    withholdingTax_Line_IN.visible(taxWithholdParameters.tdsInvoice || taxWithholdParameters.tcsInvoice);
    buttonWithholdTax_IN.visible(taxWithholdParameters.tdsInvoice || taxWithholdParameters.tcsInvoice);
}

private void setVisible_IN()
{
    boolean isTaxParametersMarked = TaxParameters::checkTaxParameters_IN();

    vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W,AssessableValue_IN)).visible(isTaxParametersMarked);
    vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W,MaximumRetailPrice_IN)).visible(isTaxParametersMarked);
    vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W,CustomsBillOfEntryNumberTable_IN)).visible(purchFormLetter.purchTable().purchTable_W().CustomsImportOrder_IN);
    VendInvoiceInfoLine_W_AssessableValueAccountingCurrency_IN.visible(purchFormLetter.purchTable().isCustomsImportOrder_IN());
}

/// <summary>
/// Displays the attchments for the given table buffer.
/// </summary>
/// <param name = "_record">The table buffer for which documents are to be attached.</param>
public void showDocuments(Common _record)
{
    if (_record)
    {
        Args args = new Args(menuItemDisplayStr(DocuView));

        args.caller(element);
        args.record(_record);
        args.menuItemName(menuitemDisplayStr(DocuView));
        args.menuItemType(MenuItemType::Display);

        FormRun formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

