    return ICMSSTAdditionalInfoFeature::isEnabled()
        && VendInvoiceInfoTable.purchTable().PurchaseType != PurchaseType::ReturnItem
        && vendInvoiceInfoLine.purchLine().hasICMSSTTax_BR();
}

