PurchUpdate specQty(PurchUpdate _specQty = purchParmUpdate.SpecQty)
{
    // This method is used when calculating totals
    if (_specQty != purchParmUpdate.SpecQty)
    {
        purchParmUpdate.SpecQty = _specQty;
        purchFormLetter.parmSpecQtyIsSet(true);
        element.updateDocWithSpecQty();
    }

    return purchParmUpdate.SpecQty;
}

void specQtyChanged()
{
    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        //We also want to disable the button if the commitments feature is enabled.
        buttonSelectJournal.enabled (purchParmUpdate.SpecQty == PurchUpdate::PackingSlip && !PublicSectorUtils::isFrenchRegulatoryEnabled());
    }
    else
    {
        buttonSelectJournal.enabled (purchParmUpdate.SpecQty == PurchUpdate::PackingSlip);
    }

    if (countryRegion_LTLV)
    {
        this.allowEditPackingSlips_W();
    }
}

/// <summary>
/// Submit to source document processor for an on-demand GL budget check
/// </summary>
/// <param name="_vendInvoiceInfoTable">
/// Vendor invoice.
/// </param>
private void submitToSourceDocument(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    boolean isSubmitSourceDocumentLinesForHeader = false;

    isSubmitSourceDocumentLinesForHeader =
        _vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Draft
        && vendParameters.editCalculateInvoiceTotal()
        && _vendInvoiceInfoTable.ParmJobStatus != ParmJobStatus::Executed;

    if (isSubmitSourceDocumentLinesForHeader)
    {
        SourceDocumentProcessorFacade::submitSourceDocumentLinesForHeader(_vendInvoiceInfoTable, SourceDocumentLineAccountingStatus::FullyDistributed);
    }
    else if (element.shouldSubmitToSourceDocumentForBudgetOrProject())
    {
        SourceDocumentProcessorFacade::submitOnDemandSourceDocumentImpl(_vendInvoiceInfoTable);
    }
}

/// <summary>
/// Determines whether the current source document is valid to submit for source document processor.
/// </summary>
/// <returns>
/// true if the current source document is valid to submit for source document; otherwise, false.
/// </returns>
[Wrappable(true)]
protected final boolean shouldSubmitToSourceDocumentForBudgetOrProject()
{
    return (Ledger::isLedgerBudgetControlEnabled(Ledger::current()) || vendInvoiceInfoLine_Project.ProjId != '');
}

AccountOrder sumBy(AccountOrder _sumBy = purchParmUpdate.SumBy)
{
    // This method is used when calculating totals
    purchParmUpdate.SumBy = _sumBy;
    return purchParmUpdate.SumBy;
}

void sumByChanged(boolean _init)
{
    if (_init && purchFormLetter.isEGAISPosting_RU())
    {
        purchParmUpdate.write();
    }

    purchParmUpdate_ds.refresh();
    purchParmUpdate_ds.reread();

    if (purchFormLetter.sumBy() == AccountOrder::Order)
    {
        initSumNum = purchFormLetter.sumNum();
        initSumPurchId = purchFormLetter.sumPurchId();

        purchParmUpdate.SumNum = initSumNum;

        purchParmUpdate.SumPurchId = initSumPurchId;
    }
    else
    {
        purchParmUpdate.SumNum = '';
        purchParmUpdate.SumPurchId = '';
    }

    purchParmUpdate.write();

    if (!_init)
    {
        element.clearParmTableTotals();
    }

    // When first opening the form, there is no need to arrange if SumBy is None,
    // or there is only one invoice
    if ((_init && purchParmUpdate.SumBy == AccountOrder::None) ||
        (_init && vendInvoiceInfoTable_ds.numberOfRowsLoaded() <= 1))
    {
        reArrangeNow = false;
    }
    else
    {
        reArrangeNow = true;
    }

    if (purchFormLetter.isEGAISPosting_RU() && reArrangeNow)
    {
        element.reArrange(false);
    }

}

void summaryPurchSetup()
{
    purchFormLetter.proforma(false);

    // Arrange Drop dialog updates purchParmUpdate record, reread to update the buffer
    purchParmUpdate.reread();

    element.reArrange(true);

    purchParmUpdate_ds.refresh();
    purchParmUpdate_ds.reread();

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.chooseTable();
    }
    // </GIN>
}

/// <summary>
/// Indicates whether this current user's setup supports the global functionality for invoicing.
/// </summary>
/// <returns>A boolean indicating if the global functionality is supported</returns>
public boolean supportsGlobalFunctionality()
{
    MyLegalEntities myLegalEntities;
    select count(RecId) from myLegalEntities;
    return myLegalEntities.RecId > 1;
}

public int task(int _taskId)
{
    if (_taskId == #taskEditRecord)
    {
        if (vendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Approved)
        {
            // If the saved invoice is approved, the user can choose to either:
            // edit the "approved" invoice by selecting the yes button, open the details form in read-only mode
            // by clicking the No button, or cancel it by clicking the cancel button.
            DialogButton button = Box::yesNoCancel(strFmt("@SYS328693", vendInvoiceInfoTable.Num), DialogButton::Cancel, "@SYS328754");

            switch (button)
            {
                case DialogButton::Yes:
                    VendInvoiceInfoTable::resetVendorInvoiceWorkflow(vendInvoiceInfoTable.RecId);
                    element.refresh();
                    break;

                case DialogButton::No:
                    invoiceDetails.setFocus();
                    return 1;

                case DialogButton::Cancel:
                    return 1;
            }
        }
    }

    return super(_taskId);
}

/// <summary>
///   This method executes when tax adjustments have been reset.
/// </summary>
public void taxReset()
{
    vendInvoiceInfoTable.IgnoreCalculatedSalesTax = false;
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    Set validInventDimDSset;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init :

            if (!inventDimFormSetup)
            {
                validInventDimDSset = new Set(Types::String);
                validInventDimDSset.add(inventDim_ds.name());
                inventDimFormSetup = InventDimCtrl_Frm_Mov_VendInvoice::newFromFormInvoice(element, vendInvoiceInfoLine);
            }

        case InventDimFormDesignUpdate::Active:
        case InventDimFormDesignUpdate::FieldChange:

            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(vendInvoiceInfoLine.ItemId));
            inventDimFormSetup.formSetControls(mode == InventDimFormDesignUpdate::Active);
            element.enableVendInvoiceInfoLineCWFields();
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

private void updateDesign_VendReccuringInvoice()
{
    boolean vendRecurringInvoiceEnabled = VendRecurringInvoiceFeature::isEnabled();
    createTemplateDropDialog.visible(vendRecurringInvoiceEnabled);
    createFromTemplateDropDialog.visible(vendRecurringInvoiceEnabled);
}

private void updateDesign_BR()
{
    boolean showICMSSTAdditionalInfo = this.showICMSSTAdditionalInfo_BR();
    VendInvoiceInfoLine_BR_ds.object(fieldNum(VendInvoiceInfoLine_BR, ICMSSTCollectionPaymentMode)).visible(showICMSSTAdditionalInfo);
    VendInvoiceInfoLine_BR_ds.object(fieldNum(VendInvoiceInfoLine_BR, ICMSSTCollectionPaymentNumber)).visible(showICMSSTAdditionalInfo);
    VendInvoiceInfoLine_BR_ds.object(fieldNum(VendInvoiceInfoLine_BR, RespWithholdingICMSST)).visible(showICMSSTAdditionalInfo);
    VendInvoiceInfoLine_BR_ds.object(fieldNum(VendInvoiceInfoLine_BR, NfeVbcstretVicmsstretTagsReported)).visible(showICMSSTAdditionalInfo);

    if (showICMSSTAdditionalInfo)
    {
        VendInvoiceInfoLine_BR_ds.object(fieldNum(VendInvoiceInfoLine_BR, NfeVbcstretVicmsstretTagsReported)).setValue(true);
    }
}

public void updateDesign_IN()
{
    boolean isBOELine;

    if (TaxParameters::checkTaxParameters_IN())
    {
        isBOELine = purchFormLetter.purchTable().isCustomsImportOrder_IN()
            && VendInvoiceInfoLine_W.CustomsBillOfEntryNumberTable_IN != 0;
        VendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, AssessableValueAccountingCurrency_IN)).allowEdit(!isBOELine);
    }
}

