void updateDocFromProforma(MenuItemNameDisplay _callerMenuItem)
{
    if (purchFormLetter.proforma())
    {
        // <GEERU>
        if (purchFormLetter.printFormLetter() ||
           purchFormLetter.printFacture_RU() ||
           purchFormLetter.printAcceptanceReport_RU() ||
           purchFormLetter.printInventBaileeReport_RU())
        // </GEERU>
        {
            purchParmUpdate.Proforma = NoYes::No;
            purchParmUpdate.updateNow(true, false);
        }
    }
    else if (_callerMenuItem == menuitemDisplayStr(PurchFormLetter_Proforma_InvoiceDisplay))
    {
        purchParmUpdate.Proforma = NoYes::No;
        purchParmUpdate.updateNow(true, false);
    }
    else
    {
        purchParmUpdate.Proforma = NoYes::Yes;
        purchParmUpdate.updateNow(true, true);

        if (purchFormLetter.printFormLetter() && purchFormLetter.usePrintManagement())
        {
            purchFormLetter.printFormLetter(true);
        }
        else if (!purchFormLetter.printFormLetter())
        {
            purchFormLetter.printFormLetter(false);
        }
    }

    purchFormLetter.printout(purchFormLetter.printout());
}

void updateDocWithSpecQty()
{
    element.specQtyChanged();

    // <GEERU>
    #ISOCountryRegionCodes
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]) &&
        ((vendInvoiceDesignMode != VendInvoiceDesignMode::MultipleInvoice ||
          vendInvoiceInfoTable_DS.numberOfRowsLoaded() <= 1) &&
          vendInvoiceDesignMode != VendInvoiceDesignMode::MultipleInvoiceHeader))
    {
        element.chooseLines_RU();
    }
    else
    {
        // </GEERU>
        element.reSelect();

        // <GTE>
        if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
        {
            vendInvoiceInfoTable.markCurrentTaxDocumentTaxStatusDirty();
        }
        // </GTE>

        // <GEERU>
    }
    // </GEERU>
    element.calcTotals();
    vendInvoiceInfoTable_ds.reread();

    element.checkForPrepaymentApplicationFlag();
}

public void updateSpecQty(PurchUpdate purchUpdate)
{
    DialogButton dialogButton;
    PurchUpdate prevPurchUpdate = purchParmUpdate.SpecQty;

    purchParmUpdate.SpecQty = purchUpdate;

    if (vendInvoiceInfoTable && !(purchFormLetter.parmSpecQtyIsSet()))
    {
        if (prevPurchUpdate != purchParmUpdate.SpecQty && vendInvoiceInfoLine_ds.numberOfRowsLoaded() > 0)
        {
            dialogButton = Box::yesNo("@SYS136641", DialogButton::No);

            if (dialogButton == DialogButton::Yes)
            {
                purchFormLetter.parmSpecQtyIsSet(true);
                element.updateDocWithSpecQty();
                element.checkIfMatchedAndRefresh();
            }
            else
            {
                purchParmUpdate.SpecQty = prevPurchUpdate;
            }
        }

        else if (prevPurchUpdate != purchParmUpdate.SpecQty)
        {
            purchFormLetter.parmSpecQtyIsSet(true);
            element.updateDocWithSpecQty();
        }
    }
    else
    {
        purchFormLetter.specQty(purchParmUpdate.SpecQty);
    }

    purchFormLetter.parmSpecQtyIsSet(false);

    if (vendInvoiceInfoTable.isNonPO() && vendInvoiceInfoTable.InvoiceAccount == '')
    {
        if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
        {
            // We also want to disable the button if the commitments feature is enabled.
            buttonSelectJournal.enabled(purchParmUpdate.SpecQty == PurchUpdate::PackingSlip && !PublicSectorUtils::isFrenchRegulatoryEnabled());
        }
        else
        {
            buttonSelectJournal.enabled(purchParmUpdate.SpecQty == PurchUpdate::PackingSlip);
        }
    }
    SpecQtyDropDialog.text("@AccountsPayable:DefaultFrom" + enum2Str(PurchParmUpdate.SpecQty));
}

/**
    Synchronize the VendInvoiceInfoTable datasource with the database
**/
void updateFormCacheVendInvoiceInfoTable()
{
    VendInvoiceInfoTable vendInvoiceInfoTableDataBase;
    VendInvoiceInfoTable vendInvoiceInfoTableFormCache;

    vendInvoiceInfoTableFormCache = vendInvoiceInfoTable_ds.getFirst(0, false);

    while (vendInvoiceInfoTableFormCache)
    {
        vendInvoiceInfoTableDataBase = VendInvoiceInfoTable::find(vendInvoiceInfoTableFormCache.ParmId, vendInvoiceInfoTableFormCache.TableRefId);

        if (vendInvoiceInfoTableDataBase)
        {
            vendInvoiceInfoTableFormCache.data(vendInvoiceInfoTableDataBase);

            if (vendInvoiceInfoTableDataBase.RecId == vendInvoiceInfoTable.RecId)//the currently active record
            {
                vendInvoiceInfoTable.data(vendInvoiceInfoTableDataBase);
                vendInvoiceInfoTable_ds.setCurrent();
            }
        }

        vendInvoiceInfoTableFormCache = vendInvoiceInfoTable_ds.getNext();
    }
}

public void updateFormState(SourceDocumentLineRecId _sourceDocumentLineRecId)
{
    // Accounting distribution form has closed - update budget check result for changes from edit
    vendInvoiceInfoLine_ds.updateBudgetCheckResult();
}

