    this.enableButtonVendInvoiceXmlMatch_BR(enableMatchXmlButtons);
    this.enableButtonViewReceivedXml_BR(enableMatchXmlButtons);
    this.enableButtonUpdateQtyAndPriceFromNfe_BR(enableMatchXmlButtons);
    this.enableButtonUpdatePresumedTaxesFromNfe_BR(enableMatchXmlButtons && !isICMSSTTaxUpdatedFromXml_BR);
    this.enableButtonViewVendInvoiceXmlMapping_BR(enableMatchXmlButtons);
}

/// <summary>
/// Indicates if the vendor invoice lines could be completely mapped against the NF-e received XML lines
/// </summary>
/// <returns>
/// True if the it was NOT possible to completely map the lines; false otherwise
/// </returns>
public boolean hasMappingIssuesBetweenNfeXmlXViLines_BR()
{
    EFDocAccessKey_BR accessKey = vendInvoiceInfoTable_W.AccessKey_BR;
    EFDocumentReceivedXmlView_BR receivedXmlView = EFDocumentReceivedXmlView_BR::findByAccessKey(accessKey);
    EFDocRecXmlMappingValidator_BR mapValidator = EFDocRecXmlMappingValidator_BR::construct(receivedXmlView, VendInvoiceInfoTable);

    return !mapValidator.allItemsAreMappedBetweenXmlAndVendorInv();
}

/// <summary>
/// Updates vendor invoice lines with the values from the NF-e received XML lines
/// </summary>
public void updateVendInvoiceLinesFromNfeXmlLines_BR()
{
    MenuFunction menuFunction = new MenuFunction(menuitemActionStr(EFDocRecXmlUpdateVendorInvoiceLines_BR), MenuItemType::Action);

    Args args = new Args(formStr(EFDocumentReceivedXMLViewer_BR));
    args.record(vendInvoiceInfoTable);
    menuFunction.run(args);
}

public void activate(boolean _active)
{
    super(_active);
}

/// <summary>
/// Checks the invoice matching status of the document and then refreshes the datasource and display method cache to make sure that the UI reflects the updated status.
/// </summary>
public void checkIfMatchedAndRefresh()
{
    if (!vendParameters.editAutomaticHeaderMatching())
    {
        vendInvoiceInfoTable.checkIfMatched();
        vendInvoiceInfoTable_ds.reread();
        vendInvoiceInfoTable_ds.refresh();
    }

    if (isTaxServiceForPurchaseEnabled)
    {
        element.rereadAfterCalculatingTaxUsingTaxIntegration();
    }

    checkIfMatchedStatusObservableLink.markChanged();
}

/// <summary>
/// Checks the invoice matching status of the document and then refreshes the display method cache to make sure that the UI reflects the updated status.
/// </summary>
/// <remarks>
/// This method is only called from the header (VendInvoiceInfoTable_ds) as we want to update the matching status and match status display fields, but don't want to force a save
/// and do not want to refresh the datasource.
/// </remarks>
public void checkIfMatchedAndRefreshFromHeader()
{
    if (!vendParameters.editAutomaticHeaderMatching())
    {
        vendInvoiceInfoTable.LastMatchVariance = vendInvoiceInfoTable.checkIfMatched(false);
    }

    checkIfMatchedStatusObservableLink.markChanged();

}

/// <summary>
/// Updates the expected invoice matching value and refreshes the <c>VendInvoiceInfoTable</c> datasource
/// </summary>
public void updateExpectedMatchValuesAndRefresh()
{
    //If we are not using automatic header matching and the document either hasn't had matching run or has been invalidated, rerun the matching logic for the document
    if (!vendParameters.editAutomaticHeaderMatching() && vendInvoiceInfoTable.LastMatchVariance == LastMatchVarianceOptions::Unknown)
    {
        vendInvoiceInfoTable.updateExpectedValuesAndMatchStatus();
        vendInvoiceInfoTable_ds.refresh();
    }
}

private void updateWorkflowIconForLine()
{

    if (Workflow::findWorkItemsForCurrentUser(vendInvoiceInfoLine).elements()
        || Workflow::findTrackingStatusForDocument(vendInvoiceInfoLine))
    {
        RequestStatus.visible(true);
        if (Workflow::pendingWorkflowExistsForDocument(vendInvoiceInfoLine))
        {
            LineWorkflowDropDialog.visible(true);
        }
        else
        {
            LineWorkflowDropDialog.visible(false);
        }
    }
    else
    {
        RequestStatus.visible(false);
        LineWorkflowDropDialog.visible(false);
    }
}

private void updateWorkflowIconForHeader()
{
    //The workflow icon on header should be visible only if you can submit it to the workflow with an active workflow configuration
    //or if there are workitems on the invoice for the current user
    //or if there is a workflow history available for the invoice
    if ((this.canSubmitToWorkflow() && Workflow::findWorkflowConfigurationToActivate(vendInvoiceInfoTable, workflowTypeStr(VendProcessInvoice))
        || Workflow::findWorkItemsForCurrentUser(vendInvoiceInfoTable).elements()
        || Workflow::findTrackingStatusForDocument(vendInvoiceInfoTable)))
    {
        VendorInvoiceHeaderWorkflowDropDialog.visible(true);
    }
    else
    {
        VendorInvoiceHeaderWorkflowDropDialog.visible(false);
    }
}

/// <summary>
/// This method is called from workflow drop dialog in order to refresh lines workflow icon.
/// </summary>
public void refreshWorkflowIcononLine()
{
    vendInvoiceInfoLine_ds.reread();
    vendInvoiceInfoLine_ds.refresh();
    workflowIconObservableLink.markChanged();
}

public void enablePaymentInvoiceFixedDueDate()
{
    boolean enabled = vendInvoiceInfoTable.isNonPO() ? vendInvoiceInfoTable.PaymentSched == '' : purchTable.PaymentSched == '';
    enabled = enabled && purchEditLinesForm_Invoice.fixedDueDateAllowEdit();

    paymentInvoice_FixedDueDate.allowEdit(enabled);
    paymentInvoice_FixedDueDate.enabled(enabled);
    paymentInvoice_FixedDueDate.skip(!enabled);

    purchParmTable_FixedDueDate.allowEdit(enabled);
    purchParmTable_FixedDueDate.enabled(enabled);
    purchParmTable_FixedDueDate.skip(!enabled);

    invoiceDetails_FixedDueDate.allowEdit(enabled);
    invoiceDetails_FixedDueDate.enabled(enabled);
    invoiceDetails_FixedDueDate.skip(!enabled);

    element.refreshFixedDueDate();
}

public void refreshFixedDueDate()
{
    vendInvoiceInfoTable_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, editFixedDueDate));
}

public void initBlockVendorPaymentState()
{
    ProjParameters projParameters = ProjParameters::find();
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, SubcontractInvoice)).setValue(projParameters.SubcontractInvoice);
    if(ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled()
        && Dynamics.AX.Application.FeatureStateProvider::IsFeatureEnabled(ProjEnableSubcontractActualsPostingFeature::instance())
        && Dynamics.AX.Application.FeatureStateProvider::IsFeatureEnabled(ProjHoldVendorPaymentFeature::instance()))
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).setValue(projParameters.BlockVendorPayment);
        if(vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, SubcontractInvoice)).getValue() == NoYes::No)
        {
            vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).setValue(NoYes::No);
        }
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).allowEdit(VendInvoiceInfoTable.SubcontractInvoice);
    }
    else
    {
        SubconVendorInvoice_BlockVendorPayment.visible(false);
    }
}

public void updateBlockVendorPaymentState()
{
    if(ProjProjectOperationsIntegrationFeatureHelper::isFeatureEnabled() &&
        Dynamics.AX.Application.FeatureStateProvider::IsFeatureEnabled(ProjEnableSubcontractActualsPostingFeature::instance()))
    {
        ProjParameters projParameters = ProjParameters::find();
        if(projParameters.BlockVendorPayment == NoYes::No)
        {
            vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).setValue(NoYes::No);
        }
        else
        {
            vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).setValue(NoYes::Yes);
        }
    }
}

/// <summary>
/// Enables or disables the budget reservation fields on the vendor invoice form.
/// </summary>
public void enableBudgetReservationLineField()
{
    boolean allowEdit = (vendInvoiceInfoLine.OrigPurchId == '')
        && vendInvoiceInfoLine.BudgetReservationLine_PSN == 0
        && PublicSectorUtils::isBudgetReservationEnabled();

    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, BudgetReservationLine_PSN)).allowEdit(allowEdit);
}

private boolean canProjectBeAssigned()
{
    boolean ret = true;

    // Allow to assign projects on vendor invoice form only for
    // 1. Non-PO scenarios
    // 2. Non-Stocked item with and without procurement category assign to vendor invoice line.
    // It must not be possible to assign a project to pending vendor invoice with procurement category only.
    if (!vendInvoiceInfoLine.isNonPO()
        || (vendInvoiceInfoLine.ProcurementCategory
            && !vendInvoiceInfoLine.ItemId
            && !FeatureStateProvider::isFeatureEnabled(ProjOpsEnableProcurementCategoriesFeature::instance()))
        || vendInvoiceInfoLine.isStocked())
    {
        ret = false;
    }

    return ret;
}

private boolean isAutomaticPrepaymentEnabled()
{
    return (vendInvoiceInfoTable.DocumentOrigin == DocumentOrigin::Service
            && VendAutomateVendorInvoicesFeature::isEnabled()
            && VendParameters.AutomationPrepaymentApplication == NoYes::Yes);
}

private void rereadAfterCalculatingTaxUsingTaxIntegration()
{
    VendInvoiceInfoTable_ds.reread();
    PurchTable_ds.reread();
    VendInvoiceInfoLine_ds.research(true);
}

internal void setAccountingDistributionView(AccountingDistributionView _accountingDistributionView)
{
    if (isTaxServiceForPurchaseEnabled)
    {
        // The method setAccountingDistributionView is called in the method setupDelegates of class AccountingDistributionView to
        // subscribe the events of accounting distribution view. Here subscribes the closed event to rereas the related DS after tax calculation.
        _accountingDistributionView.closedViewEventHandler += eventhandler(this.rereadAfterCalculatingTaxUsingTaxIntegration);
    }
}

