public void setAllowEditRevision_RU()
{
    boolean isRevision = vendInvoiceInfoTableCorrectionType_RU.CorrectionType == FactureCorrectionType_RU::Revision;

    if (isRevision)
    {
        vendInvoiceInfoTable_RU.FactureExternalId_RU = '';
    }

    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, FactureExternalId_RU)).allowEdit(! isRevision);
    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, vatOnPayment_RU)).allowEdit(! isRevision);
}

void setCorrFacture_RU()
{
    FactureJour_RU corrFacture;
    VendInvoiceJour origInvoiceJour;

    if (!purchEditLinesForm_Invoice.showCorrectedFactureExternalId_RU())
    {
        return;
    }

    if ((vendInvoiceInfoTable.vendInvoiceInfoTable_RU().CorrectedInvoiceId_RU && vendInvoiceInfoTable.vendInvoiceInfoTable_RU().CorrectedInvoiceDate_RU)
        || vendInvoiceInfoTable.vendInvoiceInfoTable_RU().CorrectedJournalId)
    {
        origInvoiceJour = vendInvoiceInfoTable.vendInvoiceInfoTable_RU().getOriginalJour();

        if (origInvoiceJour)
        {
            corrFacture = CustVendInvoiceJour::findLastCorrFacture_RU([origInvoiceJour], vendInvoiceInfoTableCorrectionType_RU.CorrectionType);

            if (corrFacture)
            {
                vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU = corrFacture.FactureExternalId;
                vendInvoiceInfoTable_RU.CorrectedFactureDate_RU = corrFacture.FactureDate;
                vendInvoiceInfoTable_ds.initCorrFactureDisplay_RU();

                if (vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU && vendInvoiceInfoTable_RU.CorrectedFactureDate_RU)
                {
                    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedFactureExternalId_RU)).allowEdit(false);
                    vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, CorrectedFactureDate_RU)).allowEdit(false);

                    return;
                }
            }
        }
    }

    vendInvoiceInfoTable_RU_ds.allowEditCorrectedFactureExternalId_RU();
    vendInvoiceInfoTable_ds.refresh();
}

void initVATOnPayment_RU()
{
    VendInvoiceJour vendInvoiceJour = vendInvoiceInfoTable.vendInvoiceInfoTable_RU().getOriginalJour();

    if (vendInvoiceJour)
    {
        vendInvoiceInfoTable_RU.vatOnPayment_RU = vendInvoiceJour.vatOnPayment_RU;
        vendInvoiceInfoTable_RU_ds.refresh();
    }
}

void initCorrFactureDisplay_RU()
{
    FactureJour_RU jour;
    FactureJour_RU jourRev;

    if (! vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU || ! vendInvoiceInfoTable_RU.CorrectedFactureDate_RU)
    {
        lastRevisionNum = 0;
        lastRevisionDate = dateNull();
        return;
    }

    jour = FactureJour_RU::findCorrectedFacture(FactureModule_RU::Vend,
                                                vendInvoiceInfoTable.InvoiceAccount,
                                                vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU,
                                                vendInvoiceInfoTable_RU.CorrectedFactureDate_RU);

    [vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU,
     vendInvoiceInfoTable_RU.CorrectedFactureDate_RU] = jour ?
                                               FactureJour_RU::findLastFacture(jour, vendInvoiceInfoTableCorrectionType_RU.CorrectionType) :
                                               ['', dateNull()];

    if (vendInvoiceInfoTableCorrectionType_RU.CorrectionType == FactureCorrectionType_RU::Correction &&
        jour &&
       (jour.FactureExternalId != vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU ||
        jour.FactureDate != vendInvoiceInfoTable_RU.CorrectedFactureDate_RU))
    {
        warning(strFmt("@SYS4081878", vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU, vendInvoiceInfoTable_RU.CorrectedFactureDate_RU));
        jour = FactureJour_RU::findCorrectedFacture(FactureModule_RU::Vend,
                                                    vendInvoiceInfoTable.InvoiceAccount,
                                                    vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU,
                                                    vendInvoiceInfoTable_RU.CorrectedFactureDate_RU);
    }

    if (jour)
    {
        select firstonly jourRev
            where (jourRev.RefRevisedFacture == jour.RecId ||
                   jourRev.RecId == jour.RecId) &&
                   jourRev.Revised == NoYes::No;

        lastRevisionNum = jourRev.RevisionNum;
        lastRevisionDate = jourRev.CorrectionType == FactureCorrectionType_RU::Revision ?
                              jourRev.FactureDate :
                              dateNull();
    }
    else
    {
        lastRevisionNum = 0;
        lastRevisionDate = dateNull();
    }
}

void initCorrectedInvoiceLines_RU()
{
    VendInvoiceInfoLine::initCorrectedInvoiceLines_RU(vendInvoiceInfoTable);
    vendInvoiceInfoLine_ds.research();

    vendInvoiceInfoTable_ds.initVATOnPayment_RU();
}

/// <summary>
/// Updates budget check result display cache.
/// </summary>
/// <param name="_headerChargesOnly">
/// Indicates whether to only update the header charges budget check result cache; optional.
/// </param>
public void updateBudgetCheckResult(boolean _headerChargesOnly = true)
{
    if (Ledger::isLedgerBudgetControlEnabled(Ledger::current()))
    {
        this.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, displayBudgetCheckResult));
        vendInvoiceInfoTable_ds.refresh();

        if (!_headerChargesOnly)
        {
            vendInvoiceInfoLine_ds.updateBudgetCheckResult();
        }
    }
}

public void refresh()
{
    super();

    varianceApproved.update();
    invoiceDetails_VarianceApproved.update();
}

void markupTransMatchingChanged()
{
    vendInvoiceInfoTable.approveVariance(false, true);
}

/// <summary>
/// Checks for data specific situations when a field value in the <c>VendInvoiceInfoTable</c> Form Data Source changes.
/// </summary>
/// <param name="_fieldId">
/// The field identifier of the field being modified.
/// </param>
/// <param name="_unapprove">
/// true if the approveVariance should be called; otherwise false.
/// </param>
/// <remarks>
/// If the InvoiceAccount value is blank, the retrievePOButton needs to be disabled.
/// </remarks>
void modifiedField(FieldId _fieldId, boolean _unapprove)
{
    NoYes varianceApprovedReset;
    boolean currencyExchChanged;

    if (_unapprove)
    {
        //  Unapprove the invoice, but don't save changes here in case distributions need to be updated
        vendInvoiceInfoTable.approveVariance(false, false);

        if (vendInvoiceInfoTable.VarianceApproved == NoYes::No)
        {
            // If the invoice's varianceApproved was reset, then the expected amounts were refreshed
            varianceApprovedReset = NoYes::Yes;
        }
    }

    switch (_fieldId)
    {
        case fieldNum(VendInvoiceInfoTable, TransDate):
            if (vendInvoiceInfoTable.useDocumentDate() == false)
            {
                currencyExchChanged = true;
                element.checkIfMatchedAndRefreshFromHeader();
                purchFormLetter.transDate(vendInvoiceInfoTable.TransDate);
            }
            break;

        case fieldNum(VendInvoiceInfoTable, DocumentDate):
            if (vendInvoiceInfoTable.useDocumentDate() == true)
            {
                currencyExchChanged = true;
                element.checkIfMatchedAndRefreshFromHeader();
                purchFormLetter.transDate(vendInvoiceInfoTable.DocumentDate);
            }
            break;

        case fieldNum(VendInvoiceInfoTable, CurrencyCode):
            currencyExchChanged = true;
            break;

        case fieldNum(VendInvoiceInfoTable, ExchRate):
            currencyExchChanged = true;
            element.checkIfMatchedAndRefreshFromHeader();
            break;

        case fieldNum(VendInvoiceInfoTable, ExchRateSecondary):
            currencyExchChanged = true;
            break;

        case fieldNum(VendInvoiceInfoTable, euroTriangulation):
            currencyExchChanged = true;
            break;

        case fieldNum(VendInvoiceInfoTable, InvoiceAccount):
            element.enableRetrievePOButton();
            element.enableLineDetailControls(true);
            purchFormLetter.checkW9Received(vendInvoiceInfoTable);
            //Default remittance location for the new vend account
            vendInvoiceInfoTable.RemittanceLocation = VendTable::getDefaultRemittanceLocation(vendInvoiceInfoTable.InvoiceAccount);
            // <GJP>
            if (VendConsInvoiceType_JP::isVendConsInvoiceEnabled())
            {
                vendInvoiceInfoTable_W_DS.refresh();
            }
            // </GJP>
            break;

        case fieldNum(VendInvoiceInfoTable, PaymentSched):
            buttonPaymentSched.enabled(vendInvoiceInfoTable.PaymentSched != '');
            element.enablePaymentInvoiceFixedDueDate();
            break;

        case fieldNum(VendInvoiceInfoTable, Payment):
            element.enablePaymentInvoiceFixedDueDate();
            break;

        case fieldNum(VendInvoiceInfoTable, PaymMode):
            element.enablePaymentFormatButton();
            break;

        case fieldNum(VendInvoiceInfoTable, PurchId):
            purchFormLetter.checkW9Received(vendInvoiceInfoTable);
            break;

        case fieldNum(VendInvoiceInfoTable, PurchName):
        case fieldNum(VendInvoiceInfoTable, Num):
            element.setInvoiceDetailsHeader();
            break;
    }

    if (varianceApprovedReset == NoYes::No && currencyExchChanged == true)
    {
        // If the invoice's varianceApproved was not reset, then the expected amounts need to be refreshed when fields affect the currency/exchange rate
        VendInvoiceInfoSubMarkupMatchingTrans::updateExpectedValues(vendInvoiceInfoTable);
        VendInvoiceMatching::updateExpectedValues(vendInvoiceInfoTable);
    }
}

public void executeQuery()
{
    #ISOCountryRegionCodes

    QueryBuildDataSource vendInvoiceHoldHistoryDS;

    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        vendInvoiceHoldHistoryDS = this.query().dataSourceTable(tableNum(VendInvoiceHoldHistory));
        vendInvoiceHoldHistoryDS.addRange(fieldNum(VendInvoiceHoldHistory, RefTableId_PSN)).value(queryValue(tableNum(VendInvoiceInfoTable)));
        vendInvoiceHoldHistoryDS.addRange(fieldNum(VendInvoiceHoldHistory, InvoicePaymHold_PSN)).value(SysQuery::valueNot(NoYes::No));
        vendInvoiceHoldHistoryDS.addRange(fieldNum(VendInvoiceHoldHistory, HoldReleasedBy_PSN)).value(queryValue(''));
    }

    super();

    if (PublicSectorUtils::isFrenchRegulatoryEnabled())
    {
        paymentDelay_PSN_M_editReasonCode.allowEdit(vendInvoiceHoldHistory.InvoicePaymHold_PSN);
        paymentDelay_PSN_SecurityRole_PSN.allowEdit(vendInvoiceHoldHistory.InvoicePaymHold_PSN);
    }

    element.allowEditsForHeader();
    element.enableMatchingDetailsButtons();
    element.enableRetrievePOButton();
}

public void initValue()
{
    #define.tmpTableRefId('tmp')
    VendTable vendTable;

    super();

    vendInvoiceInfoTable.TableRefId = #tmpTableRefId;
    vendInvoiceInfoTable.CurrencyCode = Ledger::accountingCurrency(CompanyInfo::current());
    vendInvoiceInfoTable.Ordering = documentStatus;
    element.initBlockVendorPaymentState();
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        vendInvoiceInfoTable_RU.PurchBookVATProcessParametersRecId = PurchBookVATProcessParameters_RU::defaultParms().RecId;
        vendInvoiceInfoTable_RU.ProcessVAT = PurchBookVATProcessParameters_RU::defaultParms().ProcessVAT;
    }
    // </GEERU>

    if (purchEditLinesForm_Invoice.parmIsNewInvoiceFromVendor())
    {
        vendTable = purchFormLetter.parmCallerTable() as VendTable;
        vendInvoiceInfoTable.InvoiceAccount = vendTable.AccountNum;
        vendInvoiceInfoTable.OrderAccount = vendTable.AccountNum;
        vendInvoiceInfoTable.ParmId = purchParmUpdate.ParmId;
        element.invoiceAccountModified();

        if (purchFormLetter.parmCallerMenuItem() == menuitemDisplayStr(PurchFormLetter_OneTimeVendInvoice))
        {
            vendInvoiceInfoTable.Payment = vendTable.PaymTermId;
            vendInvoiceInfoTable.PaymentSched = vendTable.PaymSched;
            vendInvoiceInfoTable.PaymId = vendTable.PaymId;
            vendInvoiceInfoTable.PaymMode = vendTable.PaymMode;
            vendInvoiceInfoTable.PaymSpec = vendTable.PaymSpec;
        }

        vendInvoiceInfoTable_ds.write();
        // new invoice from vendor needs to be created only once
        purchEditLinesForm_Invoice.parmIsNewInvoiceFromVendor(false);
    }

    vendorInvoiceInitialized = true;
}

public void init()
{
    super();

    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, checkIfLines));
    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, packingSlipId));
    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, editIsVendorAdvance));
    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, checkIfWorkflowCompletedIcon));
    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, displayBudgetCheckResult));
    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, displayPurchID));
    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, editPurchID));
    vendInvoiceInfoTable_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoTable, editFixedDueDate));

    companyLookup.text(curExt());
}

void enableFixedExchRate()
{
    groupCurrency_M_editReportingCurrencyFixedExchangeRate.visible(reportingCurrencyFixedExchangeRateFeature::isEnabled());
    groupCurrency_M_editReportingCurrencyFixedExchangeRate.enabled(Ledger::isReportingCurrencyDefined());

    if (vendInvoiceInfoTable.FixedExchRate)
    {
        groupCurrency_M_editExchRate.allowEdit(purchEditLinesForm_Invoice.exchRate(vendInvoiceInfoTable));
        groupCurrency_M_editExchRateSecond.allowEdit(purchEditLinesForm_Invoice.exchRateSecondary(vendInvoiceInfoTable));
        groupCurrency_M_editReportingCurrencyFixedExchangeRate.allowEdit(purchEditLinesForm_Invoice.canEditReportingCurrencyExchangeRate(vendInvoiceInfoTable));
    }
    else
    {
        groupCurrency_M_editExchRate.allowEdit(false);
        groupCurrency_M_editExchRateSecond.allowEdit(false);
        groupCurrency_M_editReportingCurrencyFixedExchangeRate.allowEdit(false);
    }

    groupCurrency_M_editExchRate.skip(!groupCurrency_M_editExchRate.allowEdit());
    groupCurrency_M_editExchRateSecond.skip(!groupCurrency_M_editExchRateSecond.allowEdit());
    groupCurrency_M_editReportingCurrencyFixedExchangeRate.skip(!groupCurrency_M_editReportingCurrencyFixedExchangeRate.allowEdit());
}

boolean checkPaymentId(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    boolean ret = true;
    LogisticsAddressCountryRegionISOCode bankValidationCountryRegionISOCode;

    if (!this.paymentIdValidated(false, _vendInvoiceInfoTable, false))
    {
        if (_vendInvoiceInfoTable.isNonPO())
        {
            bankValidationCountryRegionISOCode = Bank::paymIdValidationCountryRegionISOCode(
                                                    LedgerJournalACType::Vend,
                                                    LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(_vendInvoiceInfoTable.InvoiceAccount, LedgerJournalACType::Vend),
                                                    _vendInvoiceInfoTable.VendBankAccountID);
        }
        else
        {
            bankValidationCountryRegionISOCode = Bank::paymIdValidationCountryRegionISOCode(
                                                    LedgerJournalACType::Vend,
                                                    LedgerDynamicAccountHelper::getDynamicAccountFromAccountNumber(_vendInvoiceInfoTable.purchTable().InvoiceAccount, LedgerJournalACType::Vend),
                                                    _vendInvoiceInfoTable.VendBankAccountID);
        }

        ret = Bank::construct(bankValidationCountryRegionISOCode).checkPaymId(_vendInvoiceInfoTable.PaymId);
    }
    return ret;
}

edit boolean paymentIdValidated(boolean set, VendInvoiceInfoTable _vendInvoiceInfoTable, NoYes _validated)
{
    NoYes validated;

    if (_vendInvoiceInfoTable.RecId)
    {
        if (!paymentIdValidatedSet)
        {
            paymentIdValidatedSet = new Set(typeName2Type(extendedTypeStr(RecId)));
        }

        if (set)
        {
            validated = _validated;
            if (_validated)
            {
                paymentIdValidatedSet.add(_vendInvoiceInfoTable.RecId);
            }
            else
            {
                paymentIdValidatedSet.remove(_vendInvoiceInfoTable.RecId);
            }
        }
        else
        {
            validated = paymentIdValidatedSet.in(_vendInvoiceInfoTable.RecId);
        }
    }
    else
    {
        validated = NoYes::No;
    }

    return validated;
}

boolean validateWrite()
{
    boolean ret = super();

    if (!this.checkPaymentId(vendInvoiceInfoTable))
    {
        ret = false;
    }

    copyTaxGroupToMarkupTrans = DialogButton::No;
    if (ret)
    {
        copyTaxGroupToMarkupTrans = vendInvoiceInfoTable.promptCopyTaxGroupToMarkupTrans();
        ret = (copyTaxGroupToMarkupTrans != DialogButton::Cancel);
    }

    if (isTaxServiceForPurchaseEnabled)
    {
        copyOverrideSalesTaxToHeaderMarkupTrans = DialogButton::No;

        if (ret)
        {
            copyOverrideSalesTaxToHeaderMarkupTrans = vendInvoiceInfoTable.promptCopyOverrideSalesTaxToMarkupTrans();
            ret = copyOverrideSalesTaxToHeaderMarkupTrans != DialogButton::Cancel;
        }
    }

    if (ret)
    {
        if (vendInvoiceInfoTable.RecId != 0)
        {
            tableToLineUpdatePrompt = VendInvoiceTableToLineUpdatePrompt::newFromVendInvoiceInfoTable(vendInvoiceInfoTable);
            tableToLineUpdatePrompt.parmCopyTaxGroupToMarkupTrans(copyTaxGroupToMarkupTrans);

            ret = tableToLineUpdatePrompt.prompt();
        }
    }

    // <GEERU>
    if (ret && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        if (purchEditLinesForm_Invoice.showCorrectedFactureExternalId_RU())
        {
            ret = FactureJour_RU::checkCorrectionParameters(vendInvoiceInfoTable_RU.CorrectedFactureExternalId_RU,
                                                            vendInvoiceInfoTable_RU.CorrectedFactureDate_RU,
                                                            vendInvoiceInfoTable_RU.Correct_RU,
                                                            vendInvoiceInfoTableCorrectionType_RU.CorrectionType);
        }
        if (purchEditLinesForm_Invoice.showCorrectedInvoiceId_RU())
        {
            ret = CustVendInvoiceJour::checkCorrectionParameters_RU(vendInvoiceInfoTable_RU.CorrectedInvoiceId_RU,
                                                                    vendInvoiceInfoTable_RU.CorrectedInvoiceDate_RU,
                                                                    vendInvoiceInfoTable_RU.Correct_RU);
        }
    }
    // </GEERU>
    return ret;
}

void write()
{
    try
    {
        if (projSubcontractActualsPostingFeatureEnabled)
        {
            vendInvoiceInfoTable.SubcontractInvoice = SubconVendorInvoice_SubcontractInvoice.checked();
        }

        VendInvoiceInfoTable originalVendInvoiceInfoTable = vendInvoiceInfoTable.orig();

        if ((isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) && vendInvoiceInfoLine_ds.numberOfRowsLoaded() == 0
            && ((purchTable && originalVendInvoiceInfoTable.PurchId != vendInvoiceInfoTable.PurchId) || vendInvoiceInfoTable.PurchAgreementHeader_PSN))
        || (!isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) && vendInvoiceInfoLine_ds.numberOfRowsLoaded() == 0
            && purchTable && originalVendInvoiceInfoTable.PurchId != vendInvoiceInfoTable.PurchId))
        {
            // If the invoice is new/has no lines, try to default them from the PO
            purchFormLetter.showQueryForm(false);
            purchFormLetter.purchTable(purchTable);
            vendInvoiceInfoTable.ParmId = purchParmUpdate.ParmId;
            purchFormLetter.purchParmUpdate(purchParmUpdate.data());
            purchFormLetter.chooseLines(false, vendInvoiceInfoTable, true);

            VendInvoiceInfoTable newVendInvoiceInfoTable = VendInvoiceInfoTable_ds.initializeVendInvoiceInfoTable();

            // Delete the empty original non-PO invoice that was created upon initial save
            if (originalVendInvoiceInfoTable)
            {
                originalVendInvoiceInfoTable.transferDataFromExistingInvoiceToNewInvoice(newVendInvoiceInfoTable);
            }

            vendInvoiceInfoTable.data(newVendInvoiceInfoTable);
            vendInvoiceInfoTable_ds.setCurrent();

            // since the VendinvoiceInfoTable is created in PurchFormLetter, and the extension tables as well, synch
            // the form datasources.
            // The conditions should align with the creation conditions in PurchFormletterParmDataInvoice.defaultHeaderCountryRegionData()
            if (isW || isGB || TaxParameters::isEasternEuropeOrVatDueDateFeatureEnabled())
            {
                vendInvoiceInfoTable_W.data(VendInvoiceInfoTable_W::findByVendInvoiceInfoTable(vendInvoiceInfoTable.RecId));
                vendInvoiceInfoTable_W_ds.setCurrent();
            }

            if (isRU)
            {
                vendInvoiceInfoTable_RU.data(VendInvoiceInfoTable_RU::findByVendInvoiceInfoTable(vendInvoiceInfoTable.RecId));
                vendInvoiceInfoTable_RU_ds.setCurrent();
            }

            if (isEU)
            {
                vendInvoiceInfoTable_Officials.data(VendInvoiceInfoTable_Officials::findByVendInvoiceInfoTable(vendInvoiceInfoTable.RecId));
                vendInvoiceInfoTable_Officials_ds.setCurrent();
            }

            element.chooseTable();

            vendInvoiceInfoTable_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, checkIfLines));
        }
        else
        {
            boolean createSubTable;

            if (!vendInvoiceInfoTable.RecId && vendInvoiceInfoTable.isNonPO())
            {
                // When a non-PO invoice is inserted, a SubTable record needs to be created
                createSubTable = true;
            }

            if (useInvoiceMatching && vendParameters.editAutomaticHeaderMatching())
            {
                // Saving or Posting will update this value, at this point we only want to flag that the value is something other than LastMatchVarianceOptions::Unknown
                vendInvoiceInfoTable.LastMatchVariance = LastMatchVarianceOptions::OK;
            }

            if (createSubTable)
            {
                VendInvoiceInfoSubTable::createFromVendInvoiceInfoTable(vendInvoiceInfoTable, true);
            }

            if ((vendInvoiceInfoTable.PurchId == "") && (VendInvoiceInfoSubTable::count(vendInvoiceInfoTable.ParmId,vendInvoiceInfoTable.TableRefId) == 1))
            {
                VendInvoiceInfoSubTable vendInvoiceInfoSubTableLocal;

                select firstonly OrigPurchId from vendInvoiceInfoSubTableLocal
                    where vendInvoiceInfoSubTableLocal.TableRefId == vendInvoiceInfoTable.TableRefId
                        && vendInvoiceInfoSubTableLocal.ParmId == vendInvoiceInfoTable.ParmId;

                if (vendInvoiceInfoSubTableLocal.OrigPurchId)
                {
                    vendInvoiceInfoTable.PurchId = vendInvoiceInfoSubTableLocal.OrigPurchId;
                    vendInvoiceInfoTable.doUpdate();
                }
            }

            DialogButton copyAddress = DialogButton::No;
            LogisticsPostalAddressRecId originalDeliveryPostalAddress;

            if (vendInvoiceInfoTable.DeliveryPostalAddress != vendInvoiceInfoTable.orig().DeliveryPostalAddress &&
                vendInvoiceInfoTable.isNonPO() && VendInvoiceInfoLine::existNonPO(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId))
            {
                copyAddress = VendInvoiceInfoTable::promptCopyDeliveryAddress();
                originalDeliveryPostalAddress = vendInvoiceInfoTable.orig().DeliveryPostalAddress;
            }

            super();

            if (vendInvoiceInfoTable.FixedExchRate && originalVendInvoiceInfoTable.ExchRate != vendInvoiceInfoTable.ExchRate &&
                vendInvoiceInfoLine.purchLine().inventTable().SalesPriceModelBasic == SalesPriceModelBasic::PurchPrice)
            {
                ProjectAccountingDistribution projectAccountingDistribution;
                AccountingDistribution accountingDistribution;
                VendInvoiceInfoLine vendInvoiceInfoLineLocal;
                VendInvoiceInfoLine_Project vendInvoiceInfoLine_Proj;

                ttsbegin;
                update_recordset vendInvoiceInfoLine_Proj
                    setting ProjSalesPrice = CurrencyExchangeHelper::amountCur2MST(vendInvoiceInfoLine.PurchPrice, vendInvoiceInfoTable.CurrencyCode,
                        vendInvoiceInfoTable.ExchRate, DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), vendInvoiceInfoTable.ExchRateSecondary)
                    exists join vendInvoiceInfoLineLocal
                        where vendInvoiceInfoLineLocal.RecId == vendInvoiceInfoLine_Proj.VendInvoiceInfoLineRefRecId
                        && vendInvoiceInfoLineLocal.TableRefId == vendInvoiceInfoTable.TableRefId
                        && vendInvoiceInfoLineLocal.ParmId == vendInvoiceInfoTable.ParmId;

                update_recordset projectAccountingDistribution
                    setting TransactionCurrencyPrice = vendInvoiceInfoLine_Proj.ProjSalesPrice,
                            TransactionCurrencyAmount = (vendInvoiceInfoLine_Proj.ProjSalesPrice * vendInvoiceInfoLineLocal.ReceiveNow) * accountingDistribution.AllocationFactor
                    join SourceDocumentHeader, SourceDocumentLine, AllocationFactor from accountingDistribution
                        where accountingDistribution.RecId == projectAccountingDistribution.AccountingDistribution
                    join SourceDocumentLine, ReceiveNow from vendInvoiceInfoLineLocal
                        where vendInvoiceInfoLineLocal.SourceDocumentLine == accountingDistribution.SourceDocumentLine
                        && vendInvoiceInfoTable.SourceDocumentHeader == accountingDistribution.SourceDocumentHeader
                        && vendInvoiceInfoTable.TableRefId == vendInvoiceInfoLineLocal.TableRefId
                        && vendInvoiceInfoTable.ParmId == vendInvoiceInfoLineLocal.ParmId
                    join ProjSalesPrice from vendInvoiceInfoLine_Proj
                        where vendInvoiceInfoLine_Proj.VendInvoiceInfoLineRefRecId == vendInvoiceInfoLineLocal.RecId;
                ttscommit;
            }

            // <GTH>
            if (isVendInvoiceInfoTableExtTHEnabled)
            {
                vendInvoiceInfoTableExtensionTH.SysExtensionSerializerExtensionMap::insertAfterBaseTable(vendInvoiceInfoTable);
            }
            // </GTH>

            if (isEU && OfficialsServ_RU::isFeatureEnabled() && !vendInvoiceInfoTable_Officials.OffSessionId)
            {
                purchFormLetter.initOfficials_RU(vendInvoiceInfoTable);

                if (vendInvoiceInfoTable_Officials.OffSessionId)
                {
                    officialsTrans_RU_ds.research();
                }
            }

            if (isW || isGB || VatDueDateFeature::isEnabled())
            {
                vendInvoiceInfoTable_W.SysExtensionSerializerExtensionMap::insertAfterBaseTable(vendInvoiceInfoTable);
            }

            if (isRU)
            {
                vendInvoiceInfoTable_RU.SysExtensionSerializerExtensionMap::insertAfterBaseTable(vendInvoiceInfoTable);
            }

            if (copyAddress != DialogButton::Cancel && copyAddress != DialogButton::No)
            {
                vendInvoiceInfoTable.copyAddressToLine(copyAddress, originalDeliveryPostalAddress);
            }

            if (copyTaxGroupToMarkupTrans == DialogButton::Yes || copyTaxGroupToMarkupTrans == DialogButton::YesToAll)
            {
                vendInvoiceInfoTable.updateMarkupTransTaxGroup(originalVendInvoiceInfoTable.TaxGroup, copyTaxGroupToMarkupTrans);
                copyTaxGroupToMarkupTrans = DialogButton::No;
            }

            if (copyOverrideSalesTaxToHeaderMarkupTrans == DialogButton::Yes)
            {
                vendInvoiceInfoTable.updateMarkupTransOverrideSalesTax(copyOverrideSalesTaxToHeaderMarkupTrans);
                copyOverrideSalesTaxToHeaderMarkupTrans = DialogButton::No;
            }
        }

        if (vendInvoiceInfoTable.TableRefId != originalVendInvoiceInfoTable.TableRefId
            || VendEditInvoiceIsRefreshWithInfoTableUpdatedFlight::instance().isEnabled())
        {
            vendInvoiceInfoTable_ds.research(true);
        }

        element.updateWorkflowIconForHeader();
        element.updateWorkflowIconForLine();

        companyLookup.allowEdit(false);

        // <GBR>
        if (BrazilParameters::isEnabled() && (operationIdChanged || serviceCodeOnDeliveryAddressChanged))
        {
            if (operationIdChanged)
            {
                vendInvoiceInfoTable.updateVendInvoiceInfoLine_BR();
                operationIdChanged = false;
            }

            if (serviceCodeOnDeliveryAddressChanged)
            {
                vendInvoiceInfoTable.updateVendInvoiceInfoLineServiceCode_BR();
                serviceCodeOnDeliveryAddressChanged = false;
            }

            vendInvoiceInfoLine_W_ds.reread();
            vendinvoiceinfoLine_w_ds.rereadReferenceDataSources();
        }
        // </GBR>

        if (tableToLineUpdatePrompt)
        {
            tableToLineUpdatePrompt.runOperation();
            if (tableToLineUpdatePrompt.wasLineUpdated())
            {
                vendInvoiceInfoLine_ds.research(true);
            }
            tableToLineUpdatePrompt = null;
        }

        //checks to see if the release date is null then sets the comments null
        if (vendInvoiceInfoTable.InvoiceReleaseDate == utcDateTimeNull())
        {
            vendInvoiceInfoTable.ReleaseDateComment = "";
        }

        element.checkParmTable();
        editLinesChanged = true;

        this.paymentIdValidated(true, vendInvoiceInfoTable, true);

        element.setLineDetailControls();

        // <GIN>
        if (isIN)
        {
            VendInvoiceInfoTable_IN.SysExtensionSerializerExtensionMap::insertAfterBaseTable(VendInvoiceInfoTable);
        }
        // </GIN>

        reverseCharge.headerDSPostWrite();
    }
    catch (Exception::Error)
    {
        vendInvoiceInfoTable_ds.reread();
        vendInvoiceInfoTable_ds.refresh();
        throw Exception::Error;
    }
}

/// <summary>
/// Initializes a <c>VendInvoiceInfoTable</c> record.
/// </summary>
/// <returns>An instance of a vendor invoice record.</returns>
protected VendInvoiceInfoTable initializeVendInvoiceInfoTable()
{
    return (vendInvoiceInfoTable.data(VendInvoiceInfoTable::findRecId(purchFormLetter.parmLastParmTableRecId())));
}

void delete()
{
    PurchTable emptyPurchTable = null;

    super();

    //If we now no longer have any invoices loaded, the vendInvoiceInfoTable buffer contains stale values, so we need to clear the buffer in
    //order to properly be able to exit the form if necessary.
    if (vendInvoiceInfoTable_ds.numberOfRowsLoaded() == 0)
    {
        vendInvoiceInfoTable.clear();
    }

    element.checkParmTable();
    element.enableMatchingDetailsButtons();
    element.enableHeaderActions();
    element.setHeaderFields();
    element.setInvoiceDetailsHeader();
    element.configurePostButtonState();
    element.updateWorkflowIconForHeader();
    element.updateWorkflowIconForLine();

    purchTable.data(emptyPurchTable);
}

void create(boolean append = false)
{
    append = true;

    if (isSingleInvoiceHeader)
    {
        // Save existing invoice
        element.saveInvoicesAndClose();
        vendInvoiceInfoTable_ds.research(true);
    }

    companyLookup.allowEdit(isSingleInvoiceHeader);

    // reset invoice saved status
    isInvoiceSaved = false;

    super(append);

    element.updateWorkflowIconForHeader();
    element.updateWorkflowIconForLine();

    element.allowEditsForHeader();

    DimensionEntryControlHeader.reactivate();
    if (vendInvoiceInfoTable.TransDate)
    {
        DimensionEntryControlHeader.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
        DimensionEntryControlHeader.parmActiveValueFilterDate(vendInvoiceInfoTable.TransDate);
    }

    //set the default group value on the invoice grouping field and set the focus on that field.
    if (vendParameters.VendInvoiceGroupingEnable == NoYes::Yes)
    {
        vendInvoiceInfoTable.VendInvoiceGroup = vendInvoiceGroupDefaultGroup;
    }
}

int active()
{
    // <GIN>
    boolean isIndiaCountryRegion = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
    // </GIN>

    int ret = super();

    element.setHeaderAttachmentButtonLabel();
    element.updateWorkflowIconForHeader();
    element.updateWorkflowIconForLine();

    if (purchEditLinesForm_Invoice.initInvoiceHeaderState(vendInvoiceInfoTable))
    {
        element.allowEditsForHeader();
        element.allowEditsForLine();
    }

    if (!vendInvoiceInfoTable.isNonPO() && !purchTable)
    {
        purchTable.data(vendInvoiceInfoTable.purchTable());
    }
    element.setHeaderFields();

    DimensionEntryControlHeader.reactivate();

    if (vendInvoiceInfoTable.TransDate)
    {
        DimensionEntryControlHeader.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
        DimensionEntryControlHeader.parmActiveValueFilterDate(vendInvoiceInfoTable.TransDate);
    }

    if (!vendInvoiceInfoTable.isNonPO())
    {
        purchEditLinesForm_Invoice.setPurchTable(purchTable);
    }

    boolean paymentSchedEmpty = vendInvoiceInfoTable.isNonPO()? vendInvoiceInfoTable.PaymentSched == '' : vendInvoiceInfoTable.purchTable().PaymentSched == '';
    boolean isAdvance = (vendInvoiceInfoTable.InvoiceType == PurchInvoiceType::VendorAdvance);
    buttonPaymentSched.enabled(!paymentSchedEmpty && !isAdvance);

    element.enablePaymentFormatButton();

    buttonErrorLog.enabled(vendInvoiceInfoTable.Log != '');

    element.enablePaymentInvoiceFixedDueDate();

    this.enableFixedExchRate();

    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, VendInvoiceSaveStatus)).allowEdit(vendInvoiceInfoTable.VendInvoiceSaveStatus != VendInvoiceSaveStatus::Pending );
    vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, VendInvoiceSaveStatus)).skip( vendInvoiceInfoTable.VendInvoiceSaveStatus == VendInvoiceSaveStatus::Pending );

    element.enablePurchaseOrderLookups();
    element.enableInvoiceAccountLookup();
    element.enablePurchAgreementHeader_PSNLookup();

    element.checkForPrepaymentApplicationFlag();
    element.checkForVendActiveInvoiceFlag(VendActiveInvoiceFlags::SalesTaxAdjustmentsProrated, "@AccountsPayable:SalesTaxAdjustmentsProrated");

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        vendInvoiceInfoTable_RU_ds.allowEditCorrectedFactureExternalId_RU();
        vendInvoiceInfoTable_RU_ds.allowEditCorrectedInvoiceId_RU();
        element.enabledVATTaxAgent_RU();

        vendInvoiceInfoLine_DS.allowCreate((vendInvoiceInfoTable_RU.InventProfileType_RU == InventProfileType_RU::General ||
                                            vendInvoiceInfoTable_RU.InventProfileType_RU == InventProfileType_RU::NotSpecified)
                                           && purchEditLinesForm_Invoice.allowLineCreate());
        vendInvoiceInfoTable_RU_ds.object(fieldNum(VendInvoiceInfoTable_RU, AgreementHeaderExt_RU)).allowEdit(vendInvoiceInfoTable.isNonPO());
        this.setAllowEditRevision_RU();

        this.setCorrFacture_RU();
        this.initCorrFactureDisplay_RU();
    }
    // </GEERU>

    element.enableMatchingDetailsButtons();

    // call to the method to set the Invoice Release Date and Release Comment according to the Approved checkbox
    element.setStoppedReleaseFields();

    if (VendInvoiceInfoSubTable::count(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId) > 1)
    {
        buttonPageParmTableAllocate.enabled(purchEditLinesForm_Invoice.checkInvoiceMiscCharges(vendInvoiceInfoTable) && workflowPartiallyEnabled);
    }
    else
    {
        buttonPageParmTableAllocate.enabled(purchEditLinesForm_Invoice.checkInvoiceMiscCharges(
                                                             VendInvoiceInfoSubTable::find(vendInvoiceInfoTable.ParmId, vendInvoiceInfoTable.TableRefId, vendInvoiceInfoTable.PurchId)) && workflowPartiallyEnabled);
    }

    groupCurrency_M_editExchRate.helpText(strFmt("@SYS78107", vendInvoiceInfoTable.ExchRate));

    // <GIN>
    if (isIndiaCountryRegion)
    {
        purchParmLine_MaximumRetailPrice_IN.allowEdit(!vendInvoiceInfoTable.purchTable().purchTable_W().CustomsImportOrder_IN);
    }
    // </GIN>

    element.configBankLCControls();

    // Disable buttons for posted invoice
    element.enablePerformBudgetCheckButtons(true);

    element.setInvoiceDetailsHeader();

    // <GBR>
    if (isBR)
    {
        vendInvoiceInfoTable.setDatesByFiscalDocumentType_BR();

        if (vendInvoiceInfoTable.isForeignerVendor_BR())
        {
            element.enableDirectImport_BR(true);
        }
        else
        {
            element.enableDirectImport_BR(false);
        }

        vendInvoiceInfoTable_W.enableDataSourceFields_BR();
        element.prepareIssuingScenario_BR();
        buttonAddFiscalReference_BR.enabled(conLen(vendInvoiceInfoTable.getFiscalEstablishment()) <= 1);

        if (vendInvoiceInfoTable.Ordering == DocumentStatus::Invoice)
        {
            carrierInformation_BR.visible(true);
            vehicleInformation_BR.visible(true);
            carrierInformationLine_BR.visible(true);
            vehicleInformationLine_BR.visible(true);

            var isNonPO = vendInvoiceInfoTable.isNonPO();

            carrierInformation_BR.enabled(!isNonPO);
            vehicleInformation_BR.enabled(!isNonPO);
            carrierInformationLine_BR.enabled(!isNonPO);
            vehicleInformationLine_BR.enabled(!isNonPO);
        }

        this.checkICMSSTUpdated_BR();
        ButtonUpdatePresumedTaxesFromNfe_BR.visible(BrazilParameters::find().ICMSPresumedTax == NoYes::Yes);
    }
    else
    {
        element.enableDirectImport_BR(false);
    }
    // </GBR>

    // <GEEU>
    if (countryRegion_LTLV)
    {
        element.initDesign_LT();
    }
    // </GEEU>

    if (isBE)
    {
        element.initDesign_BE();
    }

    element.configurePostButtonState();

    element.checkForDefaultAttachment();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoUS]))
    {
        purchFormLetter.checkW9Received(vendInvoiceInfoTable);
    }

    if (vendInvoiceInfoTable.DocumentOrigin == DocumentOrigin::Service)
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, ReceivedDate)).allowEdit(false);
    }

    this.checkPrepaymentApplication();

    projSubcontractActualsPostingFeatureEnabled = FeatureStateProvider::isFeatureEnabled(ProjEnableSubcontractActualsPostingFeature::instance());
    if (projSubcontractActualsPostingFeatureEnabled && !vendorInvoiceInitialized)
    {
        SubconVendorInvoice_SubcontractInvoice.checked(vendInvoiceInfoTable.SubcontractInvoice);
        this.refresh();
    }

    // The field AccruedSalesTaxAmount depends on the field AccrueSalesTaxType which is defaulted from vendTable.
    // And if there is imported invoice amount or imported sales tax, this field is automatically filled with imported sales tax (no matter it is zero or not)
    // Then the field is not editable. During TCS call, the value will be sent to TCS.
    element.updateVendorChargedSalesTaxControl();
    return ret;
}

/// <summary>
/// Checks if the automatic prepayment application failed but required for the matching and submit to workflow automatically background processes.
/// </summary>
private void checkPrepaymentApplication()
{
    if (VendAutomateVendorInvoicesFeature::isEnabled() &&
        VendInvoiceInfoTable.DocumentOrigin == DocumentOrigin::Service &&
        VendInvoiceInfoTable.InvoiceType != PurchInvoiceType::VendorAdvance &&
        VendInvoiceInfoTable.RequestStatus == VendInvoiceRequestStatus::Draft &&
        VendParameters.AutomationPrepaymentApplication == NoYes::Yes &&
        vendParameters.BlockAutomationWhenPrepaymentApplicationFailed == NoYes::Yes &&
        ! VendInvoiceInfoTable.hasPrepaymentApplicationLines())
    {
        // Check if the most recent prepayment application log entry indicates a failure
        DocumentEventLog mostRecentPrepaymentApplicationEventLog = DocumentEventLog::findMostRecentLogEntryOfType(tableNum(VendInvoiceInfoTable), VendInvoiceInfoTable.RecId, enumNum(VendInvoiceAutomationPrepaymentApplicationEvent));

        if (mostRecentPrepaymentApplicationEventLog.RecId != 0 &&
            mostRecentPrepaymentApplicationEventLog.EventEnumValue == VendInvoiceAutomationPrepaymentApplicationEvent::Failed)
        {
            if (vendParameters.AutomationWorkflowSubmission == NoYes::Yes || vendParameters.AutomationProductReceiptMatching == NoYes::Yes)
            {
                warning("@AccountsPayable:AutomationBlockedAndPrepaymentApplicationFailedWarning");
            }
            else
            {
                warning("@AccountsPayable:AutomationPrepaymentApplicationFailedWarning");
            }
        }
    }
}

/// <summary>
/// This method handles enable/disable of delete button in form based on selected line in grid
/// </summary>
public void markChanged()
{
    boolean allowDelete = headerDeleteEnabled;

    super();

    VendInvoiceInfoTable vendInvoiceInfoTableLocal = vendInvoiceInfoTable_DS.getFirst(1);

    while (vendInvoiceInfoTableLocal)
    {
        allowDelete = purchEditLinesForm_Invoice.invoiceHeaderAllowDelete(vendInvoiceInfoTableLocal);

        if (!allowDelete)
        {
            break;
        }

        vendInvoiceInfoTableLocal = vendInvoiceInfoTable_DS.getNext();
    }

    vendInvoiceInfoTable_DS.allowDelete(allowDelete);
}

private void checkICMSSTUpdated_BR()
{
    FDTaxTransPresumedUncommitted_BR fdTaxTransPresumedUncommitted;
    VendInvoiceInfoLine vendInvoiceInfoLineLocal;

    select firstonly RecId from fdTaxTransPresumedUncommitted
        where fdTaxTransPresumedUncommitted.SourceTableId == tableNum(VendInvoiceInfoLine)
        exists join vendInvoiceInfoLineLocal
            where vendInvoiceInfoLineLocal.RecId == fdTaxTransPresumedUncommitted.SourceRecId64
                && vendInvoiceInfoLineLocal.ParmId == vendInvoiceInfoTable.ParmId
                && vendInvoiceInfoLineLocal.TableRefId == vendInvoiceInfoTable.TableRefId;

    element.setIsICMSTTaxUpdatedFromXml_BR(fdTaxTransPresumedUncommitted.RecId != 0);
}

/// <summary>
/// Determines whether the invoice represented by current <c>VendInvoiceInfoTable</c> table record
/// matches the corresponding purchase order.
/// </summary>
/// <param name="_vendInvoiceInfoTable">
/// <c>VendInvoiceInfoTable</c> table buffer which matches the corresponding purchase order
/// </param>
/// <returns>
/// The LastMatchVarianceOptions::Warning if the invoice has either a quantity or price variance; otherwise, LastMatchVarianceOptions::OK.
/// </returns>
/// <remarks>
/// The check is positive (LastMatchVarianceOptions::OK) if every line of the invoice does not have price or quantity
/// variances; otherwise the check is negative (LastMatchVarianceOptions::Warning).
/// </remarks>
public display LastMatchVarianceOptions checkIfMatchedStatus(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    checkIfMatchedStatusObservableLink.observe();

    return _vendInvoiceInfoTable.checkIfMatchedStatus();
}

