public void performFormLookup(xFormRun _form, FormControl _formControl)
{
    FormDataSource formDataSource;
    Query query;
    QueryBuildDataSource queryDSPurchAgreementTable;
    QueryBuildDataSource queryDSAgreementClassTable;
    QueryBuildDataSource queryDSPurchAgreementHeaderDefaultTable;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        formDataSource = _form.objectSet();
        query = formDataSource.query();
        queryDSPurchAgreementTable = query.dataSourceTable(tableNum(PurchAgreementHeader));
        queryDSAgreementClassTable = queryDSPurchAgreementTable.addDataSource(tableNum(AgreementClassification));
        queryDSAgreementClassTable.relations(true);

        queryDSPurchAgreementHeaderDefaultTable = queryDSPurchAgreementTable.addDataSource(tableNum(PurchAgreementHeaderDefault));
        queryDSPurchAgreementHeaderDefaultTable.relations(true);

        queryDSAgreementClassTable.addRange(fieldNum(AgreementClassification, DirectInvoiceEnable_PSN)).value(SysQuery::value(NoYes::Yes));
        queryDSPurchAgreementTable.addRange(fieldNum(PurchAgreementHeader, AgreementState)).value(SysQuery::value(AgreementState::Effective));

        if (vendInvoiceInfoTable.InvoiceAccount != '')
        {
            queryDSPurchAgreementHeaderDefaultTable.addRange(fieldNum(PurchAgreementHeaderDefault, VendorInvoiceAccount)).value(SysQuery::value(vendInvoiceInfoTable.InvoiceAccount));
        }
    }

    super(_form, _formControl);
}

public void modified()
{
    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        vendInvoiceInfoTable.defaultRow();
        element.setHeaderFields();

        // When the Purchase agreement is entered on an invoice, force a save so the lines are created
        element.saveAndReloadForPurchaseAgreement();
    }
}

void modified()
{
    PurchId origPurchId = vendInvoiceInfoTable.orig().PurchId;

    super();

    // Don't modify when PurchID is invalid.
    if (origPurchId == vendInvoiceInfoTable.PurchId)
    {
        return;
    }

    vendInvoiceInfoTable.Ordering = documentStatus;
    purchTable.data(vendInvoiceInfoTable.purchTable());
    purchTable_ds.refresh();

    vendInvoiceInfoTable.ParmId = purchParmUpdate.ParmId;
    vendInvoiceInfoTable.defaultRow(purchTable, element.purchFormLetter().parmLedgerJournalTrans(), true);

    vendInvoiceInfoTable_ds.paymentIdValidated(true, vendInvoiceInfoTable, false);

    buttonPaymentSched.enabled(vendInvoiceInfoTable.PaymentSched != '');
    element.enablePaymentFormatButton();
    invoiceDetails_IsVendorAdvance.allowEdit(false);
    element.setHeaderFields();

    element.enableHeaderDeliveryAddressFields();

    // <GEERU><GEEU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion(#easternEuropeAllandRU))
    {
        element.initDesign_EEU();
    }
    // </GEERU></GEEU>

    element.configBankLCControls();

    // When the PO is entered on an invoice that is new/no lines, force a save so the lines are created
    element.saveAndReloadNewOrNoLineInvoice();
    element.checkForPrepaymentApplicationFlag();
}

