public boolean validate()
{
    boolean ret = super();

    if (!ret)
    {
        if (DialogButton::Ok == Box::okCancel("@SYS300934", DialogButton::Ok))
        {
            vendInvoiceInfoTable_ds.refreshEx();
        }
        else
        {
            return false;
        }
    }
    return true;
}

