}

public void modified()
{
    super();
    if(vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, SubcontractInvoice)).getValue() == NoYes::No)
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).allowEdit(NoYes::No);
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).setValue(NoYes::No);
    }
    else
    {
        vendInvoiceInfoTable_ds.object(fieldNum(VendInvoiceInfoTable, BlockVendorPayment)).allowEdit(NoYes::Yes);
        element.updateBlockVendorPaymentState();
    }
}

public void modified()
{
    DialogButton dialogButton;
    str strMessage = "@AccountsPayable:InvoiceApprovalDateChangeVoucherChange";
    str strTitle = "@SYS68455";
    boolean updateTransDate;
    boolean isApprovalJournal = vendInvoiceInfoTable.Ordering == DocumentStatus::ApproveJournal;

    if (isApprovalJournal)
    {
        // Default TransDate related fields as normal invoice, since register date is now old
        vendInvoiceInfoTable.Ordering = DocumentStatus::Invoice;

        if (purchFormLetter.parmLedgerJournalTrans() && purchFormLetter.parmLedgerJournalTrans().TransDate == vendInvoiceInfoTable.orig().TransDate)
        {
            //Prompt the user only once when the Transaction date is different from the registered date
            dialogButton = Box::okCancel(strMessage,
                                         DialogButton::Cancel, // Initial focus is on the Cancel button.
                                         strTitle);

            if (dialogButton == DialogButton::Ok)
            {
                updateTransDate = true;
            }
            else
            {
                vendInvoiceInfoTable.TransDate = vendInvoiceInfoTable.orig().TransDate;
            }
        }

    }

    super();

    if (isApprovalJournal)
    {
        // Resetting Ordering to its original value.
        vendInvoiceInfoTable.Ordering = DocumentStatus::ApproveJournal;

        if (updateTransDate)
        {
            purchFormLetter.modifyVoucherDate(vendInvoiceInfoTable.TransDate);
        }
    }

    vendInvoiceInfoTable_ds.enableFixedExchRate();
    vendInvoiceInfoTable_ds.refresh();
    element.refreshFixedDueDate();

    DimensionEntryControlHeader.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
    DimensionEntryControlHeader.parmActiveValueFilterDate(vendInvoiceInfoTable.TransDate);
    DimensionEntryControlLine.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
    DimensionEntryControlLine.parmActiveValueFilterDate(vendInvoiceInfoTable.TransDate);

    // <GEERU>
    if (isRU)
    {
        vendInvoiceInfoLine_ds.research(true);
    }
    // </GEERU>
}

