private boolean writeTax1099BoxDetail_PSN()
{
    boolean newTax1099Detail;
    Tax1099Fields tax1099Fields;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        newTax1099Detail = (tax1099BoxDetail.RecId != 0);

        tax1099Fields = Tax1099Fields::find(vendInvoiceInfoLine.Tax1099Fields);

        if (tax1099Fields.Tax1099Box == Tax1099G::unemploymentCompensation().tax1099Box || tax1099Fields.Tax1099Box == Tax1099G::incomeTaxRefundsCreditsOffsets().tax1099Box || tax1099Fields.Tax1099Box == Tax1099S::grossProceeds().tax1099Box)
        {
            if (!canClose)
            {
                if (tax1099BoxDetail_ds.validateWrite(true))
                {
                    tax1099BoxDetail_ds.write(true);

                    if (!newTax1099Detail)
                    {
                        vendInvoiceInfoLine.Tax1099BoxDetail = tax1099BoxDetail.RecId;
                    }
                }
            }
            else
            {
                return false;
            }
        }
        else if (vendInvoiceInfoLine.Tax1099BoxDetail != 0)
        {
            Tax1099BoxDetail::findRecId(vendInvoiceInfoLine.Tax1099BoxDetail, true).delete();
            vendInvoiceInfoLine.Tax1099BoxDetail = 0;
        }
    }

    return true;
}

private void allowEditVendInvoiceInfoLineField(int _fieldNum, boolean _allowEdit)
{
    vendInvoiceInfoLine_ds.object(_fieldNum).allowEdit(_allowEdit);
    vendInvoiceInfoLine_ds.object(_fieldNum).skip(!_allowEdit);
}

private void allowEditVendInvoiceInfoLineFields(
    boolean _isLastInvoiceLineDirect,
    AgreementLine _agreementLine,
    boolean _isBudgetReservationLine)
{
    isLastInvoiceLineDirect = _isLastInvoiceLineDirect;

    this.allowEditVendInvoiceInfoLineField(
        fieldNum(VendInvoiceInfoLine, PurchUnit),
        _agreementLine.isQuantityBased() && !(_agreementLine.IsMaxEnforced || _agreementLine.IsPriceInformationMandatory));

    this.allowEditVendInvoiceInfoLineField(
        fieldNum(VendInvoiceInfoLine, PriceUnit),
        _agreementLine.isQuantityBased() && !_agreementLine.IsPriceInformationMandatory);

    boolean allowEdit;
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, ProcurementCategory), false);
    }
    else
    {
        this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, ProcurementCategory), allowEdit && !_isBudgetReservationLine);
    }
    // </GBR>

    this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, ItemId), allowEdit);
}

public edit Description editCorrName_RU(boolean _set, VendInvoiceInfoLine _vendInvoiceInfoLine, Description _name)
{
    if (_set && ! _name)
    {
        VendInvoiceInfoLineCorrLink_RU::clearLinks(_vendInvoiceInfoLine.RecId);

        vendInvoiceInfoLineCorrLink_RU_DS.reread();
        vendInvoiceInfoLineCorrLink_RU_DS.active();
    }

    return vendInvoiceTransCorrected_RU.Name;
}

/// <summary>
/// Updates budget check result display cache.
/// </summary>
public void updateBudgetCheckResult()
{
    if (Ledger::isLedgerBudgetControlEnabled(Ledger::current()))
    {
        // One invoice line update potentially triggers other line budget updates because the stale tax amounts need to be deleted.
        vendInvoiceInfoLine_ds.research(true);
    }
}

void changedInventoriedStatus()
{
    // Update inventDim with new data which has been set due to modification
    inventDim.data(InventDim::find(vendInvoiceInfoLine.InventDimId));
    vendInvoiceInfoLine.modifyInventDim(inventDim, fieldNum(InventDim,InventLocationId));

    // Update inventdim fields
    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ProcurementCategory)).allowEdit(false);
    }
    else
    {
        // </GBR>
        // Disable category editing if itemid is selected
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ProcurementCategory)).allowEdit(vendInvoiceInfoLine.ItemId == '');
        // <GBR>
    }
    // </GBR>

    // Disable itemid editing if only a category is selected
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ItemId)).allowEdit(!vendInvoiceInfoLine.isCategoryBased());

    // <GEERU>
    element.enableForeignTrade_RU();
    // </GEERU>

    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, editPurchUnit));
    vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, editName));
}

/// <summary>
/// Provide access to line fields based on PO/non-PO and item/category settings.
/// </summary>
public void setLineFields()
{
    boolean isNonPOLine = vendInvoiceInfoLine.isNonPO();
    boolean hasRetentionTerms;
    PurchLine purchLineLocal;
    boolean isCategoryBased = vendInvoiceInfoLine.isCategoryBased();
    boolean allowEditInventoryQtys;
    boolean isAdvance = (vendInvoiceInfoTable.InvoiceType == PurchInvoiceType::VendorAdvance);
    VendPackingSlipTrans vendPackingSlipTrans;

    boolean isDirectInvoice = vendInvoiceInfoLine.isDirectInvoice();
    boolean allowEdit;
    AgreementLine agreementLine;

    boolean isBudgetReservationLine = vendInvoiceInfoLine.isBudgetReservationLineReferenced();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (isLastInvoiceLineDirect && !isDirectInvoice)
        {
            isLastInvoiceLineDirect = !isNonPOLine;
        }
    }

    if (previousNonPOLine != isNonPOLine)
    {
        previousNonPOLine = isNonPOLine;

        vendInvoiceInfoLine_PurchUnit.allowEdit(isNonPOLine);
        vendInvoiceInfoLine_PurchUnit.skip(!isNonPOLine);
        invoiceLineDetails_PurchUnit.allowEdit(isNonPOLine);
        invoiceLineDetails_PurchUnit.skip(!isNonPOLine);

        orderLine_Name.allowEdit(isNonPOLine);

        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfter)).allowEdit(!isNonPOLine);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfter)).skip(isNonPOLine);

        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, MultiLnDisc)).allowEdit(!isNonPOLine);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, MultiLnDisc)).skip(isNonPOLine);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, MultiLnPercent)).allowEdit(!isNonPOLine);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, MultiLnPercent)).skip(isNonPOLine);

        // Hide/show the non-PO fields
        foreignTrade.visible(isNonPOLine);
        foreignTradeCategory.visible(isNonPOLine);
        measurement.visible(isNonPOLine);

        // Show/hide the PO fields
        purchForeignTrade.visible(!isNonPOLine);
        purchForeignTradeCategory.visible(!isNonPOLine);
        purchMeasurement.visible(!isNonPOLine);

        // Call pageActivated on the foreign trade tab if it is already active to ensure that
        // it redraws correctly
        if (tabPageForeignTradeLine.isActivePage())
        {
            tabPageForeignTradeLine.pageActivated();
        }

        //Set visibility for financial dimension controls
        GroupFinancialDimensionLinePurch.visible(!isNonPOLine && !isAdvance);
        GroupFinancialDimensionLine.visible(isNonPOLine || isAdvance);
        GroupPurchDistTemplateLine.visible(!isNonPOLine);
        GroupDistributionTemplateLine.visible(isNonPOLine);
    }

    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ReceiveNow)).allowEdit (isAdvance? false: purchEditLinesForm_Invoice.editReceiveNow());
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, Closed)).allowEdit(!(isAdvance ||isNonPOLine));
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, Closed)).skip(isAdvance || isNonPOLine);

    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, PurchMarkup)).allowEdit(!isNonPOLine || !isCategoryBased);
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, PurchMarkup)).skip(isNonPOLine && isCategoryBased);
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ItemId)).allowEdit(!isCategoryBased);

    // Enable/disable inventory quantities
    if (isNonPOLine)
    {
        allowEditInventoryQtys = false;
    }
    else
    {
        purchLineLocal = vendInvoiceInfoLine.purchLine();

        // <GTE> <GIN>
        // Copy TransTaxInformation when post current purchase order with adding another purchase order
        if ((TaxSolutionScopeIntegrationUtil::isCompanyEnabled() || SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN])
            && purchLineLocal.PurchId != vendInvoiceInfoTable.PurchId))
        {
            TransTaxInformationHelper::copyTransTaxInformation(purchLineLocal, vendInvoiceInfoLine);
        }
        // </GTE> </GIN>

        allowEditInventoryQtys = purchLineLocal.isStocked();
        // When vendor retention terms are non-blank in purchase order line, retention fields are shown and may be input.
        if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
        {
            hasRetentionTerms = purchLineLocal.hasRetentionTerms();
        }
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(Project)))
    {
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, psaRetainagePercent)).visible(hasRetentionTerms);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, psaRetainageAmount)).visible(hasRetentionTerms);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, psaRetainageBalance)).visible(hasRetentionTerms);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, psaIsFinal)).visible(hasRetentionTerms);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, psaReleasePercent)).visible(hasRetentionTerms);
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, psaReleaseAmount)).visible(hasRetentionTerms);
    }

    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainBeforeInvent)).allowEdit(allowEditInventoryQtys);
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainBeforeInvent)).skip(!allowEditInventoryQtys);
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfterInvent)).allowEdit(allowEditInventoryQtys);
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, RemainAfterInvent)).skip(!allowEditInventoryQtys);

    // If there is a VendInvoiceInfoSubLine, don't allow edit
    if (allowEditInventoryQtys)
    {
        if (vendInvoiceInfoSubLine_ds.getFirst())
        {
            allowEditInventoryQtys = false;
        }
        else // Subline existence takes precedence, we shouldn't run if that is true.
        {
            // Check if this purchline has a packing slip record, do not allow edit if it exists, as PS update was the chance to enter the corrected invent qty.
            select firstonly RecId from vendPackingSlipTrans where vendPackingSlipTrans.InventTransId == vendInvoiceInfoLine.purchLine().InventTransId;
            allowEditInventoryQtys = vendPackingSlipTrans.RecId ? false : true;
        }
    }

    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, InventNow)).allowEdit(allowEditInventoryQtys);
    vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, InventNow)).skip(!allowEditInventoryQtys);

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        tabPageFixedAsset_RU.visible(purchEditLinesForm_Invoice.showAsset_RU() && (isNonPOLine || RAssetPurchTrans::exist(vendInvoiceInfoLine)));

        if (tabPageFixedAsset_RU.visible() && ! isNonPOLine)
        {
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ReceiveNow)).allowEdit(false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ReceiveNow)).skip(true);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, InventNow)).allowEdit (false);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, InventNow)).skip(true);
        }
        element.allowEditsRAsset();
        element.enableForeignTrade_RU();
        vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, CountryRegionName_RU)).enabled(
            vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, InvoiceGTDId_RU)).enabled(
                vendInvoiceInfoLine.isCategoryBased()
                ||
                !vendInvoiceInfoLine.inventTable().isStocked()
            )
        );
        vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, MarkupCode_RU)).enabled(isNonPOLine && vendInvoiceInfoLine.isMarkupCodeEnabled_RU());
    }
    // </GEERU>

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (isDirectInvoice)
        {
            agreementLine = AgreementLine::find(vendInvoiceInfoLine.AgreementLine_PSN);

            if (agreementLine)
            {
                // Invoice line has an agreement connected
                // Editability from the agreement
                this.allowEditVendInvoiceInfoLineFields(
                    isDirectInvoice,
                    agreementLine,
                    isBudgetReservationLine);
            }
            else
            {
                if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
                {
                    // Invoice line does not have an agreement
                    // Feature is enabled
                    // Allow edit
                    allowEdit = false;
                    // <GBR>
                    if (BrazilParameters::isEnabled())
                    {
                        this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, ProcurementCategory), false);
                    }
                    else
                    {
                        this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, ProcurementCategory), allowEdit && !isBudgetReservationLine);
                    }
                    // </GBR>

                    allowEdit = true;
                    this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, PriceUnit), allowEdit);

                    this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, PurchPrice), allowEdit);
                }
                else
                {
                    this.allowEditVendInvoiceInfoLineFields(
                    isDirectInvoice,
                    agreementLine,
                    isBudgetReservationLine);
                }
            }
        }
        else
        {
            allowEdit = true;

            this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, PriceUnit), allowEdit);

            this.allowEditVendInvoiceInfoLineField(fieldNum(VendInvoiceInfoLine, PurchPrice), allowEdit);

            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ProcurementCategory)).allowEdit(allowEdit && !isBudgetReservationLine);
            vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ProcurementCategory)).skip(!allowEdit);
        }
    }

    isLastLinePrepaymentApplication = (isLinePrepaymentApplication && vendInvoiceInfoLine.LineType != PurchInvoiceLineType::AdvanceApplication);

    isLinePrepaymentApplication = (vendInvoiceInfoLine.LineType == PurchInvoiceLineType::AdvanceApplication);
}

public void create(boolean _append = true)
{
    super(_append);

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ProcurementCategory)).allowEdit(false);
    }
    else
    {
        // </GBR>
        vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ProcurementCategory)).allowEdit(true);
        // <GBR>
    }
    // </GBR>

    //When creating a new invoice line, the Item number field should have focus if it is in an editable state,
    //if not, we need to set focus on the Procurement category field if it is in the proper editable state.
    if (purchParmLine_ItemId.isVisible() && purchParmLine_ItemId.enabled() && vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ItemId)).allowEdit())
    {
        purchParmLine_ItemId.setFocus();
    }
    else if (purchParmLine_ProcurementCategory.isVisible() && purchParmLine_ProcurementCategory.enabled() && vendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, ProcurementCategory)).allowEdit())
    {
        purchParmLine_ProcurementCategory.setFocus();
    }
}

public display LastMatchVarianceOptions extPriceVarianceStatus(VendInvoiceInfoLine _vendInvoiceInfoLine)
{
    return _vendInvoiceInfoLine.extendedPriceVarianceStatus(vendInvoiceInfoTable);
}

void markupTransMatchingChanged()
{
    vendInvoiceInfoTable.approveVariance(false, true);
}

void modifiedField(FieldId _fieldId, boolean _unapprove, boolean _isQtyUpdated = true, boolean _saveLine = true)
{
    // Unapprove the invoice if an existing line was modified
    if (_unapprove && vendInvoiceInfoLine.RecId)
    {
        if (useInvoiceMatching && _saveLine)
        {
            // Totals matching needs the line's changes in the database to recalculate
            if (isBR || isIN || isRU || isHU || isMX)
            {
                VendInvoiceInfoLine_W.write();
            }
            this.write();
        }

        vendInvoiceInfoTable.approveVariance(false, true, _isQtyUpdated);

        element.checkIfMatchedAndRefresh();

        varianceApproved.update();
    }

    switch (_fieldId)
    {
        case fieldNum(VendInvoiceInfoLine, ItemId):
            vendInvoiceInfoLine_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoLine, itemName));
            break;

        case fieldNum(VendInvoiceInfoLine, LineAmount):
            if (vendInvoiceInfoLine_Project)
            {
                vendInvoiceInfoLine_Project.setTransferPrice();
            }
            break;

        case fieldNum(VendInvoiceInfoLine, PurchPrice):
            if (vendInvoiceInfoLine_Project)
            {
                vendInvoiceInfoLine_Project.setTransferPrice(vendInvoiceInfoLine);
            }
            break;

        case fieldNum(VendInvoiceInfoLine, ProcurementCategory):
            if (vendInvoiceInfoLine_Project && vendInvoiceInfoLine_Project.ProjId)
            {
                VendInvoiceInfoLine_Project.setProjCategoryId(vendInvoiceInfoLine);
            }
            break;
    }
}

public void executeQuery()
{
    super();
    element.enableMatchingDetailsButtons();
}

public void init()
{
    super();

    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, packingSlipId));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, deliveryDate));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, checkIfQuantityMatch));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, priceVarianceStatus));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, editName));
    vendInvoiceInfoLine_ds.cacheAddMethod('extPriceVarianceIcon');
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, displayBudgetCheckResult));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, editPurchUnit));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, itemName));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, qualityOrderStatusDisplay));

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        this.setVisible_IN();
    }
    // </GIN>

    TransTaxInformation.visible(TaxIntegrationUtils::isTaxInformationEnabled());
}

void write()
{
    VendInvoiceInfoLine originalVendInvoiceInfoLine = vendInvoiceInfoLine.orig();
    NumberSeq numberSeq;

    try
    {
        // When a non-PO line is inserted, check to see if a SubTable record needs to be created
        if (vendInvoiceInfoLine.RecId == 0
            && vendInvoiceInfoLine.isNonPO()
            && !VendInvoiceInfoSubTable::exist(vendInvoiceInfoLine.ParmId, vendInvoiceInfoLine.TableRefId, ''))
        {
            VendInvoiceInfoSubTable::createFromVendInvoiceInfoLine(vendInvoiceInfoLine, vendInvoiceInfoLine.ParmId, vendInvoiceInfoTable.PurchName, true);
            vendInvoiceInfoSubTable_ds.research();
        }

        copyTaxGroupToMarkupTrans = DialogButton::No;

        if (vendInvoiceInfoLine && vendInvoiceInfoLine.TaxGroup != vendInvoiceInfoLine.orig().TaxGroup)
        {
            // This codepath may require user interaction. The dialog should be outside of transaction scope.
            Debug::assert(appl.ttsLevel() == 0);

            copyTaxGroupToMarkupTrans = MarkupTrans::promptCopyTaxGroupToMarkupTrans(vendInvoiceInfoLine.TableId, vendInvoiceInfoLine.RecId);
        }

        if (copyTaxGroupToMarkupTrans == DialogButton::Cancel)
        {
            return;
        }

        if (isTaxServiceForPurchaseEnabled)
        {
            copyOverrideSalesTaxToLineMarkupTrans = DialogButton::No;

            if (vendInvoiceInfoLine && vendInvoiceInfoLine.OverrideSalesTax != vendInvoiceInfoLine.orig().OverrideSalesTax)
            {
                // This codepath may require user interaction. The dialog should be outside of transaction scope.
                Debug::assert(appl.ttsLevel() == 0);

                copyOverrideSalesTaxToLineMarkupTrans = MarkupTrans::promptCopyOverrideSalesTaxToMarkupTrans(vendInvoiceInfoLine.TableId, vendInvoiceInfoLine.RecId, false);
            }

            if (copyOverrideSalesTaxToLineMarkupTrans == DialogButton::Cancel)
            {
                return;
            }
        }

        reverseCharge.lineDSPreWrite();

        ttsbegin;

        // Must make sure we write tax1099Detail only when we are writing VendInvoiceInfoLine
        // to maintain relationship
        if (!this.writeTax1099BoxDetail_PSN())
        {
            ttsabort;
            return;
        }

        if (vendInvoiceInfoLine.isNonPO() && vendInvoiceInfoLine_Project.ProjId && !vendInvoiceInfoLine.InventTransId)
        {
            // InventTransId is taken from purchLine for PO lines, but is needed by project for all invoice lines
            numberSeq = NumberSeq::newGetNum(InventParameters::numRefInventTransId());
            vendInvoiceInfoLine.InventTransId = numberSeq.num();
        }

        vendInvoiceInfoLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

        super();

        if (isTaxServiceForPurchaseEnabled)
        {
            element.rereadAfterCalculatingTaxUsingTaxIntegration();
        }

        if (isW)
        {
            vendInvoiceInfoLine_W.SysExtensionSerializerExtensionMap::insertAfterBaseTable(vendInvoiceInfoLine);
        }

        if (isBR)
        {
            vendInvoiceInfoLine_BR.SysExtensionSerializerExtensionMap::insertAfterBaseTable(vendInvoiceInfoLine);
        }

        if (copyTaxGroupToMarkupTrans == DialogButton::Yes || copyTaxGroupToMarkupTrans == DialogButton::YesToAll)
        {
            MarkupTrans::updateTaxGroup(
                vendInvoiceInfoLine.TableId,
                vendInvoiceInfoLine.RecId,
                vendInvoiceInfoLine.TaxGroup,
                originalVendInvoiceInfoLine.TaxGroup,
                copyTaxGroupToMarkupTrans);

            copyTaxGroupToMarkupTrans = DialogButton::No;
        }

        if (copyOverrideSalesTaxToLineMarkupTrans == DialogButton::Yes)
        {
            MarkupTrans::updateOverrideSalesTax(
                vendInvoiceInfoLine.TableId,
                vendInvoiceInfoLine.RecId,
                vendInvoiceInfoLine.OverrideSalesTax,
                copyOverrideSalesTaxToLineMarkupTrans);

            copyOverrideSalesTaxToLineMarkupTrans = DialogButton::No;
        }

        element.enableMatchingDetailsButtons();

        editLinesChanged = true;

        if (vendInvoiceInfoLine.isNonPO() && vendInvoiceInfoLine.isCategoryBased() && !vendInvoiceLineForeignTradeCategory.VendInvoiceInfoLine)
        {
            vendInvoiceLineForeignTradeCategory.VendInvoiceInfoLine = vendInvoiceInfoLine.RecId;
            vendInvoiceLineForeignTradeCategory_ds.write();
        }

        if (vendInvoiceInfoLine.isNonPO() && !vendInvoiceInfoLine_Project.RecId && vendInvoiceInfoLine_Project.ProjId)
        {
            vendInvoiceInfoLine_Project.VendInvoiceInfoLineRefRecId = vendInvoiceInfoLine.RecId;

            if (vendInvoiceInfoLine_Project_ds.validateWrite())
            {
                vendInvoiceInfoLine_Project_ds.write();
            }
            else
            {
                throw Exception::Error;
            }
        }

        if (vendInvoiceInfoLine.InventDimId != inventDim.InventDimId)
        {
            inventDim.data(InventDim::find(vendInvoiceInfoLine.InventDimId));
            inventDim_ds.setCurrent();
        }

        if (originalVendInvoiceInfoLine.RecId == 0)
        {
            vendInvoiceInfoTable_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, checkIfLines));

            if (vendParameters.UseTotalPriceMatching == NoYes::Yes || vendParameters.UseMiscChargeMatching == NoYes::Yes)
            {
                // Unapprove the invoice
                vendInvoiceInfoTable.approveVariance(false, true);

                element.checkIfMatchedAndRefresh();

                varianceApproved.update();
            }
        }

        // <GIN>
        if (element.isNeeded())
        {
            if (!vendInvoiceInfoLineTaxExtension_IN.VendInvoiceInfoLine)
            {
                vendInvoiceInfoLineTaxExtension_IN.initForNonPO(vendInvoiceInfoLine);
                if (vendInvoiceInfoLineTaxExtension_IN_DS.validateWrite())
                {
                    vendInvoiceInfoLineTaxExtension_IN_DS.write();
                    vendInvoiceInfoLineTaxExtension_IN_DS.executeQuery();
                }
            }
        }
        // </GIN>

        if (originalVendInvoiceInfoLine.RecId == 0 || vendInvoiceInfoLine.distributionUpdateNeeded(originalVendInvoiceInfoLine))
        {
            vendInvoiceInfoLine_ds.updateBudgetCheckResult();
        }

        ttscommit;

        element.updateWorkflowIconForHeader();
        element.updateWorkflowIconForLine();

        reverseCharge.lineDSPostWrite();

        element.enablePurchaseOrderLookups();
        element.enableInvoiceAccountLookup();
        element.enablePurchAgreementHeader_PSNLookup();
    }
    catch (Exception::Error)
    {
        vendInvoiceInfoLine_ds.reread();
        vendInvoiceInfoLine_ds.refresh();
    }
}

void delete()
{
    // <GIN>
    VendDocumentLineMap vendDocumentLineMap;
    boolean needRefresh = false;
    // </GIN>

    Tax1099RecId tax1099RecId;

    PurchId purchId = vendInvoiceInfoLine.OrigPurchId;
    ParmId parmId = vendInvoiceInfoLine.ParmId;

    // <GIN>
    RefRecId purchLineRecId = vendInvoiceInfoLine.PurchLineRecId;
    TradeLineRefId tradeLineRefId = vendInvoiceInfoLine.TableRefId;
    // </GIN>

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        tax1099RecId = vendInvoiceInfoLine.Tax1099BoxDetail;
    }

    RecId origRecId = vendInvoiceInfoLine.RecId;

    reverseCharge.lineDSPreDelete();

    super();

    // <GIN>
    if (vendInvoiceInfoTable && VendDocumentLineMap::isTransactionRelatedToBOE_IN(vendInvoiceInfoTable.purchTable(), purchParmUpdate))
    {
        vendDocumentLineMap.updateLinkedParmline_IN(vendInvoiceInfoTable.Ordering, parmId, purchLineRecId, tradeLineRefId, purchParmUpdate.CreditRemaining, false);
        needRefresh = true;
    }
    // </GIN>
    if (purchId != '')
    {
        VendInvoiceInfoSubTable::deleteIfNoLines(purchId, parmId, tradeLineRefId);
        vendInvoiceInfoTable_ds.research(true);
    }
    if (VendInvoiceEditPurchaseAgreementFromPendingVendorInvoiceFlight::instance().isEnabled()
        && !vendInvoiceInfoTable.existLine()
        && vendInvoiceInfoTable.isNonPO()
        && vendInvoiceInfoTable.PurchAgreementHeader_PSN)
    {
        vendInvoiceInfoTable.removePurchAgreementReferenceFromInvoice();
        vendInvoiceInfoTable_ds.research(true);
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        ttsbegin;
        Tax1099BoxDetail::findRecId(tax1099RecId,true).delete();
        ttscommit;
    }

    vendInvoiceInfoTable_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, checkIfLines));
    vendInvoiceInfoTable_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, packingSlipId));
    vendInvoiceInfoTable_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, displayPurchID));
    vendInvoiceInfoTable_ds.cacheCalculateMethod(tableMethodStr(VendInvoiceInfoTable, editPurchID));

    element.enableMatchingDetailsButtons();

    if (origRecId != 0 && vendParameters.editUseInvoiceMatching() == NoYes::Yes)
    {
        // Unapprove the invoice when the last line is deleted
        if (vendInvoiceInfoLine_ds.recordsMarked().lastIndex() == 0)
        {
            vendInvoiceInfoTable.approveVariance(false, true);

            element.checkIfMatchedAndRefresh();

            varianceApproved.update();
        }
    }

    vendInvoiceInfoSubTable_ds.research();

    element.updateWorkflowIconForHeader();
    element.updateWorkflowIconForLine();

    element.enablePurchaseOrderLookups();
    element.enableInvoiceAccountLookup();
    element.enablePurchAgreementHeader_PSNLookup();
    // <GBR>
    if (isBR)
    {
        buttonAddFiscalReference_BR.enabled(conLen(vendInvoiceInfoTable.getFiscalEstablishment()) <= 1);
    }
    // </GBR>

    // <GIN>
    if (needRefresh)
    {
        vendInvoiceInfoLine_ds.research(true);
    }
    // </GIN>
}

int active()
{
    int ret = super();

    element.setLineAttachmentButtonLabel();

    vendInvoiceInfoLine_ds.setLineFields();

    DimensionEntryControlHeader.reactivate();

    if (vendInvoiceInfoTable.TransDate)
    {
        DimensionEntryControlLine.parmNonActiveValueErrorTolerance(ErrorTolerance::Warning);
        DimensionEntryControlLine.parmActiveValueFilterDate(vendInvoiceInfoTable.TransDate);
    }

    // Update inventdim fields
    element.updateDesign(InventDimFormDesignUpdate::Active);

    element.enableLineDeliveryAddressFields();

    element.updateWorkflowIconForLine();

    if (purchEditLinesForm_Invoice.initInvoiceLineState(vendInvoiceInfoLine) || isLinePrepaymentApplication || isLastLinePrepaymentApplication)
    {
        element.allowEditsForLine();
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        element.setAllowEdit1099GFields();
        element.setAllowEdit1099SFields();

        if (PublicSectorUtils::isFrenchRegulatoryEnabled())
        {
            vendDirective_PSN_ds.executeQuery();
        }

        if (vendInvoiceInfoLine.isCategoryBased() && vendInvoiceInfoLine.isDirectInvoice())
        {
            vendDirective_PSN_ds.executeQuery();

            if (vendInvoiceInfoLine.AgreementLine_PSN)
            {
                element.enableAgreementButtons(true, true, true);
            }
            else
            {
                element.enableAgreementButtons(false, true, false);
            }
        }
    }
    else
    {
        element.enableAgreementButtons(false, false, false);
    }

    element.enablePerformBudgetCheckButtons(false);

    vendInvoiceInfoLine_Project_ds.setAllowEdit();

    // <GBR>
    if (isBR)
    {
        element.enableServiceFields_BR();
        vendInvoiceInfoLine_W_ds.object(fieldNum(VendInvoiceInfoLine_W, diAddition_BR)).visible(vendInvoiceInfoTable.isForeignerVendor_BR());
    }
    // </GBR>

    if (isMX)
    {
        element.enableCFDIWithholding_MX();
    }

    element.enableBudgetReservationLineField();

    if (isTaxServiceForPurchaseEnabled)
    {
        VendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, TaxGroup)).allowEdit(vendInvoiceInfoLine.OverrideSalesTax == NoYes::Yes);
        VendInvoiceInfoLine_ds.object(fieldNum(VendInvoiceInfoLine, TaxItemGroup)).allowEdit(vendInvoiceInfoLine.OverrideSalesTax == NoYes::Yes);
    }

    return ret;
}

display NoYes backOrder(VendInvoiceInfoLine _vendInvoiceInfoLine)
{
    if (!_vendInvoiceInfoLine.isNonPO() && -InventTrans::backOrderQtyIssue(_vendInvoiceInfoLine.ItemId, _vendInvoiceInfoLine.InventDimId) > 0)
    {
        return NoYes::Yes;
    }

    return NoYes::No;
}

void linkActive()
{
    #DEFINE.NOTPOSITIONED(-1)
    int pos;

    if (searchParmLine)
    {
        pos = vendInvoiceInfoLine_ds.getPosition();
        super();
        if (pos != #NOTPOSITIONED)
        {
            vendInvoiceInfoLine_ds.setPosition(pos);
        }
    }
}

private void setVisible_IN()
{
    boolean isTaxParametersMarked;
    #ISOCountryRegionCodes

    isTaxParametersMarked = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]) && TaxParameters::checkTaxParameters_IN();

    vendInvoiceInfoLine_AssessableValue_IN.visible(isTaxParametersMarked);
    vendInvoiceInfoLine_MaximumRetailPrice_IN.visible(isTaxParametersMarked);
}

/// <summary>
/// This method is used to display the workflow icon on line if there are active workitems for the current user.
/// </summary>
/// <param name="_vendInvoiceInfoLine">
/// The vendor invoice line for which this icon should be displayed.
/// </param>
public display container displayWorkflowLineIcon(vendInvoiceInfoLine _vendInvoiceInfoLine)
{
    ImageReference imgRef;
    workflowIconObservableLink.observe();

    if (Workflow::findWorkItemsForCurrentUser(_vendInvoiceInfoLine).elements())
    {
        imgRef = ImageReference::constructForSymbol(ImageReferenceSymbol::Workflow);
    }
    else
    {
        imgRef = ImageReference::constructForSymbol('');
    }

    return imgRef.pack();
}

