    lookup.parmInventLocationId(vendInvoiceInfoLine.inventDim().InventLocationId);

    lookup.addRoleByType(LogisticsLocationRoleType::Delivery);
    lookup.parmReturnLocation(false);

    ret = lookup.performFormLookup();
    return ret;
}

