public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    RecId purchAgreementHeaderRecid;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        purchAgreementHeaderRecid = vendInvoiceInfoTable.PurchAgreementHeader_PSN;

        if (!purchAgreementHeaderRecid && purchTable)
        {
            purchAgreementHeaderRecid = purchTable.MatchingAgreement;
        }
    }

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)) && purchAgreementHeaderRecid)
    {
        return EcoResCategory::lookupCategoryHierarchyRole(
            _formReferenceControl,
            EcoResCategoryNamedHierarchyRole::Procurement,
            false, true, true, null, 0, 0, 0, purchAgreementHeaderRecid);
    }
    else
    {
        return EcoResCategory::lookupCategoryHierarchyRole(_formReferenceControl, EcoResCategoryNamedHierarchyRole::Procurement);
    }
}

public void modified()
{
    // Note: If the category has been modified, it is a non-PO line

    super();

    if (isFinTagConfigurationSetForCurrent
        && PurchaseOrderFinTagFeature::isEnabled() && vendInvoiceInfoTable.FinTag)
    {
        vendInvoiceInfoLine.FinTag = vendInvoiceInfoTable.FinTag;
    }

    vendInvoiceInfoLine_ds.changedInventoriedStatus();
}

public boolean validate()
{
    boolean ret;
    EcoResCategory ecoResCategory;
    EcoResCategory ecoResCategoryChildren;
    AgreementLine agreementLineLocal;

    RecId purchAgreementHeaderRecid;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        purchAgreementHeaderRecid = vendInvoiceInfoTable.PurchAgreementHeader_PSN;

        if (!purchAgreementHeaderRecid && purchTable)
        {
            purchAgreementHeaderRecid = purchTable.MatchingAgreement;
        }
    }

    if (purchAgreementHeaderRecid)
    {
        select firstonly RecId from ecoResCategory
            where ecoResCategory.RecId == vendInvoiceInfoLine.ProcurementCategory
                join Category from agreementLineLocal
                where ecoResCategory.RecId == agreementLineLocal.Category
                && agreementLineLocal.Agreement == vendInvoiceInfoTable.PurchAgreementHeader_PSN;

        if (!ecoResCategory.RecId)
        {
            select firstonly RecId from ecoResCategory
                join Category from agreementLineLocal
                    where ecoResCategory.RecId == agreementLineLocal.Category
                    && agreementLineLocal.Agreement == purchAgreementHeaderRecid
                exists join ParentCategory, RecId from ecoResCategoryChildren
                    where ecoResCategoryChildren.CategoryHierarchy == ecoResCategory.CategoryHierarchy
                    && ecoResCategoryChildren.NestedSetLeft >= ecoResCategory.NestedSetLeft
                    && ecoResCategoryChildren.NestedSetRight <= ecoResCategory.NestedSetRight
                    && (ecoResCategory.RecId == vendInvoiceInfoLine.ProcurementCategory || ecoResCategoryChildren.RecId == vendInvoiceInfoLine.ProcurementCategory);
        }
        if (ecoResCategory.RecId || ecoResCategoryChildren.RecId)
        {
            ret = super();
        }
        else
        {
            ret = false;
        }
    }
    else
    {
        ret = super();
    }

    return ret;
}

