public void modified()
{
    // <GIN>
    #ISOCountryRegionCodes;
    // </GIN>

    super();

    if (vendinvoiceinfoline.purchLine().PSARetainScheduleId)
    {
        vendinvoiceinfoline_ds.research(true);
    }

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]) && TaxParameters::checkTaxParameters_IN())
    {
        if ( purchParmUpdate.SpecQty == PurchUpdate::BillOfEntryQuantity_IN )
        {
            vendinvoiceinfoline_ds.research(true);
        }
        if (!PurchFormLetter.purchTable().purchTable_W().CustomsImportOrder_IN)
        {
            info("@GLS6626");
        }
    }
    // </GIN>
}

public boolean validate()
{
    boolean ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (vendInvoiceInfoLine.Tax1099Amount != 0.0 && !vendInvoiceInfoLine.Tax1099Fields)
        {
            ret = checkFailed(strFmt("@SYS86315", "@SYS128436"));
        }
    }

    return ret;
}

public void modified()
{
    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        element.setAllowEdit1099GFields();
        element.setAllowEdit1099SFields();

        Tax1099BoxDetail::deleteOrClear1099Detail(Tax1099Fields::find(vendInvoiceInfoLine.Tax1099Fields).Tax1099Box, tax1099BoxDetail.RecId);

        if (vendinvoiceinfoline.Tax1099Amount == 0.0 && (vendInvoiceInfoLine.Tax1099Fields == Tax1099Base::findTax1099FieldsRecId(Tax1099G::incomeTaxRefundsCreditsOffsets()) || vendInvoiceInfoLine.Tax1099Fields == Tax1099Base::findTax1099FieldsRecId(Tax1099S::grossProceeds())))
        {
            vendinvoiceinfoline.Tax1099Amount = vendinvoiceinfoline.LineAmount;
        }
        tax1099boxdetail_ds.executeQuery();
    }
}

public void modified()
{
    VendInvoiceInfoLine vendInvoiceInfoLineOrig = vendInvoiceInfoLine.orig();

    super();

    if (vendInvoiceInfoLine.TaxGroup != vendInvoiceInfoLineOrig.TaxGroup)
    {
        TaxApplicabilityChangedTelemetry::writeUpdateEvent(
            tableId2Name(vendInvoiceInfoTable.TableId), TaxApplicabilityChangedTelemetry::TaxGroup, vendInvoiceInfoTable.RecId, vendInvoiceInfoLine.RecId);
    }
}

public void modified()
{
    VendInvoiceInfoLine vendInvoiceInfoLineOrig = vendInvoiceInfoLine.orig();

    super();

    if (vendInvoiceInfoLine.TaxItemGroup != vendInvoiceInfoLineOrig.TaxItemGroup)
    {
        TaxApplicabilityChangedTelemetry::writeUpdateEvent(
            tableId2Name(vendInvoiceInfoTable.TableId), TaxApplicabilityChangedTelemetry::TaxItemGroup, vendInvoiceInfoTable.RecId, vendInvoiceInfoLine.RecId);
    }
}

public boolean validateWrite()
{
    if (vendInvoiceInfoLine.isNonPO())
    {
        return super();
    }

    // PO dimensions are not editable on invoice; always return true
    return true;
}

public void initValue()
{
    inventDim.data(vendInvoiceInfoLine.inventDim());

    super();
}

public void write()
{
    //    super();
}

public void modified()
{
    super();

    if (TaxParameters::checkTaxParameters_IN())
    {
        vendInvoiceInfoLine.modifyInventDim(inventDim,fieldNum(InventDim,InventLocationId));
        vendInvoiceInfoLine.setAddressFromInventDim(inventDim);
        vendInvoiceInfoLine_ds.rereadReferenceDataSources();
        vendInvoiceInfoLine_ds.refresh();
        vendInvoiceInfoLineTaxExtension_IN_ds.refresh();
    }
    else
    {
        vendInvoiceInfoLine.setAddressFromInventDim(inventDim);
    }
}

public void modified()
{
    super();

    vendInvoiceInfoLine.setAddressFromInventDim(inventDim);

    boolean checkTaxParameters = TaxParameters::checkTaxParameters_IN();

    // <GBR>
    if (BrazilParameters::isEnabled() || checkTaxParameters)
    {
        vendInvoiceInfoLine_ds.rereadReferenceDataSources();
        vendInvoiceInfoLine_ds.refresh();

        vendInvoiceInfoLine.modifyInventDim(inventDim, fieldNum(InventDim, InventSiteId));
    }
    // <GIN>
    if (checkTaxParameters)
    {
        vendInvoiceInfoLineTaxExtension_IN_ds.refresh();
    }
    // </GIN>
}

