/// <summary>
/// Activates the <c>PurchLine</c> datasource.
/// </summary>
/// <returns>
/// An integer value indicating if the mehtod is successful. It always returns 1.
/// </returns>
/// <remarks>
/// This method calls the <c>setAllowEditAssetFields</c> method of the <c>PurchTableForm</c> class
/// to ensure that the fixed asset fields are available to be edited.
/// </remarks>
int active()
{
    int ret = super();

    purchEditLinesForm_Invoice.setAllowEditAssetFields(vendInvoiceInfoTable, vendInvoiceInfoLine,
        vendInvoiceInfoLine_Asset, vendInvoiceInfoLine_Asset_ds);
    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        purchEditLinesForm_Invoice.setAllowVisibleReductionEntryFields_JP(vendInvoiceInfoLine_Asset, reductionEntry_JP);
    }
    // </GJP>

    if (vendInvoiceInfoLine_Asset.CreateFixedAsset == NoYes::Yes)
    {
        distributionTemplateLine.allowEdit(false);
    }
    else
    {
        distributionTemplateLine.allowEdit(true);
        vendInvoiceInfoLine_Asset_ds.refresh();
    }

    return ret;
}

void create(boolean append = true)
{
    super(append);
    this.createFixedAssetModified();
}

/// <summary>
/// Broadcasts that the <c>CreateFixedAsset</c> field value has changed to the <c>purchLine</c> table buffer.
/// </summary>
void createFixedAssetModified()
{
    vendInvoiceInfoLine_Asset.modifyCreateFixedAsset();
}

public void write()
{
    VendInvoiceInfoLine_Asset orig = vendInvoiceInfoLine_Asset.orig();

    super();

    if (vendInvoiceInfoLine.RecId != 0
        && (orig.AssetId != vendInvoiceInfoLine_Asset.AssetId
        || orig.AssetBookId != vendInvoiceInfoLine_Asset.AssetBookId
        || orig.AssetGroup != vendInvoiceInfoLine_Asset.AssetGroup))
    {
        vendInvoiceInfoLine.updateDistribution();
    }
}

public void modified()
{
    super();

    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        purchEditLinesForm_Invoice.setAllowVisibleReductionEntryFields_JP(vendInvoiceInfoLine_Asset, reductionEntry_JP);
    }
    // </GJP>
}

public void modified()
{
    super();

    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        purchEditLinesForm_Invoice.setAllowVisibleReductionEntryFields_JP(vendInvoiceInfoLine_Asset, reductionEntry_JP);
    }
    // </GJP>

    if (VendInvoiceInfoLine_Asset.AssetId && VendInvoiceInfoLine_Asset.AssetBookId)
    {
        VendInvoiceInfoLine.DefaultDimension =  vendInvoiceInfoLine.mergeDimension(VendInvoiceInfoLine.DefaultDimension, VendInvoiceInfoLine_Asset.getAssetBookDefaultDimension());
    }

    VendInvoiceInfoLine_Project_ds.allowEdit(!VendInvoiceInfoLine_Asset.AssetId && !VendInvoiceInfoLine_Asset.CreateFixedAsset);
}

/// <summary>
/// Modifies the <c>CreateFixedAssetField</c> field value on the <c>PurchLine</c> table buffer.
/// </summary>
/// <remarks>
/// This method broadcasts the change in value of the <c>CreateFixedAssetField</c> field by
/// calling <c>createFixedAssetModified</c> on the data source. Then it calls the
/// <c>setAllowEditAssetFields</c> method of the <c>purchTableForm</c> class to correctly
/// set the <c>allowEdit</c> property of the fixed asset fields.
/// </remarks>
public void modified()
{
    super();

    vendInvoiceInfoLine_Asset_ds.createFixedAssetModified();
    purchEditLinesForm_Invoice.setAllowEditAssetFields(vendInvoiceInfoTable, vendInvoiceInfoLine,
        vendInvoiceInfoLine_Asset, vendInvoiceInfoLine_Asset_ds);
    // <GJP>
    if (AssetDocumentUtility_JP::isAssetDocumentEnabled())
    {
        purchEditLinesForm_Invoice.setAllowVisibleReductionEntryFields_JP(vendInvoiceInfoLine_Asset, reductionEntry_JP);
    }

    VendInvoiceInfoLine_Project_ds.allowEdit(!VendInvoiceInfoLine_Asset.CreateFixedAsset && !VendInvoiceInfoLine_Asset.AssetId);

    // </GJP>
}

public void setAllowEditResource()
{
    if (vendInvoiceInfoLine_Project.isItemBased(vendInvoiceInfoLine))
    {
        vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, Resource)).allowEdit(false);
    }
    else
    {
        boolean isBudgetReservationLine = vendInvoiceInfoLine.isBudgetReservationLineReferenced();
        vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, Resource)).allowEdit(!isBudgetReservationLine);
    }
}

public void updatePricingDesign()
{
    if (vendInvoiceInfoLine_Project.isIntercompany())
    {
        element.control(element.controlId(formControlStr(VendEditInvoice, ProjTransferPriceGroup))).enabled(true);
    }
    else
    {
        element.control(element.controlId(formControlStr(VendEditInvoice, ProjTransferPriceGroup))).enabled(false);
    }
}

/// <summary>
/// Allows entry of the unit of measure.
/// </summary>
/// <param name="_set">
/// Determines whether setting the unit of measure value.
/// </param>
/// <param name="_salesUnit">
/// The unit of measure value.
/// </param>
/// <returns>
/// The unit of measure value.
/// </returns>
public edit SalesUnit editProjSalesUnitId(boolean _set, SalesUnit _salesUnit)
{
    UnitOfMeasure unitOfMeasure;

    if (_set)
    {
        unitOfMeasure = UnitOfMeasure::findBySymbol(_salesUnit);
        vendInvoiceInfoLine_Project.ProjSalesUnitId = unitOfMeasure.RecId;
    }
    else
    {
        unitOfMeasure = UnitOfMeasure::find(vendInvoiceInfoLine_Project.ProjSalesUnitId);
    }

    return unitOfMeasure.Symbol;
}

public void setAllowEdit()
{
    if (vendInvoiceInfoLine.isNonPO())
    {
        if (vendInvoiceInfoLine_Project.VendInvoiceInfoLineRefRecId == 0)
        {
            //Only create a new record if none exist
            vendInvoiceInfoLine_Project_ds.allowCreate(true);
            vendInvoiceInfoLine_Project_ds.create();
            vendInvoiceInfoLine_Project_ds.allowEdit(true);
            vendInvoiceInfoLine_Project_ds.setLineFields();
            vendInvoiceInfoLine_Project_ds.allowCreate(false);
        }
    }
    else
    {
        vendInvoiceInfoLine_Project_ds.allowEdit(false);
        vendInvoiceInfoLine_Project_ds.setLineFields();
    }

    this.setAllowEditResource();
}

public int active()
{
    int ret = super();

    vendInvoiceInfoLine_Project_ds.allowEdit(vendInvoiceInfoLine.isNonPO());
    vendInvoiceInfoLine_Project_ds.setLineFields();

    return ret;
}

public void create(boolean _append = false)
{
    if (vendInvoiceInfoLine.isNonPO())
    {
        super(_append);
        if(vendInvoiceInfoLine.AgreementLine_PSN != 0)
        {
            AgreementLine localAgreementLine = AgreementLine::find(vendInvoiceInfoLine.AgreementLine_PSN);

            if (localAgreementLine && localAgreementLine.ProjectProjId)
            {
                vendInvoiceInfoLine_Project.ProjId = localAgreementLine.ProjectProjId;
                vendInvoiceInfoLine_Project.VendInvoiceInfoLineRefRecId = vendInvoiceInfoLine.RecId;
                vendInvoiceInfoLine_Project.modifiedField(fieldNum(vendInvoiceInfoLine_Project, ProjId));
                LedgerDimensionAccount projDefaultDimension = ProjTable::find(vendInvoiceInfoLine_Project.ProjId).DefaultDimension;
                if (projDefaultDimension)
                {
                    vendInvoiceInfoLine.DefaultDimension = vendInvoiceInfoLine.mergeDimension(projDefaultDimension, vendInvoiceInfoLine.DefaultDimension);
                }
            }
        }
    }
}

public void setLineFields()
{
    boolean isNonPOLine = vendInvoiceInfoLine.isNonPO();
    boolean hasProject = (vendInvoiceInfoLine_Project.ProjId ? true : false);
    boolean mustDefaultCostPriceAsSalesPrice;
    boolean isBudgetReservationLine = VendInvoiceInfoLine.isBudgetReservationLineReferenced();

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjId)).allowEdit(isNonPOLine && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjId)).skip(!isNonPOLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjId)).mandatory(vendInvoiceInfoLine_Project.RecId);

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjActivityNumber)).allowEdit(isNonPOLine && hasProject && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjActivityNumber)).skip(!(isNonPOLine && hasProject));

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjCategoryId)).allowEdit(isNonPOLine && hasProject && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjCategoryId)).skip(!(isNonPOLine && hasProject));
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjCategoryId)).mandatory(isNonPOLine && hasProject);

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjLinePropertyId)).allowEdit(isNonPOLine && hasProject && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjLinePropertyId)).skip(!(isNonPOLine && hasProject));
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjLinePropertyId)).mandatory(isNonPOLine && hasProject);

    if (!vendInvoiceInfoLine.ItemId)
    {
        mustDefaultCostPriceAsSalesPrice = ProjCategory::isProjCategoryTypeItem(vendInvoiceInfoLine_Project.ProjCategoryId);
    }

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjSalesCurrencyId)).allowEdit(isNonPOLine && hasProject && !mustDefaultCostPriceAsSalesPrice && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjSalesCurrencyId)).skip(!(isNonPOLine && hasProject && !mustDefaultCostPriceAsSalesPrice));
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjSalesCurrencyId)).mandatory(isNonPOLine && hasProject && !mustDefaultCostPriceAsSalesPrice);

    salesPrice_ProjSalesUnitId.allowEdit(isNonPOLine && hasProject && !isBudgetReservationLine);
    salesPrice_ProjSalesUnitId.skip(!(isNonPOLine && hasProject));
    salesPrice_ProjSalesUnitId.mandatory(isNonPOLine && hasProject);

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjSalesPrice)).allowEdit(isNonPOLine && hasProject && !mustDefaultCostPriceAsSalesPrice && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjSalesPrice)).skip(!(isNonPOLine && hasProject && !mustDefaultCostPriceAsSalesPrice));

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjTaxGroupId)).allowEdit(isNonPOLine && hasProject && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjTaxGroupId)).skip(!(isNonPOLine && hasProject));

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjTaxItemGroupId)).allowEdit(isNonPOLine && hasProject && !isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjTaxItemGroupId)).skip(!(isNonPOLine && hasProject));

    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, ProjDataAreaId)).allowEdit(!isBudgetReservationLine);
    vendInvoiceInfoLine_Project_ds.object(fieldNum(VendInvoiceInfoLine_Project, TransDate)).allowEdit(!isBudgetReservationLine);

    vendInvoiceInfoLine_Project_ds.updatePricingDesign();

    element.updateProjectControls(projOpsIntegEnabled);
}

