public void modified()
{
    // Workaround for the Enhanced Preview on the Project lookup, which changes company automatically to the wrong LE
    appl.setDefaultCompany(currentCompany);

    super();

    vendInvoiceInfoLine_Project.setProjCategoryId(vendInvoiceInfoLine);
    LedgerDimensionAccount projDefaultDimension = ProjTable::find(vendInvoiceInfoLine_Project.ProjId).DefaultDimension;
    if (projDefaultDimension)
    {
        vendInvoiceInfoLine.DefaultDimension = vendInvoiceInfoLine.mergeDimension(projDefaultDimension, vendInvoiceInfoLine.DefaultDimension);
    }
    else
    {
        vendInvoiceInfoLine.DefaultDimension = vendInvoiceInfoLine.copyDimension(VendTable::find(vendInvoiceInfoLine.orderAccount).DefaultDimension);
    }
    vendInvoiceInfoLine_Project.setProjSalesUnitId(vendInvoiceInfoLine);
    vendInvoiceInfoLine_Project.setProjSalesPrice(vendInvoiceInfoLine);
    vendInvoiceInfoLine_Project_ds.setLineFields();
    element.enableBudgetReservationLineField();

    VendInvoiceInfoLine_Project_DS.refresh();

    if (vendInvoiceInfoLine.isNonPO()
        && vendInvoiceInfoLine_Project.ProjId
        && !vendInvoiceInfoLine.InventTransId)
    {
        vendInvoiceInfoLine_ds.forceWrite(true);
    }

    VendInvoiceInfoLine_Asset_ds.allowEdit(!VendInvoiceInfoLine_Project.ProjId);
}

