public void lookup(FormControl _formControl, str _filterStr)
{
    Query query = new Query();

    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(PSASchedRole), _formControl);
    sysTableLookup.addLookupfield(fieldNum(PSASchedRole, RoleId), true);
    sysTableLookup.addLookupfield(fieldNum(PSASchedRole, Description));

    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(PSASchedRole));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void modified()
{
    super();
    vendInvoiceInfoLine_Project.setProjSalesPrice(vendInvoiceInfoLine);
}

void executeQuery()
{
    switch (vendInvoiceInfoTable.Ordering)
    {
        case DocumentStatus::Invoice:
            OfficialsTrans_RU::searchAccountIdx(officialsTrans_RU,
                                                vendInvoiceInfoTable_Officials.OffReportType,
                                                vendInvoiceInfoTable_Officials.OffSessionId);

            break;
        case DocumentStatus::Facture_RU:
            OfficialsTrans_RU::searchAccountIdx(officialsTrans_RU,
                                                vendInvoiceInfoTable_Officials.OffReportType,
                                                vendInvoiceInfoTable_Officials.OffSessionId,
                                                vendInvoiceInfoTable_Officials.OffReportType == OffReportType_RU::PurchInvoice ?
                                                OffReportType_RU::VendFacture :
                                                OffReportType_RU::VendFactureCreditNote);
            break;
    }
    super();
}

public void modified()
{
    super();
    officialsTrans_RU.nameChanged(NoYes::No);
    officialsTrans_RU_ds.refresh();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    boolean isOurGuy = officialsTrans_RU.isOurGuy() == OffIsOurGuy_RU::Yes;
    OfficialsTrans_RU::nameLookup(_formControl,
                                  isOurGuy,
                                  "",
                                  isOurGuy ? "" : officialsTrans_RU.CustVendRelation);
}

