public void initValue()
{
    vendInvoiceInfoTableSymbol.initFromVendInvoiceInfoTable(vendInvoiceInfoTable);

    if (vendInvoiceInfoTableSymbol.VendInvoiceInfoTable)
    {
        vendInvoiceInfoTableSymbol.write();
        vendInvoiceInfoTableSymbol_DS.executeQuery();
    }
}

public void create(boolean _append = false)
{
    Percent sumOfPercentAllocation;

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (purchEditLinesForm_Invoice.allowPaymentDisbursement(
            vendInvoiceInfoTable.ParmId,
            vendInvoiceInfoTable.TableRefId))
        {
            super(_append);

            sumOfPercentAllocation = VendInvoiceInfo_PaymentDisbursement::getSumOfAllocationPercentage(
            vendInvoiceInfoTable.ParmId,
            vendInvoiceInfoTable.TableRefId);

            if (sumOfPercentAllocation !=100)
            {
                vendInvoiceInfo_PaymentDisbursement.PercentageAllocation = 100 - sumOfPercentAllocation;
            }
        }
    }
}

/// <summary>
/// Checks that the payment disbursement is allowed or not to create the records.
/// </summary>
/// <returns>
/// Always returns 1.
/// </returns>
public int active()
{
    int ret;
    boolean allowPaymentDisbursement;

    ret = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        allowPaymentDisbursement = purchEditLinesForm_Invoice.allowPaymentDisbursement(
                                        vendInvoiceInfoTable.ParmId,
                                        vendInvoiceInfoTable.TableRefId,
                                        false);

        vendInvoiceInfo_PaymentDisbursement_ds.allowCreate(allowPaymentDisbursement);
    }

    return ret;
}

public void delete()
{
    boolean allowPaymentDisbursement;

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        allowPaymentDisbursement = purchEditLinesForm_Invoice.allowPaymentDisbursement(
        vendInvoiceInfoTable.ParmId,
        vendInvoiceInfoTable.TableRefId,false);

        vendInvoiceInfo_PaymentDisbursement_ds.allowCreate(allowPaymentDisbursement);
    }
}

public boolean validateWrite(boolean _tax1099DetailRequired = false)
{
    boolean isValid;
    Tax1099Fields tax1099Fields;

    isValid = super();

    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (_tax1099DetailRequired)
        {
            tax1099Fields = Tax1099Fields::find(vendInvoiceInfoLine.Tax1099Fields);

            if (isValid)
            {
                if (tax1099Fields.Tax1099Box == Tax1099G::unemploymentCompensation().tax1099Box)
                {
                    if ((abs(tax1099BoxDetail.Tax1099StateTaxWithheld) > abs(vendInvoiceInfoLine.Tax1099Amount)))
                    {
                        isValid = checkFailed("@SPS1943");
                    }
                }

                if (tax1099Fields.Tax1099Box == Tax1099G::incomeTaxRefundsCreditsOffsets().tax1099Box)
                {
                    if ((tax1099BoxDetail.Tax1099TaxYear < (year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))-15)) || (tax1099BoxDetail.Tax1099TaxYear > (year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())) + 10)) )
                    {
                        isValid = checkFailed(strFmt("@SPS17", "@SPS1064", int2str(year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())) - 15), int2str(year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())) + 10)));
                    }

                    if (tax1099BoxDetail.Tax1099TaxYear > year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())) && tax1099BoxDetail.Tax1099TaxYear < (year(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())) + 15))
                    {
                        warning("@SPS2");
                    }
                }

                if (tax1099Fields.Tax1099Box == Tax1099S::grossProceeds().tax1099Box)
                {
                    if ((tax1099BoxDetail.Tax1099DateOfClosing == dateNull()) || (tax1099BoxDetail.Tax1099AddressorLegalDesc ==""))
                    {
                        isValid = checkFailed("@SPS3");
                    }

                    if (tax1099BoxDetail.Tax1099BuyersTax == 0.0)
                    {
                        warning(strFmt("@SPS14", "@PublicSector:Tax1099S6BuyersPart"));
                    }

                    if (vendInvoiceInfoLine.Tax1099Amount == 0 && tax1099BoxDetail.Tax1099PropertyOrServices == NoYes::Yes)
                    {
                        warning(strFmt("@SPS18", "@SYS23907"));
                    }

                    if (vendInvoiceInfoLine.Tax1099Amount == 0 && tax1099BoxDetail.Tax1099PropertyOrServices == NoYes::No)
                    {
                        warning("@SPS1165");
                    }
                }
            }
        }
    }

    return isValid;
}

public void write(boolean _tax1099DetailRequired = false)
{
    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        if (_tax1099DetailRequired)
        {
            //Called as a child record for VendInvoiceInfoLine
            super();
        }
        else if (vendInvoiceInfoLine.validateWrite())
        {
            vendInvoiceInfoLine_ds.write();
        }
    }
}

