    if (isConfigurationkeyEnabled(configurationKeyNum(PublicSector)))
    {
        Tax1099BoxDetail::jumpRefTax1099State(vendInvoiceInfoTable.InvoiceAccount,tax1099BoxDetail.State);
    }
}

